--
-- Đang đổ dữ liệu cho bảng `tin`
--

INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(66, 'Shangri-La thung lũng huyền thoại', 'Shangri-La-Thung-Lung-Huyen-Thoai', 'Thành phố Shangri-La thuộc tỉnh Vân Nam, Trung Quốc là điểm du lịch hấp dẫn với cảnh quan thiên nhiên tươi đẹp, những ngôi chùa theo kiến trúc Phật giáo Tây Tạng…', 'dulich2.jpg', NULL, 22, '<p class=\"Title\">Shangri-La thung lũng huyền thoại</p>\r\n<p class=\"Lead\">Th&agrave;nh phố Shangri-La thuộc tỉnh V&acirc;n Nam, Trung Quốc l&agrave; điểm du lịch hấp dẫn với cảnh quan thi&ecirc;n nhi&ecirc;n tươi đẹp, những ng&ocirc;i ch&ugrave;a theo kiến tr&uacute;c Phật gi&aacute;o T&acirc;y Tạng&hellip;</p>\r\n<p class=\"Normal\">M&ugrave;a hạ nơi đ&acirc;y đẹp hơn khi cỏ c&acirc;y căng tr&agrave;n sức sống, những triền đồi trải đầy hoa nở, m&acirc;y n&uacute;i trập tr&ugrave;ng như tranh thủy mặc. Shangri-La nằm trong một g&oacute;c phố nhỏ, ẩn m&igrave;nh s&acirc;u trong phố ch&iacute;nh với những con đường l&aacute;t đ&aacute; đậm phong c&aacute;ch kiến tr&uacute;c T&acirc;y Tạng. Th&agrave;nh phố ch&agrave;o đ&oacute;n kh&aacute;ch du lịch với sự thanh b&igrave;nh v&agrave; y&ecirc;n ả.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/FE/26/dulich2.jpg\" border=\"1\" alt=\"Những hồ nước ngọt phẳng lặng, nằm phơi m&igrave;nh b&ecirc;n những d&atilde;y n&uacute;i cao tạo cảm gi&aacute;c y&ecirc;n b&igrave;nh cho Shangrila.\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những hồ nước ngọt phẳng lặng, nằm phơi m&igrave;nh b&ecirc;n d&atilde;y n&uacute;i cao tạo cảm gi&aacute;c y&ecirc;n b&igrave;nh cho thị trấn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&aacute;c gian h&agrave;ng b&agrave;y nhiều sản phẩm d&acirc;n gian của người Tạng. Những chiếc v&ograve;ng đeo tay v&agrave; khuy&ecirc;n tai đủ m&agrave;u sắc; chiếc chu&ocirc;ng c&oacute; tiếng k&ecirc;u leng keng vui tai; b&uacute;p b&ecirc; mang h&igrave;nh ảnh những c&ocirc; b&eacute; d&acirc;n tộc Nạp T&acirc;y Tạng hoặc Moshua; c&aacute;c con b&ograve; Yak nhồi b&ocirc;ng nhỏ xinh. Ngo&agrave;i ra, cặp, t&uacute;i l&agrave;m từ da thuộc l&agrave; những m&oacute;n qu&agrave; th&uacute; vị cho kh&aacute;ch du lịch.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/FE/26/dulich04.jpg\" border=\"1\" alt=\"Những m&oacute;n qu&agrave; lưu niệm đặc trưng khi đến Shangri-La\" width=\"400\" height=\"366\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những m&oacute;n qu&agrave; lưu niệm đặc trưng Shangri-La.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đến với Shangri-La, một địa danh kh&ocirc;ng thể bỏ qua l&agrave; ng&ocirc;i ch&ugrave;a Đại Phật Tự nằm tr&ecirc;n ngọn đồi trong khu cổ trấn. Bạn c&oacute; thể nh&igrave;n to&agrave;n cảnh khu th&agrave;nh cổ khi đến đ&acirc;y. &ldquo;Ph&aacute;p lu&acirc;n kinh quay&rdquo; khổng lồ cao 24 m (c&ograve;n gọi l&agrave; v&ograve;ng quay tụng ch&uacute;) trong s&acirc;n ch&ugrave;a được cho l&agrave; lớn nhất thế giới.</p>\r\n<p class=\"Normal\">Người d&acirc;n nơi đ&acirc;y tin rằng vừa niệm Phật vừa d&ugrave;ng tay k&eacute;o &ldquo;Ph&aacute;p lu&acirc;n kinh quay&rdquo; nhiều v&ograve;ng sẽ đem lại những điều an l&agrave;nh, như &yacute;. Bởi vậy, h&igrave;nh ảnh &ldquo;Ph&aacute;p lu&acirc;n kinh quay&rdquo; được mạ v&agrave;ng, chạm trổ h&igrave;nh Phật với những v&ograve;ng quay kh&ocirc;ng dứt gi&uacute;p tăng th&ecirc;m vẻ uy nghi&ecirc;m, thanh khiết của ng&ocirc;i ch&ugrave;a.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/FE/26/dulich4.jpg\" border=\"1\" alt=\"Ch&ugrave;a Đại Phật Tự nằm tr&ecirc;n một ngọn đồi.\" width=\"450\" height=\"309\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ch&ugrave;a Đại Phật Tự nằm tr&ecirc;n một ngọn đồi.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chi tiết chương tr&igrave;nh v&agrave; gi&aacute; xem tại Website:&nbsp;<a class=\"Normal\" href=\"http://www.travel365.com.vn/\">http://www.travel365.com.vn/</a></p>\r\n<p class=\"Normal\">Hoặc li&ecirc;n hệ trực tiếp Asiana Travel</p>\r\n<p class=\"Normal\">Địa chỉ: 159 Phương Mai, Đống Đa, H&agrave; Nội</p>\r\n<p class=\"Normal\">Tel: (84 - 4) 35 736 748 &ndash; 49/ Fax: (84 - 4) 35 736 750 - Hotline: 0983.388.387 (Ms. Mỹ); 0983.000.448 (Ms. Nh&acirc;n)</p>\r\n<p class=\"Normal\">Email:&nbsp;<a class=\"Normal\" href=\"mailto:info@travel365.com.vn\">info@travel365.com.vn</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Asiana Travel</em>)</p>', 3, 1, 434, 0, 1),
(67, 'Công viên đá ở Côn Minh', 'Cong-Vien-Da-O-Con-Minh', 'Tạo hóa đã ưu ái cho Thạch Lâm (Côn Minh, Vân Nam, Trung Quốc) một phong cảnh thần tiên của đá và nước. Những cột đá nằm trong một khu vực rộng tới 350 km2, tập trung nhất là khu công viên đá Thạch Lâm.', '1 (3).jpg', NULL, 22, '<p class=\"Title\">C&ocirc;ng vi&ecirc;n đ&aacute; ở C&ocirc;n Minh</p>\r\n<p class=\"Lead\">Tạo h&oacute;a đ&atilde; ưu &aacute;i cho Thạch L&acirc;m (C&ocirc;n Minh, V&acirc;n Nam, Trung Quốc) một phong cảnh thần ti&ecirc;n của đ&aacute; v&agrave; nước. Những cột đ&aacute; nằm trong một khu vực rộng tới 350 km2, tập trung nhất l&agrave; khu c&ocirc;ng vi&ecirc;n đ&aacute; Thạch L&acirc;m.</p>\r\n<p class=\"Normal\">Kh&aacute;ch du lịch Việt Nam đi theo tuyến H&agrave; Nội &ndash; Alư Cổ Động &ndash; Thạch L&acirc;m &ndash; C&ocirc;n Minh &ndash; H&agrave; Nội đều c&oacute; cảm nhận: Thạch L&acirc;m l&agrave; điểm tham quan l&yacute; tưởng với vẻ đẹp kỳ diệu, một sự kết hợp h&agrave;i h&ograve;a giữa thi&ecirc;n nhi&ecirc;n v&agrave; con người.</p>\r\n<p class=\"Normal\">Ngắm nh&igrave;n bạn sẽ thấy c&aacute;c khối đ&aacute; như một ng&ocirc;i ch&ugrave;a, h&igrave;nh th&ugrave; giống một rừng kiếm, một d&atilde;y cột, một con voi, một con phượng ho&agrave;ng x&ograve;e c&aacute;nh, rồi h&igrave;nh tượng của H&ograve;a thượng Đường Tăng, của một thiếu nữ h&oacute;a đ&aacute;, lại c&oacute; cả giường Ti&ecirc;n, những hồ nước trong xanh với c&aacute;c đảo đ&aacute; nhấp nh&ocirc;... Đi trong rừng đ&aacute; với mu&ocirc;n v&agrave;n những h&igrave;nh dạng kỳ diệu ấy du kh&aacute;ch như lạc v&agrave;o một thế giới ri&ecirc;ng, chỉ c&oacute; trong chuyện thần ti&ecirc;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/1.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Rừng đ&aacute; h&ugrave;ng vĩ. Ảnh:&nbsp;<em>Hồng Phương.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nơi đ&acirc;y đ&atilde; được chọn l&agrave; một trong những trường quay của bộ phim &ldquo;T&acirc;y du k&yacute;&rdquo;, với những điểm ghi dấu ấn của T&ocirc;n Ngộ Kh&ocirc;ng v&agrave; c&aacute;c sư đệ đ&atilde; thể hiện t&agrave;i tr&iacute; trong việc th&aacute;p t&ugrave;ng Đường Tăng đi T&acirc;y Tr&uacute;c lấy kinh. Đ&oacute; l&agrave; lỗ đ&aacute; h&igrave;nh tr&ograve;n, l&agrave; khe đ&aacute; hẹp chỉ cho lọt cổ.</p>\r\n<p class=\"Normal\">Cảnh đẹp thần ti&ecirc;n của Thạch L&acirc;m c&agrave;ng được du kh&aacute;ch h&agrave;i l&ograve;ng khi đến tham quan ch&iacute;nh bởi những con người nơi đ&acirc;y: những c&aacute;n bộ, nh&acirc;n vi&ecirc;n quản l&yacute; c&ocirc;ng vi&ecirc;n. Nhiều người trong số họ thuộc d&acirc;n tộc Yi, một trong những d&acirc;n tộc thiểu số đ&ocirc;ng nhất ở V&acirc;n Nam. Những c&ocirc; g&aacute;i d&acirc;n tộc Yi chăm chỉ v&agrave; hiếu kh&aacute;ch, trong trang phục đặc sắc, l&aacute;i &ocirc;t&ocirc; điện đưa du kh&aacute;ch đến c&aacute;c điểm tham quan trong &ldquo;rừng đ&aacute;&rdquo;. Đ&oacute; l&agrave; loại xe rất tiện lợi, đặc biệt l&agrave; giữ cho m&ocirc;i trường sạch sẽ, kh&ocirc;ng bị &ocirc; nhiễm, kh&ocirc;ng ồn &agrave;o.</p>\r\n<p class=\"Normal\">Những c&ocirc;ng nh&acirc;n chuy&ecirc;n sửa sang, bảo dưỡng c&ocirc;ng vi&ecirc;n một c&aacute;ch rất tỉ mỉ, rất khoa học đối với từng c&acirc;y hoa, ngọn cỏ... để b&ecirc;n đường đi l&agrave; những thảm cỏ xanh mượt m&agrave; v&agrave; l&agrave; những vườn hoa đẹp rực rỡ như đ&oacute;n ch&agrave;o du kh&aacute;ch. Tất cả đều lu&ocirc;n nở nụ cười th&acirc;n thiện với kh&aacute;ch. Nhiều người giao tiếp được bằng tiếng Việt c&agrave;ng l&agrave;m cho quan hệ chủ, kh&aacute;ch th&ecirc;m th&acirc;n mật.</p>\r\n<p class=\"Normal\">Ch&iacute;nh v&igrave; thế m&agrave; nơi đ&acirc;y &ndash; một c&ocirc;ng vi&ecirc;n, một di sản thi&ecirc;n nhi&ecirc;n thế giới &ndash; đ&atilde; được giữ g&igrave;n một c&aacute;ch ho&agrave;n hảo, hết sức sạch sẽ. Bản th&acirc;n điều đ&oacute; như một lời nhắc nhở, tạo cho du kh&aacute;ch một th&oacute;i quen rất văn minh: kh&ocirc;ng dẫm l&ecirc;n cỏ, kh&ocirc;ng h&aacute;i một b&ocirc;ng hoa, vứt r&aacute;c đ&uacute;ng nơi quy định...</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/5.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cảnh quan trữ t&igrave;nh hiếm c&oacute;. Ảnh:&nbsp;<em>Hồng Phương.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ch&uacute;ng t&ocirc;i đến Thạch L&acirc;m đ&uacute;ng dịp mở đầu tuần Lễ hội của người Sani, một nh&aacute;nh của d&acirc;n tộc Yi, được chứng kiến điệu nhảy tập thể, giản dị nhưng đầy mầu sắc. Những ch&agrave;ng trai, c&ocirc; g&aacute;i người d&acirc;n tộc trong trang phục truyền thống nhảy trong tiếng nhạc d&acirc;n gian, tay trong tay, nhảy theo một v&ograve;ng tr&ograve;n, sẵn s&agrave;ng mời v&agrave; nhảy rất vui vẻ c&ugrave;ng du kh&aacute;ch.</p>\r\n<p class=\"Normal\">L&uacute;c n&agrave;y kh&ocirc;ng c&ograve;n ph&acirc;n biệt chủ - kh&aacute;ch, tất cả h&ograve;a trong kh&ocirc;ng kh&iacute; lễ hội, trong t&igrave;nh hữu nghị giữa c&aacute;c d&acirc;n tộc, c&aacute;c quốc gia. Lễ hội của người Sani diễn ra giữa khung cảnh h&ugrave;ng tr&aacute;ng, thơ mộng của đ&aacute;, của nước c&agrave;ng l&agrave;m cho Thạch L&acirc;m th&ecirc;m quyến rũ, để lại cho du kh&aacute;ch một ấn tượng kh&ocirc;ng thể n&agrave;o qu&ecirc;n.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hồng Phương</strong></p>', 3, 1, 1090, 0, 1),
(68, 'Học sinh Hà Nội dự kiến nghỉ Tết 11 ngày', 'Hoc-Sinh-Ha-Noi-Du-Kien-Nghi-Tet-11-Ngay', 'Ngày 6/1, Sở Giáo dục Đào tạo đã có tờ trình gửi UBND Hà Nội đề xuất thời gian nghỉ Tết Tân Mão của ngành giáo dục là 11 ngày. ', 'hs-gialanh13.jpg', NULL, 22, '<h1 class=\"Title\">Học sinh H&agrave; Nội dự kiến nghỉ Tết 11 ng&agrave;y</h1>\r\n<h2 class=\"Lead\">Ng&agrave;y 6/1, Sở Gi&aacute;o dục Đ&agrave;o tạo đ&atilde; c&oacute; tờ tr&igrave;nh gửi UBND H&agrave; Nội đề xuất thời gian nghỉ Tết T&acirc;n M&atilde;o của ng&agrave;nh gi&aacute;o dục l&agrave; 11 ng&agrave;y.&nbsp;</h2>\r\n<p class=\"Normal\">Theo tờ tr&igrave;nh, c&aacute;n bộ, gi&aacute;o vi&ecirc;n, học sinh c&aacute;c trường mầm non, tiểu học, THCS, THPT, bổ t&uacute;c văn h&oacute;a, trung t&acirc;m gi&aacute;o dục thường xuy&ecirc;n, trung t&acirc;m gi&aacute;o dục kỹ thuật tổng hợp tr&ecirc;n địa b&agrave;n H&agrave; Nội sẽ được nghỉ Tết 11 ng&agrave;y, từ 28/1 (25 th&aacute;ng Chạp) đến hết ng&agrave;y 7/2 (m&ugrave;ng 5 th&aacute;ng Gi&ecirc;ng).</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/51/C8/hs-gialanh13.jpg\" border=\"1\" alt=\"\" width=\"495\" height=\"340\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Học sinh thủ đ&ocirc; đang chịu cảnh gi&aacute; lạnh. Ảnh:&nbsp;<em>L&ecirc; Hiếu.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ri&ecirc;ng c&aacute;c trường trung cấp chuy&ecirc;n nghiệp được nghỉ 17 ng&agrave;y, từ ng&agrave;y 22/1 (19 th&aacute;ng Chạp) đến hết ng&agrave;y 7/2 (m&ugrave;ng 5 th&aacute;ng Gi&ecirc;ng). C&aacute;n bộ c&ocirc;ng chức, vi&ecirc;n chức của Sở GD&amp;ĐT, c&aacute;c ph&ograve;ng GD&amp;ĐT quận, huyện, thị x&atilde; sẽ đi l&agrave;m v&agrave;o ng&agrave;y thứ bảy (29/1), chủ nhật (30/1) v&agrave; nghỉ Tết từ ng&agrave;y 31/1 đến hết ng&agrave;y 7/2 (8 ng&agrave;y).&nbsp;<br /><br />Sau khi c&oacute; quyết định ph&ecirc; duyệt của UBND H&agrave; Nội, Sở GD&amp;ĐT H&agrave; Nội sẽ c&oacute; th&ocirc;ng b&aacute;o cụ thể về thời gian nghỉ Tết để c&aacute;c nh&agrave; trường, phụ huynh, học sinh thực hiện.</p>\r\n<p class=\"Normal\">Trước đ&oacute;, Thủ tướng cũng đ&atilde; quyết định cho c&ocirc;ng chức, vi&ecirc;n chức cả nước được nghỉ Tết nguy&ecirc;n đ&aacute;n trong 8 ng&agrave;y.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đo&agrave;n Loan</strong></p>', 1, 1, 1107, 0, 1),
(95, 'Thủ khoa đại học Khoa học tự nhiên TP HCM đạt 28,5 điểm', 'Thu-Khoa-Dai-Hoc-Khoa-Hoc-Tu-Nhien-Tp-Hcm-Dat-28,5-Diem', 'hiều 26/7, Đại học Khoa học tự nhiên TP HCM công bố kết quả điểm thi và điểm chuẩn dự kiến đối với các ngành.\r\n\r\nThủ khoa khối A là Nguyễn Mạnh Tiến đạt 28,5 điểm (Toán 9,75; Lý 9; Hóa 9,5). Thí sinh Phạm Thị Lộc Xuân dẫn đầu khối B với 27,5 điểm (Sinh 9; Toán 9; Hóa 9,5).', 'diem1.jpg', NULL, 22, '<p class=\"Title\">Thủ khoa đại học Khoa học tự nhi&ecirc;n TP HCM đạt 28,5 điểm</p>\r\n<p class=\"Lead\">Chiều 26/7, Đại học Khoa học tự nhi&ecirc;n TP HCM c&ocirc;ng bố kết quả điểm thi v&agrave; điểm chuẩn dự kiến đối với c&aacute;c ng&agrave;nh.</p>\r\n<p class=\"Normal\">Thủ khoa khối A l&agrave;&nbsp;<span style=\"color: #000000;\">Nguyễn Mạnh Tiến đạt 28,5 điểm (To&aacute;n 9,75; L&yacute; 9; H&oacute;a 9,5). Th&iacute; sinh Phạm Thị Lộc Xu&acirc;n dẫn đầu khối B với 27,5 điểm (Sinh 9; To&aacute;n 9; H&oacute;a 9,5).</span></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/E7/EA/diem1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"357\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&iacute; sinh dự thi đại học 2010. Ảnh:&nbsp;<em>Hải Duy&ecirc;n.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">C&oacute; ba b&agrave;i thi đạt điểm 10 đều thuộc m&ocirc;n To&aacute;n, trong đ&oacute; khối A một b&agrave;i v&agrave; khối B hai b&agrave;i. Nhằm khuyến kh&iacute;ch tinh thần học tập, những th&iacute; sinh c&oacute; điểm thi từ 27 trở l&ecirc;n đối với khối A v&agrave; 26 trở l&ecirc;n đối với khối B đều được nh&agrave; trường tặng một suất học bổng tương đương một năm học miễn ph&iacute;. Trong suốt thời gian học, nếu th&iacute; sinh đạt kết quả tốt sẽ tiếp tục được nhận c&aacute;c học bổng kh&aacute;c của trường.</span></p>\r\n<p><span style=\"color: #000000;\"> </span></p>\r\n<p class=\"Normal\">Một số ng&agrave;nh dự kiến tuyển sinh nguyện vọng 2 của ĐH Khoa học tự nhi&ecirc;n gồm: To&aacute;n - Tin, Vật l&yacute;, C&ocirc;ng nghệ th&ocirc;ng tin, Hải dương học - Kh&iacute; tượng v&agrave; thủy văn, Khoa học vật liệu, Sinh học.</p>\r\n<p class=\"Normal\">Điểm chuẩn dự kiến của c&aacute;c ng&agrave;nh:&nbsp;<br />(Điểm c&aacute;ch biệt với c&aacute;c nh&oacute;m đối tượng l&agrave; 1 v&agrave; khu vực ưu ti&ecirc;n l&agrave; 0,5)</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"0\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">M&atilde; ng&agrave;nh</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">T&ecirc;n ng&agrave;nh</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">Khối thi</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">Chi ti&ecirc;u</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">Điểm</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">101</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">To&aacute;n - Tin</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">300</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">15</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">104</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Vật L&yacute;</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">250</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">14.5</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">105</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Điện tử viễn th&ocirc;ng</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">200</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">17</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">107</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">C&ocirc;ng nghệ th&ocirc;ng tin</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">550</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">18</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">109</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Hải Dương học, Kh&iacute; tượng thủy văn</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A, B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">50/50</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">14.5/15</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">201</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">H&oacute;a học</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">250</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">16</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">203</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Đại chất</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A, B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">75/75</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">14/17</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">205</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Khoa học m&ocirc;i trường</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A, B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">75/75</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">15.5/18</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">206</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">C&ocirc;ng nghệ m&ocirc;i trường</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A, B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">60/60</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">16/19</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">207</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Khoa học vật liệu</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">180</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">14</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">301</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">Sinh học</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">300</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">16</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" width=\"55\" valign=\"top\">312</td>\r\n<td class=\"Normal\" width=\"150\" valign=\"top\">C&ocirc;ng nghệ sinh học</td>\r\n<td class=\"Normal\" width=\"66\" valign=\"top\">A, B</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">70/130</td>\r\n<td class=\"Normal\" width=\"72\" valign=\"top\">17/21</td>\r\n</tr>\r\n<tr>\r\n<td colspan=\"5\" width=\"415\" valign=\"top\">&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" colspan=\"5\" width=\"415\" valign=\"top\">Hệ cao đẳng ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin: 800 chỉ ti&ecirc;u, lấy bằng điểm s&agrave;n.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Hải Duy&ecirc;n</strong></p>\r\n<p>&nbsp;</p>', 1, 1, 737, 0, 1),
(96, 'Hai học sinh lớp 11 đoạt huy chương Olympic Vật lý', 'Hai-Hoc-Sinh-Lop-11-Doat-Huy-Chuong-Olympic-Vat-Ly', 'Trong số 5 học sinh Việt Nam đoạt huy chương Olympic Vật lý Quốc tế (IPHO) 41 tại Croatia, Phan Văn Trung (THPT Hà Nội - Amsterdam) và Nguyễn Hoành Đạo (Chuyên Lam Sơn, Thanh Hóa) là 2 học sinh trẻ nhất.\r\n', 'HCV.jpg', NULL, 22, '<p class=\"Title\">Hai học sinh lớp 11 đoạt huy chương Olympic Vật l&yacute;</p>\r\n<p class=\"Lead\">Trong số 5 học sinh Việt Nam đoạt huy chương Olympic Vật l&yacute; Quốc tế (IPHO) 41 tại Croatia, Phan Văn Trung (THPT H&agrave; Nội - Amsterdam) v&agrave; Nguyễn Ho&agrave;nh Đạo (Chuy&ecirc;n Lam Sơn, Thanh H&oacute;a) l&agrave; 2 học sinh trẻ nhất.</p>\r\n<p class=\"Lead\"><br />Theo th&ocirc;ng tin từ Bộ GD&amp;ĐT, em Đinh Anh Minh (lớp 12 THPT chuy&ecirc;n Quốc học Huế) đoạt huy chương V&agrave;ng, Phan Minh Trung (lớp 11, THPT Chuy&ecirc;n H&agrave; Nội - Amsterdam), Phạm B&igrave;nh Minh (lớp 12, THPT chuy&ecirc;n Trần Ph&uacute; - Hải Ph&ograve;ng) v&agrave; Phạm Văn Quyền (lớp 12, THPT chuy&ecirc;n L&ecirc; Hồng Phong - Nam Định) c&ugrave;ng đoạt huy chương Bạc. C&ograve;n Nguyễn Ho&agrave;nh Đạo (lớp 11, THPT chuy&ecirc;n Lam Sơn - Thanh Ho&aacute;) đoạt huy chương Đồng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/E7/7D/HCV.jpg\" alt=\"Ảnh: TTXVN.\" width=\"450\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đinh Anh Minh - huy chương V&agrave;ng Olympic Vật l&yacute; 41. Ảnh:&nbsp;<em>TTXVN.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Olympic Vật l&yacute; Quốc tế (IPHO) 41 tại Croatia (ng&agrave;y 17-25/7) c&oacute; 80 nước v&agrave; v&ugrave;ng l&atilde;nh thổ tham dự. Đo&agrave;n Việt Nam c&oacute; 5 học sinh tham dự. Chiều tối 27/7 đo&agrave;n sẽ về tới s&acirc;n bay Nội B&agrave;i.</p>\r\n<p class=\"Normal\">Năm nay, đo&agrave;n Việt Nam cũng gi&agrave;nh được 2 HCB v&agrave; 1 HCĐ&nbsp;<a class=\"Normal\" href=\"http://vnexpress.net/GL/Xa-hoi/Giao-duc/2010/07/3BA1E33F/\">Olympic Sinh học Quốc tế</a>&nbsp;(IBO 21, H&agrave;n Quốc), v&agrave; 1 HCV, 4 HCB, 1 HCĐ tại&nbsp;<a class=\"Normal\" href=\"http://vnexpress.net/GL/Xa-hoi/Giao-duc/2010/07/3BA1DF50/\">Olympic To&aacute;n học Quốc tế</a>&nbsp;(IMO 51, Kazakhstan).</p>\r\n<p class=\"Normal\"><strong>Tiến Dũng</strong></p>', 1, 1, 729, 0, 1),
(97, 'Sinh viên thể thao khỏe khoắn trên sàn diễn', 'Sinh-Vien-The-Thao-Khoe-Khoan-Tren-San-Dien', 'Những sinh viên Đại học Thể dục thể thao II, TP HCM thướt tha trong những bộ áo dài hay trang phục khỏe khoắn trình diễn như người mẫu trên sàn catwalk.', 'thethao2.jpg', NULL, 22, '<h1 class=\"Title\">Sinh vi&ecirc;n thể thao khỏe khoắn tr&ecirc;n s&agrave;n diễn</h1>\r\n<h2 class=\"Lead\">Những sinh vi&ecirc;n Đại học Thể dục thể thao II, TP HCM thướt tha trong những bộ &aacute;o d&agrave;i hay trang phục khỏe khoắn tr&igrave;nh diễn như người mẫu tr&ecirc;n s&agrave;n catwalk.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao2.jpg\" border=\"1\" alt=\"Cuộc thi Miss v&agrave; Mr của trường Đại học Thể dục thể thao II, quận Thủ Đức, TP HCM, thu h&uacute;t c&aacute;c bạn sinh vi&ecirc;n tham gia nồng nhiệt. Sau v&ograve;ng loại đ&atilde; chọn ra 14 bạn v&agrave;o chung kết đ&ecirc;m 18/1.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Cuộc thi Miss v&agrave; Mr của trường Đại học Thể dục thể thao II, quận Thủ Đức, TP HCM, thu h&uacute;t c&aacute;c bạn sinh vi&ecirc;n tham gia nồng nhiệt. Sau v&ograve;ng loại đ&atilde; chọn ra 14 bạn v&agrave;o chung kết đ&ecirc;m 18/1.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao3.jpg\" border=\"1\" alt=\"C&aacute;c th&iacute; sinh tham gia thi với trang phục &aacute;o d&agrave;i, thể thao v&agrave; phần thi năng khiếu, ứng xử.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">C&aacute;c th&iacute; sinh tham gia thi với trang phục &aacute;o d&agrave;i, thể thao v&agrave; phần thi năng khiếu, ứng xử.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao4.jpg\" border=\"1\" alt=\"Tự tin tr&igrave;nh diễn như c&aacute;c người mẫu thực sự.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Tự tin tr&igrave;nh diễn như c&aacute;c người mẫu thực sự.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao1.jpg\" border=\"1\" alt=\"E ấp trong trang phục &aacute;o d&agrave;i.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">E ấp trong trang phục &aacute;o d&agrave;i.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao6.jpg\" border=\"1\" alt=\"Trang phục thể thao đ&uacute;ng sở trường n&ecirc;n được c&aacute;c đ&ocirc;i thể hiện rất tự tin.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trang phục thể thao đ&uacute;ng sở trường n&ecirc;n được c&aacute;c đ&ocirc;i thể hiện rất tự tin. Nhưng đ&ocirc;i khi ch&agrave;ng e ngại n&ecirc;n nhắm mắt trước bạn diễn nữ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao7.jpg\" border=\"1\" alt=\"Phong c&aacute;ch tr&igrave;nh diễn mang vẻ đẹp khỏe khoắn đ&uacute;ng chất sinh vi&ecirc;n ng&agrave;nh thể thao.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Phong c&aacute;ch tr&igrave;nh diễn mang vẻ đẹp khỏe khoắn đ&uacute;ng chất sinh vi&ecirc;n ng&agrave;nh thể thao.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao8.jpg\" border=\"1\" alt=\"Tr&igrave;nh diễn c&aacute;c ca kh&uacute;c m&igrave;nh y&ecirc;u th&iacute;ch trong phần thi năng khiếu.\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Tr&igrave;nh diễn c&aacute;c ca kh&uacute;c m&igrave;nh y&ecirc;u th&iacute;ch trong phần thi năng khiếu.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao9.jpg\" border=\"1\" alt=\"Tất nhi&ecirc;n kh&ocirc;ng thiếu phần năng khiếu thể thao.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Tất nhi&ecirc;n kh&ocirc;ng thiếu phần năng khiếu thể thao.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao10.jpg\" border=\"1\" alt=\"Nữ sinh vi&ecirc;n xinh xắn thể hiện kỹ năng b&oacute;ng rổ của m&igrave;nh.\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Nữ sinh vi&ecirc;n xinh xắn thể hiện kỹ năng b&oacute;ng rổ của m&igrave;nh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao13.jpg\" border=\"1\" alt=\"14 th&iacute; sinh tr&igrave;nh diễn trong khi đợi ban gi&aacute;m khảo th&ocirc;ng b&aacute;o kết quả.\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">14 th&iacute; sinh tr&igrave;nh diễn trong khi đợi ban gi&aacute;m khảo th&ocirc;ng b&aacute;o kết quả.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao11.jpg\" border=\"1\" alt=\"Danh hiệu Miss Thể thao cuối c&ugrave;ng được trao cho Lệ Hằng (khoa Y sinh), Mr thể thao d&agrave;nh cho Trung Hiếu (khoa thể thao giải tr&iacute;).\" width=\"350\" height=\"490\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Danh hiệu Miss Thể thao cuối c&ugrave;ng được trao cho Lệ Hằng (khoa Y sinh), Mr thể thao d&agrave;nh cho Trung Hiếu (khoa thể thao giải tr&iacute;).</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/89/thethao12.jpg\" border=\"1\" alt=\"Đ&ecirc;m diễn thu h&uacute;t h&agrave;ng trăm sinh vi&ecirc;n thể thao tham dự.\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"left\">Đ&ecirc;m diễn thu h&uacute;t h&agrave;ng trăm sinh vi&ecirc;n thể thao. Chương tr&igrave;nh được t&agrave;i trợ bởi c&ocirc;ng ty Codad v&agrave; Shytex.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ki&ecirc;n Cường</strong></p>', 2, 1, 2815, 0, 1),
(121, 'Hot girl Bảo Ngọc khoe vẻ đẹp lạnh lùng', 'Hot-Girl-Bao-Ngoc-Khoe-Ve-Dep-Lanh-Lung', 'Thoát khỏi hình ảnh trẻ trung, xinh tươi hằng ngày, hot girl của Sài Gòn hóa thân thành thiếu nữ mang vẻ đẹp buồn, chất chứa nhiều tâm trạng. Bảo Ngọc cho biết, đây là cách cô làm mới chính mình.', '400x250.jpg', NULL, 22, '<h1 class=\"Title\">Hot girl Bảo Ngọc khoe vẻ đẹp lạnh l&ugrave;ng</h1>\r\n<h2 class=\"Lead\">Tho&aacute;t khỏi h&igrave;nh ảnh trẻ trung, xinh tươi hằng ng&agrave;y, hot girl của S&agrave;i G&ograve;n h&oacute;a th&acirc;n th&agrave;nh thiếu nữ mang vẻ đẹp buồn, chất chứa nhiều t&acirc;m trạng. Bảo Ngọc cho biết, đ&acirc;y l&agrave; c&aacute;ch c&ocirc; l&agrave;m mới ch&iacute;nh m&igrave;nh.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/2E/81/bao-ngoc-1.jpg\" border=\"1\" alt=\"Tho&aacute;t khỏi h&igrave;nh ảnh dễ thương quen thuộc Bảo Ngọc h&oacute;a th&acirc;n th&agrave;nh một thiếu nữ mang vẻ đẹp lạnh l&ugrave;ng đầy c&aacute; t&iacute;nh.\" width=\"400\" height=\"593\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bảo Ngọc g&oacute;p mặt trong chung kết Miss Teen khu vực ph&iacute;a Nam. Tuy được y&ecirc;u mến nhưng c&ocirc; trượt top 20.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/2E/81/bao-ngoc-2.jpg\" border=\"1\" alt=\"L&agrave; gương mặt được nhiều kh&aacute;n giả hi vọng, nhưng Bảo Ngọc phải chia tay sớm với cuộc thi Miss Teen v&igrave; kh&ocirc;ng c&oacute; t&ecirc;n trong danh s&aacute;ch 20 th&iacute; sinh. C&ocirc; cho biết cũng hơi buồn nhưng vẫn tự tin cho rằng m&igrave;nh cũng l&agrave; một Miss Teen. C&ocirc; cho biết sẽ c&oacute; c&aacute;ch để l&agrave;m cho độc giả kh&ocirc;ng qu&ecirc;n m&igrave;nh.\" width=\"400\" height=\"572\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bảo Ngọc chia sẻ: \"C&oacute; lẽ do thời gian qu&aacute; ngắn, em kh&ocirc;ng chuẩn bị tốt trong phần thi t&agrave;i năng. D&ugrave; hơi buồn, nhưng em tin, m&igrave;nh cũng l&agrave; một Miss Teen trong mắt kh&aacute;n giả\".</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/2E/81/bao-ngoc-3.jpg\" border=\"1\" alt=\"Thời gian gần đ&acirc;y Bảo Ngọc cho biết kh&aacute; bận rộn v&igrave; nhận được nhiều lời mời chụp h&igrave;nh v&agrave; đ&oacute;ng clip quảng c&aacute;o. Hiện đang l&agrave; một học sinh cuối cấp n&ecirc;n Bảo Ngọc cho biết sẽ tập chung v&agrave;o việc học trong thời gian tới.\" width=\"400\" height=\"571\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&ocirc; dự định thử sức ở những cuộc thi kh&aacute;c khi c&oacute; điều kiện.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/2E/81/bao-ngoc-4.jpg\" border=\"1\" alt=\"C&ocirc; cho biết c&oacute; thể sẽ tham gia v&agrave;o những cuộc thi d&agrave;nh cho c&aacute;c bạn tuổi teen sau khi ho&agrave;n th&agrave;nh việc học h&agrave;nh thi cử.\" width=\"480\" height=\"334\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bảo Ngọc từng l&agrave;m người mẫu từ năm lớp 7. Thời gian gần đ&acirc;y, c&ocirc; kh&aacute; bận rộn với nhiều lời mời chụp h&igrave;nh v&agrave; đ&oacute;ng clip quảng c&aacute;o.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/2E/81/bao-ngoc-5.jpg\" border=\"1\" alt=\"Bảo Ngọc dự kiến sẽ thi v&agrave;o ng&agrave;nh quản trị kinh doanh, hoặc tin dung vi muốn đi theo con đường kinh doanh.\" width=\"471\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bảo Ngọc đang học lớp 12 v&agrave; dự kiến thi v&agrave;o ng&agrave;nh Quản trị kinh doanh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/2E/81/bao-ngoc-7.jpg\" border=\"1\" alt=\"Mới l&agrave; học sinh lớp 12 nhưng c&ocirc; c&oacute; vẻ l&agrave; người mẫu chuy&ecirc;n nghiệp.\" width=\"400\" height=\"547\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">N&oacute;i về t&ecirc;n gọi hot girl, Bảo Ngọc thấy vui nhưng mong mọi người hiểu đ&uacute;ng về &yacute; nghĩa của từ n&agrave;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/2E/81/bao-ngoc-8.jpg\" border=\"1\" alt=\"Bảo Ngọc đầy c&aacute; t&iacute;nh.\" width=\"480\" height=\"327\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/2E/81/bao-ngoc-9.jpg\" border=\"1\" alt=\"Việc thay đổi phong c&aacute;ch của c&ocirc; thể hiện ngay trong một bộ ảnh.\" width=\"400\" height=\"581\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&ocirc; chọn c&aacute;ch khai th&aacute;c vẻ lạnh l&ugrave;ng, mạnh mẽ trong bộ ảnh n&agrave;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>B&igrave;nh Nguy&ecirc;n<br /></strong><em>Ảnh</em>:<strong>&nbsp;T. Sơn</strong></p>', 2, 1, 2323, 0, 1),
(122, 'Nón lá Việt Nam tại ‘lục địa đen’', 'Non-La-Viet-Nam-Tai-‘Luc-Dia-Den’', 'Bị “hút hồn” bởi vẻ đẹp và sự tiện dụng của nón lá, các thiếu nữ ở Nam Phi thích thú làm dáng với sản phẩm đặc trưng của Việt Nam. VnExpress.net ghi lại hình ảnh tại Festival thanh niên, sinh viên lần thứ 17.', 'Fesitval-17_Non-la-12.jpg', NULL, 22, '<h1 class=\"Title\">N&oacute;n l&aacute; Việt Nam tại &lsquo;lục địa đen&rsquo;</h1>\r\n<h2 class=\"Lead\">Bị &ldquo;h&uacute;t hồn&rdquo; bởi vẻ đẹp v&agrave; sự tiện dụng của n&oacute;n l&aacute;, c&aacute;c thiếu nữ ở Nam Phi th&iacute;ch th&uacute; l&agrave;m d&aacute;ng với sản phẩm đặc trưng của Việt Nam.&nbsp;<em>VnExpress.net</em>&nbsp;ghi lại h&igrave;nh ảnh tại Festival thanh ni&ecirc;n, sinh vi&ecirc;n lần thứ 17.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\"><img src=\"upload/tintuc/Fesitval-17_Non-la-12.jpg\" alt=\"\" width=\"495\" height=\"337\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Trong số c&aacute;c mặt h&agrave;ng được đo&agrave;n Việt Nam đem đi giới thiệu tại Festival thanh ni&ecirc;n, sinh vi&ecirc;n thế giới lần thứ 17 được tổ chức tại Nam Phi, chiếc n&oacute;n l&aacute; lu&ocirc;n l&agrave; mặt h&agrave;ng thu h&uacute;t sự quan t&acirc;m của c&aacute;c thiếu nữ ở &ldquo;lục địa đen&rdquo;.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"upload/tintuc/Fesitval-17_Non-la-8.jpg\" alt=\"\" width=\"495\" height=\"327\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nhiều thiếu nữ cho hay, chiếc n&oacute;n l&aacute; Việt Nam được y&ecirc;u th&iacute;ch bởi n&oacute; nhẹ v&agrave; ph&ugrave; hợp với thời tiết mưa nắng thất thường trong m&ugrave;a h&egrave; ở Nam Phi.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"upload/tintuc/Fesitval-17_Non-la-3.jpg\" alt=\"\" width=\"495\" height=\"347\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Chỉ sau 3 ng&agrave;y, hơn 400 chiếc n&oacute;n l&aacute; mang từ Việt Nam sang&hellip;</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"upload/tintuc/Fesitval-17_Non-la-7.jpg\" alt=\"\" width=\"495\" height=\"322\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">&hellip; đ&atilde; được b&aacute;n hết veo.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"upload/tintuc/Fesitval-17_Non-la-2.jpg\" alt=\"\" width=\"495\" height=\"344\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"upload/tintuc/Fesitval-17_Non-la-4.jpg\" alt=\"\" width=\"495\" height=\"332\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c thiếu nữ ch&acirc;u Phi th&iacute;ch th&uacute; l&agrave;m d&aacute;ng với n&oacute;n l&aacute;.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"upload/tintuc/Fesitval-17_Non-la-14.jpg\" alt=\"\" width=\"495\" height=\"366\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Được hỏi về cảm gi&aacute;c khi đội chiếc n&oacute;n l&aacute; Việt Nam, những người đẹp của &ldquo;lục địa đen&rdquo; nhoẻn miệng cười rồi đ&aacute;p: &ldquo;Very nice!&rdquo;.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"upload/tintuc/Fesitval-17_Chup-anh-2.jpg\" alt=\"\" width=\"495\" height=\"353\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Kh&ocirc;ng chỉ phụ nữ m&agrave; ngay cả c&aacute;c nam giới cũng th&iacute;ch th&uacute; với chiếc n&oacute;n l&aacute;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong>&nbsp;(<em>từ Nam Phi</em>)</p>', 2, 1, 18, 0, 1),
(123, 'Du học hè Singapore 2011', 'Du-Hoc-He-Singapore-2011', 'Đăng ký trước ngày 29/1, học sinh sẽ được tặng 6 triệu đồng khi tham gia lịch trình 4 tuần ở tại resort và tặng 4 triệu đồng cho học sinh tham gia lịch trình 2 hoặc 3 tuần ở tại resort.', '400.jpg', NULL, 22, '<h1 class=\"Title\">Du học h&egrave; Singapore 2011</h1>\r\n<h2 class=\"Lead\">Đăng k&yacute; trước ng&agrave;y 29/1, học sinh sẽ được tặng 6 triệu đồng khi tham gia lịch tr&igrave;nh 4 tuần ở tại resort v&agrave; tặng 4 triệu đồng cho học sinh tham gia lịch tr&igrave;nh 2 hoặc 3 tuần ở tại resort.</h2>\r\n<p class=\"Normal\">Ngo&agrave;i ra, học sinh l&agrave; anh chị em, bạn c&ugrave;ng lớp, bạn c&ugrave;ng cơ quan bố mẹ được khuyến mại 1.000.000 đồng. Tất cả học sinh được tặng một sim điện thoại di động hoặc một thẻ gọi điện thoại quốc tế tại Singapore, bal&ocirc; thời trang, &aacute;o ph&ocirc;ng đồng phục, mũ, một qu&agrave; lưu niệm biểu tượng của Singapore v&agrave; tặng đĩa CD, DVD quay kỷ niệm to&agrave;n cảnh chuyến đi cho mỗi học sinh.</p>\r\n<p class=\"Normal\">Lịch khởi h&agrave;nh: 2 tuần: 29/5, 12/6, 5/6, 3/7, 17/7; 3 tuần: 29/5, 5/6, 3/7, 10/7; 4 tuần: 29/5, 3/7.</p>\r\n<p class=\"Normal\">Chuyến đi gi&uacute;p học sinh n&acirc;ng cao khả năng tiếng Anh qua c&aacute;c kh&oacute;a học v&agrave; giao tiếp với bạn b&egrave; quốc tế. C&aacute;c em sẽ c&oacute; một kỳ nghỉ h&egrave; l&yacute; th&uacute;, được vui chơi tại những thắng cảnh nổi tiếng, thư gi&atilde;n sau một năm học v&agrave; thật nhiều cơ hội t&igrave;m hiểu, l&agrave;m quen với m&ocirc;i trường v&agrave; cuộc sống khi du học.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/54/F0/400.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"282\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chương tr&igrave;nh chi tiết: Học sinh c&oacute; thế lựa chọn ở tại Resort Costa Sands Resort - Down Town East hoặc Homestay (căn hộ chung cư).</p>\r\n<p class=\"Normal\">Học sinh sẽ học tiếng Anh 3 đến 3,5 giờ mỗi ng&agrave;y từ thứ 2 đến thứ 6 hằng tuần ngay tại trường ngoại ngữ Coleman ngay tại trung t&acirc;m Singapore. Tất cả học sinh được kiểm tra xếp lớp ph&ugrave; hợp tr&igrave;nh độ khi tới Singapore. Nửa ng&agrave;y c&ograve;n lại v&agrave; buổi tối, c&aacute;c em sẽ t&igrave;m hiểu, thăm quan, vui chơi, d&atilde; ngoại v&agrave; xem phim.</p>\r\n<p class=\"Normal\">So với chương tr&igrave;nh học h&egrave; của c&aacute;c năm trước, c&aacute;c em được trải nghiệm hai điều đặc biệt:</p>\r\n<p class=\"Normal\">- Sky Park: chi&ecirc;m ngưỡng to&agrave;n cảnh Singapore v&agrave; một phần đất nước Malaysia tr&ecirc;n độ cao gần 200 m, kh&aacute;m ph&aacute; thiết kế độc đ&aacute;o của kh&aacute;ch sạn 7 sao tại vịnh Marina.</p>\r\n<p class=\"Normal\">- Kh&aacute;m ph&aacute; phim trường Hollywood tại Singapore Universal Studios. Thăm quan Đại lộ danh vọng (Walk of Fame), nơi gắn liền với t&ecirc;n tuổi của nhiều ng&ocirc;i sao m&agrave;n ảnh nổi tiếng, gh&eacute; thăm th&agrave;nh phố m&ocirc; phỏng lớn nhất nước Mỹ - New York với hệ thống đ&egrave;n m&agrave;u rực rỡ. Kh&aacute;m ph&aacute; Thế giới kỳ lạ (Far Far Away) v&agrave; xem phim 4D với ch&agrave;ng khổng lồ Shrek nổi tiếng...</p>\r\n<p class=\"Normal\">C&ugrave;ng với c&aacute;c chương tr&igrave;nh: Singapore Flyer; Thăm đảo Sentosa: thế giới đại dương với nhiều lo&agrave;i c&aacute; qu&yacute; hiếm, d&agrave;n nhạc nước Song of the sea, xiếc c&aacute; heo hồng, bảo t&agrave;ng s&aacute;p&hellip; Thư gi&atilde;n cắm trại v&agrave; tắm biển, trượt patin v&agrave; xe đạp địa h&igrave;nh tại b&atilde;i biển East Coast Park Way; thăm vườn chim Jurong v&agrave; vườn th&uacute; Quốc gia Singapore Zoo, vườn th&uacute; đ&ecirc;m Night Safari. Kh&aacute;m ph&aacute; khu khoa học quốc gia, vui chơi tại c&ocirc;ng vi&ecirc;n nước v&agrave; c&ocirc;ng vi&ecirc;n giải tr&iacute; với 14 tr&ograve; chơi cảm gi&aacute;c mạnh...</p>\r\n<p class=\"Normal\">Visco sẽ lu&ocirc;n theo s&aacute;t từng học sinh, hướng dẫn, chăm s&oacute;c sức khoẻ, hỗ trợ v&agrave; tư vấn cho c&aacute;c em để c&oacute; một chuyến đi an to&agrave;n v&agrave; th&uacute; vị trong suốt h&agrave;nh tr&igrave;nh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#efefef\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#dfdfdf\">\r\n<tbody>\r\n<tr>\r\n<td><span style=\"color: #800000;\"><strong>Li&ecirc;n hệ: Văn ph&ograve;ng Visco</strong></span></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">H&agrave; Nội: 230 Kim M&atilde;, Ba Đ&igrave;nh; ĐT: 04.3726 1938; Fax: 04.3726 1936 - E-mail:<a class=\"Normal\" href=\"mailto:viscohanoi@visco.edu.vn\">viscohanoi@visco.edu.vn</a></p>\r\n<p class=\"Normal\">TP HCM: 239 CMT 8, nh&agrave; A02 Chung cư Văn h&oacute;a, phường 4, quận 3 - ĐT: 08 3832 8416; Fax: 08 3832 8417; E-mail:&nbsp;<a class=\"Normal\" href=\"mailto:viscohcm@visco.edu.vn\">viscohcm@visco.edu.vn</a></p>\r\n<p class=\"Normal\">Đ&agrave; Nẵng: 433 Phan Chu Trinh, Hải Ch&acirc;u - ĐT: 0511 3552597/96; Fax : 0511 355 2596; E-mail:&nbsp;<a class=\"Normal\" href=\"mailto:viscodn@visco.edu.vn\">viscodn@visco.edu.vn</a></p>\r\n<p class=\"Normal\">Hải Ph&ograve;ng: 309 Hai B&agrave; Trưng, C&aacute;t D&agrave;i, L&ecirc; Ch&acirc;n - ĐT: 031 3950215/3950748; Fax : 0313 950215; Email:&nbsp;<a class=\"Normal\" href=\"mailto:viscohp@visco.edu.vn\">viscohp@visco.edu.vn</a>.</p>\r\n<p class=\"Normal\">Tư vấn online Ym!: duhocvisco; Website&nbsp;<a class=\"Normal\" href=\"http://www.visco.edu.vn/\">http://www.visco.edu.vn/</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Visco)</em></p>', 4, 1, 449, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(160, 'Tư vấn chuyên ngành khoa học máy tính tại Anh', 'Tu-Van-Chuyen-Nganh-Khoa-Hoc-May-Tinh-Tai-Anh', 'Hội thảo chủ đề “Học chuyên ngành khoa học máy tính tại Anh - những điều cần biết” được tổ chức ngày 22/11 từ 18h đến 19h30 tại Hội đồng Anh Hà Nội và từ 18h30 đến 20h tại khách sạn Norfolk, 117 Lê Thánh Tôn, quận 1, TP HCM.', 'hoi-dong-anh-1.jpg', NULL, 22, '<h1 class=\"Title\">Tư vấn chuy&ecirc;n ng&agrave;nh khoa học m&aacute;y t&iacute;nh tại Anh</h1>\r\n<h2 class=\"Lead\">Hội thảo chủ đề &ldquo;Học chuy&ecirc;n ng&agrave;nh khoa học m&aacute;y t&iacute;nh tại Anh - những điều cần biết&rdquo; được tổ chức ng&agrave;y 22/11 từ 18h đến 19h30 tại Hội đồng Anh H&agrave; Nội v&agrave; từ 18h30 đến 20h tại kh&aacute;ch sạn Norfolk, 117 L&ecirc; Th&aacute;nh T&ocirc;n, quận 1, TP HCM.</h2>\r\n<p class=\"Normal\">Chương tr&igrave;nh nhằm đem lại cho sinh vi&ecirc;n những kiến thức bổ &iacute;ch về c&aacute;ch chọn kho&aacute; học, chọn trường c&ugrave;ng những kỹ năng học tập trong lĩnh vực n&agrave;y th&ocirc;ng qua sự chia sẻ kinh nghiệm của c&aacute;c cựu du học sinh Anh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/31/9F/hoi-dong-anh-1.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"267\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Kể từ khi chiếc m&aacute;y vi t&iacute;nh đầu ti&ecirc;n tr&ecirc;n thế giới được ph&aacute;t minh bởi nh&agrave; to&aacute;n học người Anh Charles Babbage v&agrave;o những năm 1830, đến nay Anh vẫn lu&ocirc;n l&agrave; một trong những nước dẫn đầu thế giới trong lĩnh vực c&ocirc;ng nghệ th&ocirc;ng tin v&agrave; khoa học m&aacute;y t&iacute;nh. C&aacute;c trường đại học đ&agrave;o tạo chuy&ecirc;n ng&agrave;nh n&agrave;y tại Anh lu&ocirc;n c&oacute; mối li&ecirc;n hệ, hợp t&aacute;c chặt chẽ với c&aacute;c tập đo&agrave;n h&agrave;ng đầu tr&ecirc;n thế giới, do đ&oacute; c&aacute;c kho&aacute; học tại Anh trong lĩnh vực n&agrave;y kh&ocirc;ng chỉ trau dồi cho bạn những kiến thức cập nhật nhất m&agrave; cả những kỹ năng về kinh doanh, quản l&yacute; dự &aacute;n v&agrave; l&agrave;m việc nh&oacute;m. Sinh vi&ecirc;n tốt nghiệp chuy&ecirc;n ng&agrave;nh n&agrave;y tại Anh lu&ocirc;n được c&aacute;c c&ocirc;ng ty h&agrave;ng đầu thế giới mở rộng cửa đ&oacute;n nhận.</p>\r\n<p class=\"Normal\">Tham gia chương tr&igrave;nh UK Alumni Talk th&aacute;ng 11 của Hội đồng Anh chủ đề &ldquo;Học chuy&ecirc;n ng&agrave;nh khoa học m&aacute;y t&iacute;nh tại Anh - Những điều cần biết&rdquo; c&oacute; c&aacute;c cựu du học sinh Anh xuất sắc trong ng&agrave;nh n&agrave;y gồm:</p>\r\n<p class=\"Normal\">H&agrave; Nội: Nguyễn Thanh B&igrave;nh - Cựu sinh vi&ecirc;n đại học East of Anglia, bậc thạc sĩ v&agrave; tiến sĩ chuy&ecirc;n ng&agrave;nh khoa học m&aacute;y t&iacute;nh. Đạt học bổng 322 của Bộ Gi&aacute;o dục v&agrave; Đ&agrave;o tạo v&agrave; học bổng của tổ chức EPSR, Anh</p>\r\n<p class=\"Normal\">L&ecirc; Việt Thắng - Cựu sinh vi&ecirc;n đại học Warwick, cử nh&acirc;n chuy&ecirc;n ng&agrave;nh khoa học m&aacute;y t&iacute;nh. Từng c&ocirc;ng t&aacute;c tại ng&acirc;n h&agrave;ng UBS tại Anh.</p>\r\n<p class=\"Normal\">TP HCM: Trần Huy Đức - Cựu sinh vi&ecirc;n đại học Northumbria, cử nh&acirc;n chuy&ecirc;n ng&agrave;nh c&ocirc;ng nghệ th&ocirc;ng tin tại Anh. Vừa ho&agrave;n th&agrave;nh kho&aacute; thạc sĩ c&ugrave;ng chuy&ecirc;n ng&agrave;nh tại Thụy Điển. Đức c&oacute; 4 năm kinh nghiệm l&agrave;m việc trong lĩnh vực c&ocirc;ng nghệ th&ocirc;ng tin tại nhiều tổ chức kh&aacute;c nhau v&agrave; hiện l&agrave; chuy&ecirc;n vi&ecirc;n ban dự &aacute;n của ng&acirc;n h&agrave;ng ANZ tại TP HCM.</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh được t&agrave;i trợ bởi đại học Central Lancashire, đại học East Anglia v&agrave; đại học West of England.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#efefef\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#dfdfdf\">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style=\"color: #800000;\">Li&ecirc;n hệ: Hội đồng Anh</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Điện thoại: HN 04 3843 6780 - TP HCM: 08 3823 2862</p>\r\n<p class=\"Normal\">Emai: HN&nbsp;<a class=\"Normal\" href=\"mailto:educationuk@britishcouncil.org.vn\">educationuk@britishcouncil.org.vn</a>&nbsp;hoặc&nbsp;<br />Email TP HCM:&nbsp;<a class=\"Normal\" href=\"mailto:educationuk.hcmc@britishcouncil.org.vn\">educationuk.hcmc@britishcouncil.org.vn</a></p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Hội đồng Anh</em>)</p>', 4, 1, 395, 0, 1),
(161, 'Hội thảo \'Con đường nhanh nhất đến thành công\'', '', 'Ông Matthew Yap, Chủ tịch Hội đồng Học thuật của Cao đẳng quốc tế Melior, Singapore sẽ có buổi diễn giải tại hội thảo \"Con đường nhanh nhất đến thành công\" vào 8h30 ngày 28/11 tại tòa nhà Melior, 149C Trương Định, phường 9, quận 3, TP HCM.', 'duhoc250.jpg', NULL, 22, '<h1 class=\"Title\">Hội thảo \'Con đường nhanh nhất đến th&agrave;nh c&ocirc;ng\'</h1>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/duhoc250.jpg\" border=\"1\" alt=\"\" width=\"250\" height=\"195\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class=\"Lead\">&Ocirc;ng Matthew Yap, Chủ tịch Hội đồng Học thuật của Cao đẳng quốc tế Melior, Singapore sẽ c&oacute; buổi diễn giải tại hội thảo \"Con đường nhanh nhất đến th&agrave;nh c&ocirc;ng\" v&agrave;o 8h30 ng&agrave;y 28/11 tại t&ograve;a nh&agrave; Melior, 149C Trương Định, phường 9, quận 3, TP HCM.</h2>\r\n<p class=\"Normal\">Trường Kinh doanh Melior thuộc Tập đo&agrave;n Gi&aacute;o dục Melior Singapore, l&agrave; đại diện đ&agrave;o tạo duy nhất của ĐH Central Queensland (CQUniversity - Australia) chuy&ecirc;n đ&agrave;o tạo c&aacute;c chuy&ecirc;n ng&agrave;nh về: quản trị kinh doanh, quản trị marketing, quản trị du lịch v&agrave; kh&aacute;ch sạn. Sinh vi&ecirc;n tốt nghiệp sẽ được cấp bằng cử nh&acirc;n của trường CQUniversity.</p>\r\n<p class=\"Normal\">Melior sẽ cung cấp c&aacute;c chương tr&igrave;nh đ&agrave;o tạo chất lượng cao v&agrave; chuẩn bị nghề nghiệp tốt nhất cho tất cả sinh vi&ecirc;n sau khi ra trường. Tất cả sinh vi&ecirc;n tốt nghiệp tại Melior sẽ được thực tập tại c&aacute;c tập đo&agrave;n, c&ocirc;ng ty quốc tế v&agrave; kh&aacute;ch sạn nổi tiếng. Tại đ&acirc;y, c&aacute;c bạn sẽ được học cơ cấu tổ chức, hoạt động v&agrave; phong c&aacute;ch l&agrave;m việc, qua đ&oacute; sẽ c&oacute; nhiều kinh nghiệm thực tiễn qu&yacute; b&aacute;u cho con đường ph&aacute;t triển sự nghiệp của m&igrave;nh.</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh miễn ph&iacute;, chỉ d&agrave;nh ri&ecirc;ng cho c&aacute;c bạn mới tốt nghiệp THPT. Do số chỗ ngồi giới hạn, đăng k&yacute; trước qua số điện thoại: (08) 39351919, hoặc qua website&nbsp;<a class=\"Normal\" href=\"http://www.mbs.edu.vn/\">www.mbs.edu.vn</a></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#dfdfdf\">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style=\"color: #800000;\">Li&ecirc;n hệ: Trường Kinh doanh Melior</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">149C Trương Định, phường 9, quận 3, TP HCM.<br />Email:&nbsp;<a class=\"Normal\" href=\"mailto:info@mbs.edu.vn\">info@mbs.edu.vn</a><br />Website:&nbsp;<a class=\"Normal\" href=\"http://www.mbs.edu.vn/\">http://www.mbs.edu.vn/</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Trường Kinh doanh Melior</em>)</p>', 4, 1, 3, 0, 1),
(162, '100% học phí cho 7 trường ĐH tại Anh', '100--Hoc-Phi-Cho-7-Truong-Dh-Tai-Anh', 'Ông Tom Keenan, đại diện tuyển sinh của INTO sẽ có buổi nói chuyện về chương trình và cơ hội học bổng cùng Tập đoàn INTO vào 17h ngày 29/11 tại TP HCM và 15h ngày 30/11 tại Hà Nội.', 'duhoc-250.jpg', NULL, 22, '<h1 class=\"Title\">100% học ph&iacute; cho 7 trường ĐH tại Anh</h1>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/duhoc-250.jpg\" border=\"1\" alt=\"\" width=\"250\" height=\"195\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h2 class=\"Lead\">&Ocirc;ng Tom Keenan, đại diện tuyển sinh của INTO sẽ c&oacute; buổi n&oacute;i chuyện về chương tr&igrave;nh v&agrave; cơ hội học bổng c&ugrave;ng Tập đo&agrave;n INTO v&agrave;o 17h ng&agrave;y 29/11 tại TP HCM v&agrave; 15h ng&agrave;y 30/11 tại H&agrave; Nội.</h2>\r\n<p class=\"Normal\">Chương tr&igrave;nh cụ thể:</p>\r\n<p class=\"Normal\">17h, thứ hai ng&agrave;y 29/11, tại Visco 239 C&aacute;ch Mạng Th&aacute;ng T&aacute;m, nh&agrave; A2, chung cư Văn H&oacute;a, phường 4, quận 3, TP HCM v&agrave; 15h, thứ 3 ng&agrave;y 30/11, tại Visco 230 Kim M&atilde;, Ba Đ&igrave;nh, H&agrave; Nội.</p>\r\n<p class=\"Normal\">Trung t&acirc;m INTO l&agrave; một phần của 7 trường đại học danh tiếng tại Anh: East Anglia, Exeter, Newcastle, Manchester, Glasgow Caledonian, City London v&agrave; Queen&rsquo;s Belfast, chịu tr&aacute;ch nhiệm đ&agrave;o tạo c&aacute;c kh&oacute;a tiếng Anh, A-level, dự bị đại học, năm nhất đại học, dự bị thạc sĩ cho sinh vi&ecirc;n quốc tế mong muốn v&agrave;o học tại c&aacute;c trường đại học tr&ecirc;n v&agrave; c&aacute;c trường đại học danh tiếng kh&aacute;c.</p>\r\n<p class=\"Normal\">Trung t&acirc;m INTO đại học East Anglia đ&atilde; mở cơ sở mới tại London với đầy đủ c&aacute;c chương tr&igrave;nh đ&agrave;o tạo.</p>\r\n<p class=\"Normal\">Hiện INTO d&agrave;nh những suất học bổng l&ecirc;n tới 100% học ph&iacute; cho kh&oacute;a dự bị đại học v&agrave; 30% học ph&iacute; cho c&aacute;c chương tr&igrave;nh A-level, năm thứ nhất đại học v&agrave; dự bị thạc sĩ, khai giảng th&aacute;ng 1 v&agrave; 9/2011.</p>\r\n<p class=\"Normal\">Khi đến nộp hồ sơ, c&aacute;c em mang theo bản copy kết quả học tập mới nhất v&agrave; chứng chỉ tiếng Anh (nếu c&oacute;). Sinh vi&ecirc;n chưa c&oacute; điểm IELTS, c&oacute; thể l&agrave;m b&agrave;i thi tiếng Anh INTO thay thế.</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh học bổng như sau:</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh A-level tại đại học East Anglia v&agrave; đại học Manchester: Học bổng 10-30% học ph&iacute;, d&agrave;nh cho học sinh ho&agrave;n tất lớp 11, điểm trung b&igrave;nh 7 trở l&ecirc;n, IELTS 5-6.</p>\r\n<p class=\"Normal\">Ho&agrave;n tất chương tr&igrave;nh A-level sinh vi&ecirc;n c&oacute; cơ hội học tại c&aacute;c trường đại học h&agrave;ng đầu của Anh: Oxford, Cambridge, LSE&hellip; v&agrave; c&aacute;c trường đại học danh tiếng kh&aacute;c tr&ecirc;n thế giới. Đặc biệt ở đ&acirc;y sinh vi&ecirc;n sẽ do c&aacute;c gi&aacute;o sư đại học giảng dạy v&agrave; được sử dụng cơ sở vật chất hiện đại của trường đại học.</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh dự bị đại học: Học bổng 10-100% học ph&iacute;, d&agrave;nh cho học sinh đ&atilde; ho&agrave;n tất lớp 11, điểm trung b&igrave;nh 7 trở l&ecirc;n, IELTS tối thiểu l&agrave; 5.</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh gồm c&aacute;c ng&agrave;nh học: Quản trị kinh doanh, kỹ sư m&aacute;y t&iacute;nh, y sinh học, h&oacute;a sinh, c&ocirc;ng nghệ m&ocirc;i trường, dược, to&aacute;n, kiến tr&uacute;c, luật, ch&iacute;nh trị&hellip; Sau khi ho&agrave;n tất kh&oacute;a học dự bị, sinh vi&ecirc;n được nhận v&agrave;o học thẳng năm nhất đại học tại c&aacute;c trường đại học của Into.</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh năm nhất đại học: Học bổng 10-30% học ph&iacute;, d&agrave;nh cho học sinh tốt nghiệp lớp 12, điểm trung b&igrave;nh 7 trở l&ecirc;n, IELTS tối thiểu 5,5 - 6. Đối với những trường hợp điểm trung b&igrave;nh lớp 12 dưới 7, th&igrave; phải l&agrave; sinh vi&ecirc;n của đại học hoặc cao đẳng tại Việt Nam.</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh gồm c&oacute; c&aacute;c ng&agrave;nh học: Kinh doanh, kinh tế, hệ thống th&ocirc;ng tin v&agrave; m&aacute;y t&iacute;nh&hellip; Sau khi ho&agrave;n tất kh&oacute;a học tại Into, học sinh được nhận học thẳng năm thứ hai tại một trong c&aacute;c trường đại học danh tiếng của INTO.</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh dự bị thạc sĩ: Học bổng 10-30% học ph&iacute;, d&agrave;nh cho sinh vi&ecirc;n tốt nghiệp đại học, cao đẳng, điểm trung b&igrave;nh từ kh&aacute; trở l&ecirc;n, IELTS tối thiểu 5,5 (cho kh&oacute;a học th&aacute;ng 9) v&agrave; 6 (cho kh&oacute;a học th&aacute;ng 1).</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh gồm c&aacute;c ng&agrave;nh học: Kinh doanh, t&agrave;i ch&iacute;nh, luật, khoa học tự nhi&ecirc;n, m&aacute;y t&iacute;nh, khoa học x&atilde; hội, kỹ thuật, ch&iacute;nh trị...</p>\r\n<p class=\"Normal\">Ngo&agrave;i ra, INTO c&oacute; c&aacute;c kh&oacute;a tiếng Anh học thuật chuy&ecirc;n s&acirc;u khai giảng quanh năm d&agrave;nh cho sinh vi&ecirc;n chưa c&oacute; đủ tr&igrave;nh độ tiếng Anh học ch&iacute;nh kh&oacute;a.</p>\r\n<p class=\"Normal\"><a class=\"Normal\" href=\"http://vnexpress.net/GL/Xa-hoi/Co-hoi-Du-hoc/2010/11/3BA235EE/page_2.asp\">Th&ocirc;ng tin chi tiết hơn về 7 trường đại học kể tr&ecirc;n</a></p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\" bgcolor=\"#dfdfdf\">\r\n<tbody>\r\n<tr>\r\n<td><strong><span style=\"color: #800000;\">Li&ecirc;n hệ: Visco</span></strong></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">- 230 Kim M&atilde;, Ba Đ&igrave;nh, H&agrave; Nội.<br />ĐT: (04) 3726 1938. E-mail:&nbsp;<a class=\"Normal\" href=\"mailto:viscohanoi@visco.edu.vn\">viscohanoi@visco.edu.vn</a><br />- 239 C&aacute;ch Mạng Th&aacute;ng T&aacute;m, Nh&agrave; A02, Chung cư văn ho&aacute;, phường 4, quận 3&nbsp;<br />ĐT: (08) 38328416. E-mail:&nbsp;<a class=\"Normal\" href=\"mailto:viscohcm@visco.edu.vn\">viscohcm@visco.edu.vn</a><br />- 309 Hai B&agrave; Trưng, C&aacute;t d&agrave;i, L&ecirc; Ch&acirc;n, Hải Ph&ograve;ng.<br />ĐT: (031) 3950748. Email:&nbsp;<a class=\"Normal\" href=\"mailto:viscohp@visco.edu.vn\">viscohp@visco.edu.vn</a><br />- 433 Phan Chu Trinh, Hải Ch&acirc;u, Đ&agrave; Nẵng.<br />ĐT: (0511) 3552596 - 3552597. E-mail: viscodn@visco.edu.vn<span style=\"color: #000000;\"><br /></span>YM:&nbsp;<a class=\"Normal\" href=\"mailto:duhocvisco@yahoo.com\">duhocvisco@yahoo.com</a>&nbsp;<br />Th&ocirc;ng tin c&oacute; thể tham khảo th&ecirc;m tại:&nbsp;<a class=\"Normal\" href=\"http://www.into.uk.com/\">http://www.into.uk.com/</a>&nbsp;hoặc<a class=\"Normal\" href=\"http://www.visco.edu.vn/\">http://www.visco.edu.vn/</a></p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Visco</em>)</p>', 4, 1, 894, 0, 1),
(163, 'Vợ đánh hổ cứu chồng', 'Vo-Danh-Ho-Cuu-Chong', 'Một phụ nữ ở phía bắc Malaysia đánh hổ bằng muỗng gỗ khi con thú tấn công chồng bà, nhờ đó người đàn ông thoát chết.', 'tiger.jpg', NULL, 22, '<h1 class=\"Title\">Vợ đ&aacute;nh hổ cứu chồng</h1>\r\n<h2 class=\"Lead\">Một phụ nữ ở ph&iacute;a bắc Malaysia đ&aacute;nh hổ bằng muỗng gỗ khi con th&uacute; tấn c&ocirc;ng chồng b&agrave;, nhờ đ&oacute; người đ&agrave;n &ocirc;ng tho&aacute;t chết.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/67/1b/tiger.jpg\" alt=\"Một con hổ tại Malaysia. Ảnh:\" width=\"450\" height=\"267\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một con hổ tại Malaysia. Ảnh:&nbsp;<em>ecoficial.com</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tambun Gediu l&agrave; một người d&acirc;n sống s&aacute;t rừng ở bang Perak, Malaysia.&nbsp;<em>AP</em>&nbsp;h&ocirc;m qua dẫn lời một cảnh s&aacute;t giấu t&ecirc;n cho hay, &ocirc;ng bị một con hổ vồ khi săn thỏ trong rừng h&ocirc;m 12/2.</p>\r\n<p class=\"Normal\">B&agrave; Han Besau, người vợ 55 tuổi của Gediu, chạy ra ngo&agrave;i với chiếc muỗng gỗ khi nghe tiếng h&eacute;t của chồng. Người phụ nữ đập chiếc muỗng v&agrave;o đầu con vật nhiều lần khiến n&oacute; hoảng sợ v&agrave; bỏ chạy.</p>\r\n<p class=\"Normal\">Khi v&agrave;o viện để điều trị c&aacute;c vết r&aacute;ch tr&ecirc;n mặt v&agrave; đ&ocirc;i ch&acirc;n do m&oacute;ng vuốt của hổ g&acirc;y n&ecirc;n, Gediu kể rằng ống đ&atilde; cố leo l&ecirc;n c&acirc;y để trốn, song con hổ k&eacute;o &ocirc;ng xuống.</p>\r\n<p class=\"Normal\">&ldquo;T&ocirc;i rất sợ v&agrave; d&ugrave;ng hết sức để đấm v&agrave;o mặt con hổ, song n&oacute; kh&ocirc;ng bu&ocirc;ng. T&ocirc;i cố gắng vật lộn để đẩy h&agrave;m răng của n&oacute; ra xa cơ thể t&ocirc;i. C&oacute; lẽ con hổ đ&atilde; t&aacute;t t&ocirc;i đến chết nếu vợ t&ocirc;i kh&ocirc;ng xuất hiện&rdquo;,&nbsp;<em>New Straits Times</em>&nbsp;dẫn lời kể của &ocirc;ng Gediu.</p>\r\n<p class=\"Normal\">Shabrina Shariff, gi&aacute;m đốc Cơ quan bảo vệ động vật hoang d&atilde; của bang, n&oacute;i với nhật b&aacute;o&nbsp;<em>The Star</em>&nbsp;rằng lực lượng bảo vệ rừng sẽ truy t&igrave;m con hổ v&agrave; đuổi n&oacute; v&agrave;o s&acirc;u hơn trong rừng, nơi kh&ocirc;ng c&oacute; con người sinh sống.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Việt Linh</strong></p>', 5, 2, 1727, 0, 1),
(327, 'Thế khó của Hàn Quốc sau khi bị \'đánh\'', '', 'Bán đảo Triều Tiên vốn căng thẳng hơn nửa thế kỷ, nhưng vụ tấn công hôm qua của miền bắc có thể coi là \"lịch sử\" qua quy mô của nó và sự kiện này đang đặt Hàn Quốc vào thế khó vì họ có quá nhiều thứ để mất.', 'Yeon.jpg', NULL, 22, '<h1 class=\"Title\">Thế kh&oacute; của H&agrave;n Quốc sau khi bị \'đ&aacute;nh\'</h1>\r\n<h2 class=\"Lead\">B&aacute;n đảo Triều Ti&ecirc;n vốn căng thẳng hơn nửa thế kỷ, nhưng vụ tấn c&ocirc;ng h&ocirc;m qua của miền bắc c&oacute; thể coi l&agrave; \"lịch sử\" qua quy m&ocirc; của n&oacute; v&agrave; sự kiện n&agrave;y đang đặt H&agrave;n Quốc v&agrave;o thế kh&oacute; v&igrave; họ c&oacute; qu&aacute; nhiều thứ để mất.</h2>\r\n<p id=\"story_continues_1\" class=\"Normal\">Cho d&ugrave; b&aacute;n đảo Triều Ti&ecirc;n chưa bao giờ hết căng thẳng kể từ cuộc chiến 1950-1953 đến nay, những h&igrave;nh ảnh h&ocirc;m qua từ đảo Yeonpyeong của H&agrave;n Quốc sau khi bị Triều Ti&ecirc;n n&atilde; ph&aacute;o vẫn khiến nhiều người sốc. H&agrave;ng loạt cột kh&oacute;i bốc cao từ những ng&ocirc;i nh&agrave; ch&aacute;y dữ dội v&agrave; người d&acirc;n địa phương sợ h&atilde;i chạy tới nơi tr&uacute; ấn. H&igrave;nh ảnh n&agrave;y phần n&agrave;o gợi nhớ k&yacute; ức về cuộc chiến chưa phai của những người Triều Ti&ecirc;n cao tuổi.</p>\r\n<p class=\"Normal\">Hiện trường giao tranh lần n&agrave;y cũng kh&aacute;c nhiều so với c&aacute;c vụ đụng độ trước đ&acirc;y của qu&acirc;n đội hai miền, vốn thường xảy ra giữa t&agrave;u hải qu&acirc;n của hai b&ecirc;n hoặc những vụ bắn trả ngang v&ugrave;ng phi qu&acirc;n sự tr&ecirc;n bi&ecirc;n giới tr&ecirc;n bộ. Lần n&agrave;y chắc chắn nghi&ecirc;m trọng hơn v&igrave; phần l&atilde;nh thổ c&oacute; người ở của H&agrave;n Quốc đ&atilde; bị tấn c&ocirc;ng dữ dội, g&acirc;y ra thương vong v&agrave; thiệt hại về vật chất.</p>\r\n<p class=\"Normal\">Tuy vậy, vụ tấn c&ocirc;ng cũng kh&ocirc;ng dẫn đến cảnh hoảng sợ ở thủ đ&ocirc; Seoul, nằm c&aacute;ch nơi bị ph&aacute;o k&iacute;ch chỉ hơn 100 km về ph&iacute;a t&acirc;y. Nhịp sống ở th&agrave;nh phố n&aacute;o nhiệt n&agrave;y vẫn diễn ra b&igrave;nh thường, ngoại trừ những tin tức dồn dập tr&ecirc;n truyền th&ocirc;ng v&agrave; đồng won bị mất gi&aacute;. Kh&ocirc;ng hề c&oacute; cảnh mọi người nh&aacute;o nh&aacute;c t&igrave;m c&aacute;ch rời khỏi th&agrave;nh phố, v&igrave; dường như họ đ&atilde; quen với việc sống trong cảnh n&agrave;y từ nhiều năm nay.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/35/F0/Yeon.jpg\" border=\"1\" alt=\"Những ng&ocirc;i nh&agrave; bị ph&aacute; huỷ do đạn ph&aacute;o của miền bắc tr&ecirc;n đảo. Ảnh: AP\" width=\"399\" height=\"266\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những ng&ocirc;i nh&agrave; bị ph&aacute; huỷ do đạn ph&aacute;o của miền bắc tr&ecirc;n đảo Yeonpyeong. Ảnh:<em>AP</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 class=\"SubTitle\">Tiến tho&aacute;i lưỡng nan</h3>\r\n<p class=\"Normal\">Ch&iacute;nh phủ H&agrave;n Quốc cũng c&oacute; phản ứng \"b&igrave;nh thản\" tương tự người d&acirc;n, khi qu&acirc;n đội bắn trả miền bắc bằng khoảng 80 quả đạn ph&aacute;o, nhưng họ kh&ocirc;ng hề t&iacute;nh đến việc trả đũa qu&acirc;n sự tr&ecirc;n quy m&ocirc; lớn. Tổng thống Lee Myung-bak một mặt lệnh cho cấp dưới \"trừng phạt đ&iacute;ch đ&aacute;ng\" miền bắc, một mặt lại y&ecirc;u cầu họ phải đảm bảo \"t&igrave;nh h&igrave;nh kh&ocirc;ng leo thang\".</p>\r\n<p class=\"Normal\">Nguy&ecirc;n nh&acirc;n v&igrave; việc leo thang căng thẳng qu&acirc;n sự ở mức độ nghi&ecirc;m trọng sẽ g&acirc;y ra những hậu quả chết người về cuộc sống, t&agrave;i sản v&agrave; cho cả nền kinh tế H&agrave;n Quốc. N&oacute;i c&aacute;ch kh&aacute;c H&agrave;n Quốc c&oacute; qu&aacute; nhiều thứ để mất nếu để chiến tranh thực sự giữa hai miền xảy ra. Nhưng ch&iacute;nh điều n&agrave;y đ&atilde; đẩy họ v&agrave;o thế kh&oacute; khi phải t&igrave;m c&aacute;ch đ&aacute;p trả l&agrave;m sao xứng đ&aacute;ng với h&agrave;nh động được cho l&agrave; \"khi&ecirc;u kh&iacute;ch\" tr&ecirc;n mức b&igrave;nh thường đến từ miền bắc như h&ocirc;m qua.</p>\r\n<p id=\"story_continues_2\" class=\"Normal\">Vậy ch&iacute;nh phủ H&agrave;n Quốc c&oacute; thể l&agrave;m g&igrave; sau khi đ&atilde; cho đ&aacute;p trả Triều Ti&ecirc;n bằng loạt đạn ph&aacute;o? Theo&nbsp;<em>BBC</em>&nbsp;nhiều khả năng sẽ c&oacute; phản ứng th&ocirc;ng qua con đường ngoại giao, v&iacute; dụ như một lời k&ecirc;u gọi Hội đồng Bảo an Li&ecirc;n Hợp Quốc trừng phạt h&agrave;nh động của B&igrave;nh Nhưỡng. Tuy nhi&ecirc;n, đằng sau những lời tuy&ecirc;n bố l&ecirc;n &aacute;n giống như trong vụ chiến hạm Cheonan của H&agrave;n Quốc bị đ&aacute;nh ch&igrave;m hồi th&aacute;ng 3 vừa qua, c&oacute; thể thấy rất kh&oacute; đạt được c&aacute;i g&igrave; cụ thể th&ocirc;ng qua con đường n&agrave;y.</p>\r\n<p class=\"Normal\">Trong khi đ&oacute;, hiện Triều Ti&ecirc;n đ&atilde; l&agrave; một trong những quốc gia bị trừng phạt nhiều nhất thế giới li&ecirc;n quan đến vấn đề hạt nh&acirc;n, với li&ecirc;n tiếp c&aacute;c nghị quyết cứng rắn của Hội đồng Bảo an, c&ugrave;ng một số biện ph&aacute;p trừng phạt đơn phương của những nước kh&aacute;c. Nhưng ngay cả điều n&agrave;y cũng kh&ocirc;ng ngăn cản họ x&acirc;y dựng nh&agrave; m&aacute;y tinh chế uranium ở quy m&ocirc; c&ocirc;ng nghiệp v&agrave; mức độ hiện đại được đ&aacute;nh gi&aacute; l&agrave; \"kh&ocirc;ng thua k&eacute;m Mỹ\" trong những th&aacute;ng gần đ&acirc;y.</p>\r\n<p class=\"Normal\">Với những diễn biến hiện nay, c&aacute;c nh&agrave; b&igrave;nh luận H&agrave;n Quốc c&oacute; quan điểm khuynh tả c&oacute; thể cho rằng ch&iacute;nh phủ nước n&agrave;y đ&atilde; tự chuốc lấy thế kh&oacute; cho m&igrave;nh. Ch&iacute;nh phủ thuộc phe bảo thủ của Tổng thống Lee Myung-bak l&ecirc;n cầm quyền từ năm 2008 đ&atilde; từ bỏ con đường mềm mỏng v&agrave; ưu ti&ecirc;n ho&agrave; giải gần như v&ocirc; điều kiện của những người tiền nhiệm thuộc phe tự do. Thay v&agrave;o đ&oacute;, Seoul ki&ecirc;n quyết rằng viện trợ v&agrave; thương mại với miền bắc phải c&oacute; điều kiện, nhằm hướng tới tương lai phi hạt nh&acirc;n ho&aacute; b&aacute;n đảo Triều Ti&ecirc;n.</p>\r\n<p class=\"Normal\">B&igrave;nh Nhưỡng, tất nhi&ecirc;n, đ&atilde; thể hiện r&otilde; r&agrave;ng sự tức giận của họ đối với c&aacute;ch tiếp cận vấn đề mới của Seoul từ năm 2008 đến nay. Bất đồng n&agrave;y ch&iacute;nh l&agrave; nguy&ecirc;n nh&acirc;n s&acirc;u xa g&acirc;y ra thế kh&oacute; d&ugrave; kh&ocirc;ng mới nhưng gai g&oacute;c m&agrave; H&agrave;n Quốc đang phải đối mặt, trước c&aacute;c h&agrave;nh động được cho l&agrave; \"khi&ecirc;u kh&iacute;ch\" ng&agrave;y một dữ dội của miền bắc.</p>\r\n<p class=\"Normal\">Trong khi đ&oacute;, tại Triều Ti&ecirc;n đang trải qua giai đoạn kh&oacute; dự đo&aacute;n: chuyển giao quyền lực giữa Chủ tịch Kim Jong-il cho người con trai &uacute;t Kim Jong-un. Vụ chiến hạm Cheonan của H&agrave;n Quốc bị đ&aacute;nh ch&igrave;m hồi đầu năm v&agrave; vụ ph&aacute;o k&iacute;ch đảo Yeonpyeong h&ocirc;m qua được cho l&agrave; li&ecirc;n quan đến qu&aacute; tr&igrave;nh n&agrave;y. Nếu c&aacute;c h&agrave;nh động cứng rắn li&ecirc;n tiếp gần đ&acirc;y l&agrave; c&aacute;ch để củng cố vị thế của nh&agrave; l&atilde;nh đạo Triều Ti&ecirc;n tương lai, th&igrave; kh&ocirc;ng loại trừ khả năng những h&agrave;nh động mạnh tay của miền bắc chưa dừng ở đ&acirc;y v&agrave; điều n&agrave;y c&agrave;ng khiến ch&iacute;nh phủ H&agrave;n Quốc th&ecirc;m kh&oacute; khăn trong việc t&igrave;m ra c&aacute;ch đối ph&oacute; hợp l&yacute;.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/35/F0/Lee.jpg\" border=\"1\" alt=\"Tổng thống H&agrave;n Quốc Lee Myung-bak họp khẩn với c&aacute;c quan chức an ninh sau vụ giao tranh. Ảnh: AP\" width=\"399\" height=\"266\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tổng thống H&agrave;n Quốc Lee Myung-bak họp khẩn với c&aacute;c quan chức an ninh sau vụ giao tranh. Ảnh:&nbsp;<em>AP</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 class=\"SubTitle\">Đồng minh Mỹ thận trọng</h3>\r\n<p id=\"story_continues_1\" class=\"Normal\">Trong khi đ&oacute;, đồng minh quan trọng nhất của Seoul về quốc ph&ograve;ng l&agrave; Mỹ tỏ ra thận trọng. Nh&agrave; Trắng l&ecirc;n &aacute;n Triều Ti&ecirc;n bằng những lời lẽ mạnh mẽ sau vụ tấn c&ocirc;ng h&ocirc;m qua v&agrave; Tổng thống Barack Obama nhấn mạnh sẽ bảo vệ H&agrave;n Quốc. \"H&agrave;n Quốc l&agrave; đồng minh của ch&uacute;ng t&ocirc;i từ Chiến tranh Triều Ti&ecirc;n v&agrave; ch&uacute;ng t&ocirc;i khẳng định một c&aacute;ch mạnh mẽ cam kết bảo vệ H&agrave;n Quốc như một phần của li&ecirc;n minh đ&oacute;\", &ocirc;ng Obama tuy&ecirc;n bố với&nbsp;<em>ABC</em>.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, &ocirc;ng chủ Nh&agrave; Trắng đ&atilde; tr&aacute;nh kh&ocirc;ng đả động g&igrave; tới bất cứ h&agrave;nh động qu&acirc;n sự n&agrave;o m&agrave; Mỹ c&oacute; thể thực hiện. Đến nay cũng kh&ocirc;ng thấy c&oacute; ai trong ch&iacute;nh quyền Mỹ cho rằng vụ giao tranh giữa hai miền h&ocirc;m qua l&agrave; một h&agrave;nh động chiến tranh hay sự vi phạm thoả thuận ngừng bắn giữa Triều Ti&ecirc;n v&agrave; H&agrave;n Quốc năm 1953.</p>\r\n<p class=\"Normal\">Lầu Năm G&oacute;c cũng \"k&iacute;n kẽ\" kh&ocirc;ng k&eacute;m Nh&agrave; Trắng khi tuy&ecirc;n bố h&atilde;y c&ograve;n qu&aacute; sớm để thảo luận về c&aacute;c biện ph&aacute;p qu&acirc;n sự đ&aacute;p trả Triều Ti&ecirc;n. \"Ch&uacute;ng t&ocirc;i vẫn đang theo d&otilde;i t&igrave;nh h&igrave;nh v&agrave; trao đổi với c&aacute;c đồng minh của m&igrave;nh\", ph&aacute;t ng&ocirc;n vi&ecirc;n Lầu Năm G&oacute;c Dave Lapan cho biết.</p>\r\n<p class=\"Normal\">C&ograve;n ph&aacute;t ng&ocirc;n vi&ecirc;n Bộ Ngoại giao Mỹ Mark Toner n&ecirc;u r&otilde; Washington sẽ t&igrave;m kiếm \"một c&aacute;ch tiếp cận thận trọng\" vụ việc h&ocirc;m qua, trong sự phối hợp với c&aacute;c đồng minh khu vực như H&agrave;n Quốc v&agrave; Nhật Bản.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đ&igrave;nh Nguyễn</strong></p>', 8, 2, 3, 0, 1),
(328, 'Tại sao Triều Tiên khai hỏa?', 'Tai-Sao-Trieu-Tien-Khai-Hoa-', 'Quá trình chuyển giao quyền lực tại Triều Tiên và các cuộc tập trận của Hàn Quốc có thể là nguyên nhân châm ngòi vụ giao tranh bằng pháo hôm qua.', 'Yeo.jpg', NULL, 22, '<h1 class=\"Title\">Tại sao Triều Ti&ecirc;n khai hỏa?</h1>\r\n<h2 class=\"Lead\">Qu&aacute; tr&igrave;nh chuyển giao quyền lực tại Triều Ti&ecirc;n v&agrave; c&aacute;c cuộc tập trận của H&agrave;n Quốc c&oacute; thể l&agrave; nguy&ecirc;n nh&acirc;n ch&acirc;m ng&ograve;i vụ giao tranh bằng ph&aacute;o h&ocirc;m qua.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Yeo.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"263\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đảo Yeonpyeong của H&agrave;n Quốc đang bị Triều Ti&ecirc;n ph&aacute;o k&iacute;ch h&ocirc;m qua. Ảnh:&nbsp;<em>AFP</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 class=\"SubTitle\">Chuyển giao quyền lực</h3>\r\n<p class=\"Normal\">Một số nh&agrave; ph&acirc;n t&iacute;ch tin rằng những h&agrave;nh động được cho l&agrave; \"khi&ecirc;u kh&iacute;ch\" của Triều Ti&ecirc;n li&ecirc;n quan nhiều tới qu&aacute; tr&igrave;nh chuyển giao quyền lực đang diễn ra tại nước n&agrave;y. Nhiều nguồn tin nhận định Chủ tịch Kim Jong-il đang bắt đầu c&aacute;c bước chuẩn bị để chuyển giao quyền l&atilde;nh đạo đất nước cho con trai &uacute;t Kim Jong-un.</p>\r\n<p class=\"Normal\">Hồi th&aacute;ng 9 vừa qua, đảng cầm quyền Triều Ti&ecirc;n đ&atilde; tổ chức đại hội bất thường, trong đ&oacute; Kim Jong-un được trao c&aacute;c trọng tr&aacute;ch trong đảng v&agrave; Uỷ ban qu&acirc;n sự trung ương.&nbsp;<em>BBC</em>&nbsp;dẫn quan điểm của giới ph&acirc;n t&iacute;ch cho rằng, những vụ căng thẳng gần đ&acirc;y tr&ecirc;n b&aacute;n đảo như chiến hạm Cheonan của H&agrave;n Quốc bị đ&aacute;nh ch&igrave;m hồi th&aacute;ng 3 v&agrave; mới nhất l&agrave; vụ đọ ph&aacute;o giữa hai miền h&ocirc;m qua l&agrave; nhằm củng cố vị thế của nh&agrave; l&atilde;nh đạo tương lai Triều Ti&ecirc;n Kim Jong-un.</p>\r\n<p class=\"Normal\">Mark Fitzpatrick thuộc Viện nghi&ecirc;n cứu chiến lược quốc tế của Anh nhận định: \"Đ&oacute; dường như l&agrave; điều kh&ocirc;ng thể tr&aacute;nh khỏi khi c&aacute;c vụ tr&ecirc;n c&oacute; li&ecirc;n quan đến qu&aacute; tr&igrave;nh chuyển giao quyền lực. Kim Jong-un kh&ocirc;ng c&oacute; th&agrave;nh t&iacute;ch g&igrave; trong hồ sơ của m&igrave;nh. Nhưng nếu c&oacute; thể chắc chắn trở th&agrave;nh người l&atilde;nh đạo qu&acirc;n đội th&igrave; &ocirc;ng sẽ phải đạt được một số th&agrave;nh c&ocirc;ng về qu&acirc;n sự, điều đ&oacute; c&oacute; thể hỗ trợ cho qu&aacute; tr&igrave;nh tiếp nhận quyền lực của m&igrave;nh\".</p>\r\n<p class=\"Normal\">Trong khi đ&oacute;, cựu trợ l&yacute; ngoại trưởng Mỹ Christopher Hill, người từng l&atilde;nh đạo ph&aacute;i đo&agrave;n Mỹ trong cuộc đ&agrave;m ph&aacute;n 6 b&ecirc;n về hạt nh&acirc;n tr&ecirc;n b&aacute;n đảo Triều Ti&ecirc;n, cho rằng qu&acirc;n đội Triều Ti&ecirc;n đang ng&agrave;y c&agrave;ng gia tăng việc tự đưa ra mệnh lệnh. Cũng theo lời ch&iacute;nh trị gia n&agrave;y h&agrave;nh động mới nhất h&ocirc;m qua do qu&acirc;n đội đơn phương thực hiện m&agrave; kh&ocirc;ng c&oacute; \"vỏ bọc ch&iacute;nh trị\" n&agrave;o.</p>\r\n<p class=\"Normal\">Do đ&oacute; &ocirc;ng Hill cũng đặt ra c&acirc;u hỏi về ch&iacute;nh quyền d&acirc;n sự tại Triều Ti&ecirc;n. \"Triều Ti&ecirc;n đang trải qua qu&aacute; tr&igrave;nh chuyển giao nội bộ. C&oacute; một điều r&otilde; r&agrave;ng rằng qu&acirc;n đội Triều Ti&ecirc;n kh&ocirc;ng nhiệt t&igrave;nh lắm về đề xuất chuyển giao quyền lực của &ocirc;ng Kim Jong-il cho con trai. C&oacute; rất nhiều vấn đề ở đ&oacute; v&agrave; t&ocirc;i nghĩ ch&uacute;ng ta sẽ chứng kiến những h&agrave;nh động theo c&aacute;ch m&agrave; họ ứng xử với b&ecirc;n ngo&agrave;i\", nh&agrave; ngoại giao Mỹ n&oacute;i th&ecirc;m.</p>\r\n<h3 class=\"SubTitle\">Thế bế tắc về hạt nh&acirc;n</h3>\r\n<p class=\"Normal\">Triều Ti&ecirc;n trước đ&acirc;y từng t&igrave;m c&aacute;ch gia tăng căng thẳng như l&agrave; một c&aacute;ch để củng cố vị thế tr&ecirc;n b&agrave;n đ&agrave;m ph&aacute;n của m&igrave;nh, đặc biệt l&agrave; trong c&aacute;c vấn đề li&ecirc;n quan đến hạt nh&acirc;n. Trong khi đ&oacute; cuộc đ&agrave;m ph&aacute;n 6 b&ecirc;n gồm hai miền Triều Ti&ecirc;n, Mỹ, Trung Quốc, Nga v&agrave; Nhật Bản nhằm chấm dứt chương tr&igrave;nh hạt nh&acirc;n của B&igrave;nh Nhưỡng đ&atilde; l&acirc;m v&agrave;o bế tắc từ th&aacute;ng 4/2009.</p>\r\n<p class=\"Normal\">Năm 2005, Triều Ti&ecirc;n từng đồng &yacute; từ bỏ tham vọng hạt nh&acirc;n để đổi lấy viện trợ v&agrave; những nhượng bộ ch&iacute;nh trị. Nhưng thoả thuận n&agrave;y tan vỡ do bất đồng về vấn đề gi&aacute;m s&aacute;t v&agrave; đặc biệt l&agrave; nghi ngờ Triều Ti&ecirc;n c&oacute; chương tr&igrave;nh tinh chế uranium hay kh&ocirc;ng, b&ecirc;n cạnh chương tr&igrave;nh sản xuất plutonium của họ.</p>\r\n<p class=\"Normal\">Mỹ từ l&acirc;u nghi ngờ B&igrave;nh Nhưỡng tinh chế uranium, c&aacute;ch thức thứ hai họ c&oacute; thể chế tạo bom nguy&ecirc;n tử b&ecirc;n cạnh c&aacute;ch thức sản xuất bằng plutonium sẵn c&oacute;. Lo ngại n&agrave;y đ&atilde; được chứng minh l&agrave; c&oacute; thật v&agrave;o tuần trước, khi Triều Ti&ecirc;n h&eacute; lộ với chuy&ecirc;n gia hạt nh&acirc;n h&agrave;ng đầu của Mỹ về nh&agrave; m&aacute;y tinh chế uranium hiện đại c&oacute; quy m&ocirc; c&ocirc;ng nghiệp nhỏ. Giới chức Mỹ kinh ngạc khi biết Triều Ti&ecirc;n c&oacute; hơn 1.000 m&aacute;y ly t&acirc;m phục vụ tinh chế uranium, nhưng cho biết họ kh&ocirc;ng ngạc nhi&ecirc;n về sự tồn tại của những cỗ m&aacute;y n&agrave;y.</p>\r\n<p class=\"Normal\">Trong khi đ&oacute;, chỉ v&agrave;i giờ trước khi nổ ra cuộc đọ ph&aacute;o dữ dội giữa hai miền xung quanh h&ograve;n đảo Yeonpyeong của H&agrave;n Quốc h&ocirc;m qua, đặc ph&aacute;i vi&ecirc;n của Mỹ về b&aacute;n đảo Triều Ti&ecirc;n Stephen Bosworth đ&atilde; c&oacute; mặt tại H&agrave;n Quốc v&agrave; tuy&ecirc;n bố: \"Mỹ kh&ocirc;ng thể chờ đợi việc nối lại c&aacute;c v&ograve;ng đ&agrave;m ph&aacute;n trong khi c&oacute; chương tr&igrave;nh hạt nh&acirc;n đang hoạt động tại Triều Ti&ecirc;n\".</p>\r\n<p class=\"Normal\">Do đ&oacute; việc Triều Ti&ecirc;n bất ngờ tấn c&ocirc;ng dữ dội h&ograve;n đảo của miền nam c&oacute; thể l&agrave; c&aacute;ch thức nhằm nối lại v&ograve;ng đ&agrave;m ph&aacute;n hạt nh&acirc;n đang bế tắc, theo c&aacute;ch thức m&agrave; họ muốn. Nhưng giới ph&acirc;n t&iacute;ch cho rằng đ&acirc;y l&agrave; c&aacute;ch thức đ&atilde; cũ, theo lời gi&aacute;o sư ng&agrave;nh quan hệ quốc tế Zhu Feng của Đại học Bắc Kinh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/35/A4/Quan.jpg\" border=\"1\" alt=\"Một cuộc diễn tập qu&acirc;n sự của H&agrave;n Quốc. Ảnh: AFP\" width=\"450\" height=\"261\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một cuộc diễn tập qu&acirc;n sự của H&agrave;n Quốc tr&ecirc;n đảo Yeonpyeong. Ảnh:&nbsp;<em>AFP</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 class=\"SubTitle\">C&aacute;c cuộc diễn tập qu&acirc;n sự</h3>\r\n<p class=\"Normal\">Qu&acirc;n đội hai miền Triều Ti&ecirc;n từng giao tranh một số lần trước đ&acirc;y kể từ sau cuộc chiến tranh giai đoạn 1950-1953, đặc biệt l&agrave; tại khu vực căng thẳng bi&ecirc;n giới tr&ecirc;n biển ở ph&iacute;a t&acirc;y. H&agrave;n Quốc c&ocirc;ng nhận Đường giới hạn ph&iacute;a bắc (NLL) được qu&acirc;n Li&ecirc;n Hợp Quốc do Mỹ l&atilde;nh đạo vạch ra v&agrave;o cuối cuộc Chiến tranh Triều Ti&ecirc;n năm 1953, để ph&acirc;n định bi&ecirc;n giới tr&ecirc;n biển giữa hai miền.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n đến nay Triều Ti&ecirc;n vẫn kh&ocirc;ng c&ocirc;ng nhận đường ranh giới l&atilde;nh hải n&agrave;y. H&agrave;n Quốc thường tổ chức diễn tập qu&acirc;n sự tại khu vực biển tranh chấp, h&agrave;nh động khiến Triều Ti&ecirc;n tức giận v&agrave; coi l&agrave; sự khi&ecirc;u kh&iacute;ch. Từ thứ hai tuần n&agrave;y, một ng&agrave;y trước cuộc giao tranh, Seoul cũng bắt đầu cuộc tập trận thường ni&ecirc;n tại đ&acirc;y.</p>\r\n<p class=\"Normal\">Giới chức H&agrave;n Quốc cho biết, ngay trước khi Triều Ti&ecirc;n bắt đầu n&atilde; ph&aacute;o h&ograve;n đảo Yeonpyeong của họ, B&igrave;nh Nhưỡng đ&atilde; gửi tới một số th&ocirc;ng điệp phản đối cuộc tập trận của miền nam v&agrave; y&ecirc;u cầu dừng hoạt động n&agrave;y nhưng bất th&agrave;nh. H&ograve;n đảo n&agrave;y chỉ nằm c&aacute;ch đường bi&ecirc;n giới NLL khoảng 3 km về ph&iacute;a nam v&agrave; c&aacute;ch thủ đ&ocirc; Seoul của H&agrave;n Quốc 120 km về ph&iacute;a t&acirc;y. Như vậy nhiều khả năng cuộc tập trận của H&agrave;n Quốc đ&atilde; ch&acirc;m ng&ograve;i cho cuộc giao tranh chớp nho&aacute;ng giữa hai miền h&ocirc;m qua.</p>\r\n<h3 class=\"SubTitle\">Bất đồng về viện trợ</h3>\r\n<p class=\"Normal\">Quan điểm cứng rắn của Tổng thống H&agrave;n Quốc Lee Myung-bak về viện trợ kinh tế cho miền bắc đ&atilde; dẫn tới những căng thẳng ng&agrave;y c&agrave;ng s&acirc;u sắc giữa hai miền, sau khi đ&atilde; c&oacute; dấu hiệu ho&agrave; giải dưới thời ch&iacute;nh quyền tiền nhiệm. Kể từ khi &ocirc;ng Lee l&ecirc;n cầm quyền năm 2008, d&ograve;ng viện trợ cho miền bắc đ&atilde; bị thắt chặt tới mức nhỏ giọt, v&igrave; &ocirc;ng giữ quan điểm viện trợ phải gắn liền với tiến triển trong qu&aacute; tr&igrave;nh phi hạt nh&acirc;n ho&aacute; b&aacute;n đảo.</p>\r\n<p class=\"Normal\">Trong khi đ&oacute;, Triều Ti&ecirc;n phải dựa nhiều v&agrave;o nguồn viện trợ từ b&ecirc;n ngo&agrave;i để đ&aacute;p ứng c&aacute;c nhu cầu trong nước. Dười thời tiền nhiệm của &ocirc;ng Lee l&agrave; cựu tổng thống Roh Moo-hyun, miền bắc thường xuy&ecirc;n nhận được c&aacute;c g&oacute;i viện trợ lớn qua bi&ecirc;n giới tr&ecirc;n bộ. V&igrave; vậy sự thay đổi ch&iacute;nh s&aacute;ch viện trợ của ch&iacute;nh quyền mới tại H&agrave;n Quốc từ năm 2008 đến nay khiến Triều Ti&ecirc;n gặp nhiều kh&oacute; khăn.</p>\r\n<p class=\"Normal\">Hơn nữa những năm gần đ&acirc;y Triều Ti&ecirc;n hứng chịu nhiều đợt lũ lụt tồi tệ ph&aacute; huỷ m&ugrave;a m&agrave;ng, trong bối cảnh Li&ecirc;n Hợp Quốc v&agrave; Mỹ tiếp tục thắt chặt cấm vận c&agrave;ng khiến nền kinh tế của Triều Ti&ecirc;n th&ecirc;m lao đao. Do vậy giới ph&acirc;n t&iacute;ch nhận định vụ tấn c&ocirc;ng của miền bắc c&oacute; thể xuất ph&aacute;t từ sự kh&ocirc;ng h&agrave;i l&ograve;ng của họ với những g&igrave; nhận được từ b&ecirc;n ngo&agrave;i.</p>\r\n<h3 class=\"SubTitle\">H&agrave;nh động g&acirc;y ch&uacute; &yacute;</h3>\r\n<p class=\"Normal\">Những tuần gần đ&acirc;y, Triều Ti&ecirc;n n&ecirc;u quan điểm cần nối lại c&aacute;c v&ograve;ng đ&agrave;m ph&aacute;n đa phương về hạt nh&acirc;n, trong khi họ cũng muốn nhận được viện trợ kinh tế cũng như sự ch&uacute; &yacute; nhiều hơn nữa từ ph&iacute;a Mỹ. Trước mỗi thời điểm đặc biệt như vậy, B&igrave;nh Nhưỡng từng c&oacute; những h&agrave;nh động g&acirc;y sốc như ph&oacute;ng t&ecirc;n lửa v&agrave; thử hạt nh&acirc;n để củng cố vị thế của m&igrave;nh tr&ecirc;n trường quốc tế v&agrave; nhằm để b&ecirc;n ngo&agrave;i ch&uacute; &yacute; hơn đến vấn đề tr&ecirc;n b&aacute;n đảo Triều Ti&ecirc;n.</p>\r\n<p class=\"Normal\">Do vậy h&agrave;nh động qu&acirc;n sự của B&igrave;nh Nhưỡng h&ocirc;m qua cũng được cho l&agrave; mang t&iacute;nh chất g&acirc;y ch&uacute; &yacute;. Tuy nhi&ecirc;n, trước mọi ph&acirc;n t&iacute;ch, h&atilde;ng th&ocirc;ng tấn Triều Ti&ecirc;n&nbsp;<em>KCNA</em>h&ocirc;m nay ch&iacute;nh thức c&oacute; tuy&ecirc;n bố về sự kiện tr&ecirc;n, trong đ&oacute; c&aacute;o buộc H&agrave;n Quốc đ&atilde; n&atilde; đạn ph&aacute;o v&agrave;o v&ugrave;ng biển của họ trước, g&acirc;y k&iacute;ch động khiến họ nổ s&uacute;ng v&agrave; dẫn đến giao tranh tại khu vực gần đảo Yeonpyeong.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đ&igrave;nh Nguyễn</strong></p>', 8, 2, 86, 0, 1),
(329, 'Triều Tiên tấn công: Không hoàn toàn bất ngờ', 'Trieu-Tien-Tan-Cong:-Khong-Hoan-Toan-Bat-Ngo', 'Giới theo dõi tình hình bán đảo Triều Tiên vẫn dự đoán miền bắc có thể sắp \"động thủ\", nhưng việc họ bất ngờ bắn phá một hòn đảo của Hàn Quốc một cách dữ dội như hôm nay vẫn khiến nhiều người sốc.', 'Han (1).jpg', NULL, 22, '<h1 class=\"Title\">Triều Ti&ecirc;n tấn c&ocirc;ng: Kh&ocirc;ng ho&agrave;n to&agrave;n bất ngờ</h1>\r\n<h2 class=\"Lead\">Giới theo d&otilde;i t&igrave;nh h&igrave;nh b&aacute;n đảo Triều Ti&ecirc;n vẫn dự đo&aacute;n miền bắc c&oacute; thể sắp \"động thủ\", nhưng việc họ bất ngờ bắn ph&aacute; một h&ograve;n đảo của H&agrave;n Quốc một c&aacute;ch dữ dội như h&ocirc;m nay vẫn khiến nhiều người sốc.</h2>\r\n<p class=\"Normal\">B&igrave;nh Nhưỡng h&ocirc;m nay tấn c&ocirc;ng miền nam mạnh nhất kể từ cuộc chiến tranh Triều Ti&ecirc;n hơn nửa thế kỷ trước, khi bắn ph&aacute; đảo Yeonpyeong của miền nam l&agrave;m hơn 60 ng&ocirc;i nh&agrave; bị thi&ecirc;u ch&aacute;y v&agrave; d&acirc;n thường bỏ chạy trong hoảng loạn. Ngay lập tức H&agrave;n Quốc cho m&aacute;y bay chiến đấu F-16 xuất k&iacute;ch nhưng chưa c&oacute; động th&aacute;i tấn c&ocirc;ng n&agrave;o, trong khi bộ binh của nước n&agrave;y đ&atilde; bắn trả.</p>\r\n<p class=\"Normal\">Vụ giao tranh giữa hai miền xung quanh h&ograve;n đảo Yeonpyeong, nằm c&aacute;ch đường bi&ecirc;n giới Li&ecirc;n Triều tr&ecirc;n biển khoảng 3 km về ph&iacute;a nam v&agrave; c&aacute;ch thủ đ&ocirc; Seoul của H&agrave;n Quốc 120 km về ph&iacute;a t&acirc;y, diễn ra trong khoảng một tiếng sau đ&oacute; đột ngột im tiếng s&uacute;ng. Tuy nhi&ecirc;n dư &acirc;m của n&oacute; chắc chắn sẽ t&aacute;c động mạnh đến bầu kh&ocirc;ng kh&iacute; tr&ecirc;n b&aacute;n đảo Triều Ti&ecirc;n.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/35/50/Han.jpg\" border=\"1\" alt=\"Những cuộc diễn tập qu&acirc;n sự của H&agrave;n Quốc như thế n&agrave;y khiến Triều Ti&ecirc;n tức giận. Ảnh: AFP\" width=\"480\" height=\"258\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những cuộc diễn tập qu&acirc;n sự của H&agrave;n Quốc như thế n&agrave;y khiến Triều Ti&ecirc;n tức giận. Ảnh:&nbsp;<em>AFP</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<h3 class=\"SubTitle\">\"H&agrave;nh động khi&ecirc;u kh&iacute;ch\"</h3>\r\n<p class=\"Normal\">Trong khi Tổng thống H&agrave;n Quốc Lee Myung-bak c&oacute; cuộc họp khẩn với hội đồng an ninh v&agrave; qu&acirc;n đội c&oacute; những triển khai trong tư thế sẵn s&agrave;ng chiến đấu, th&igrave; Ng&acirc;n h&agrave;ng Trung ương H&agrave;n Quốc cũng chuẩn bị nh&oacute;m họp khẩn để đ&aacute;nh gi&aacute; ảnh hưởng của vụ tấn c&ocirc;ng tới thị trường. Đồng won của H&agrave;n Quốc đ&atilde; phản ứng ti&ecirc;u cực khi mất gi&aacute; do lo ngại kinh tế sụt giảm v&igrave; căng thẳng leo thang.</p>\r\n<p class=\"Normal\">Vụ tấn c&ocirc;ng của miền bắc xảy ra ngay sau khi đặc ph&aacute;i vi&ecirc;n của Mỹ Stephen Bosworth c&oacute; mặt tr&ecirc;n b&aacute;n đảo Triều Ti&ecirc;n v&agrave; B&igrave;nh Nhưỡng cũng vừa h&eacute; lộ về nh&agrave; m&aacute;y tinh chế uranium của m&igrave;nh. Một chuy&ecirc;n gia hạt nh&acirc;n Mỹ l&agrave; Siegfried Hecker c&oacute; dịp hiếm hoi được tới thăm khu phức hợp nguy&ecirc;n tử Yongbyon của Triều Ti&ecirc;n x&aacute;c nhận nước n&agrave;y đ&atilde; c&oacute; một nh&agrave; m&aacute;y tinh chế uranium hiện đại kh&ocirc;ng k&eacute;m Mỹ ở quy m&ocirc; c&ocirc;ng nghiệp nhỏ.</p>\r\n<p class=\"Normal\">Tinh chế uranium sẽ cho ph&eacute;p Triều Ti&ecirc;n c&oacute; c&aacute;ch thứ hai để chế tạo bom nguy&ecirc;n tử, sau c&aacute;ch thức đ&atilde; biết l&agrave; chương tr&igrave;nh dựa tr&ecirc;n qu&aacute; tr&igrave;nh sản xuất plutonium. Nếu tinh chế ở mức độ thấp, uranium c&oacute; thể được sử dụng l&agrave;m nhi&ecirc;n liệu trong c&aacute;c l&ograve; phản ứng ph&aacute;t điện hạt nh&acirc;n. Nhưng nếu được tinh chế ở mức độ cao hơn n&oacute; c&oacute; thể được sử dụng trong chế tạo bom nguy&ecirc;n tử.</p>\r\n<p class=\"Normal\">Trong khi đ&oacute;, B&igrave;nh Nhưỡng gần đ&acirc;y b&agrave;y tỏ mong muốn nối lại v&ograve;ng đ&agrave;m ph&aacute;n 6 b&ecirc;n về hạt nh&acirc;n vốn bế tắc trong hai năm qua. Những diễn biến n&agrave;y khiến &iacute;t người nhận định Triều Ti&ecirc;n lại bất ngờ \"ra đ&ograve;n\" với miền nam một c&aacute;ch \"mạnh tay\" như vậy. Nh&igrave;n chung giới quan s&aacute;t đều coi h&agrave;nh động của miền bắc l&agrave; sự khi&ecirc;u kh&iacute;ch một c&aacute;ch c&oacute; chủ đ&iacute;ch.</p>\r\n<p class=\"Normal\">Gi&aacute;o sư ng&agrave;nh quan hệ quốc tế Đại học Bắc Kinh Zhu Feng thốt l&ecirc;n: \"Thật kh&ocirc;ng thể tin nổi. Tin tức h&ocirc;m nay chứng tỏ Triều Ti&ecirc;n đ&atilde; bắn ph&aacute; đảo của H&agrave;n Quốc d&ugrave; kh&ocirc;ng hề c&oacute; sự khi&ecirc;u kh&iacute;ch n&agrave;o. Đ&acirc;y ch&iacute;nh l&agrave; sự khi&ecirc;u kh&iacute;ch một c&aacute;ch liều lĩnh. Họ muốn tạo ra c&uacute; nổ lớn v&agrave; đưa cuộc đ&agrave;m ph&aacute;n quay lại theo c&aacute;ch họ muốn. Đ&oacute; l&agrave; một chi&ecirc;u thức cũ r&iacute;ch\".</p>\r\n<p class=\"Normal\">Gi&aacute;o sư Kim Yong-Hyun thuộc Đại học Dongguk H&agrave;n Quốc cũng nhấn mạnh với<em>AFP</em>: \"Đ&acirc;y l&agrave; h&agrave;nh động khi&ecirc;u kh&iacute;ch c&oacute; chủ đ&iacute;ch nhằm gia tăng căng thẳng bi&ecirc;n giới hai miền. Miền bắc đ&atilde; c&oacute; một loạt động th&aacute;i nhưng kh&ocirc;ng c&oacute; phản ứng g&igrave; từ miền nam v&agrave; Mỹ. Giờ đ&acirc;y họ đang sử dụng chiến lược \'b&ecirc;n miệng hố chiến tranh\', nhằm buộc Seoul v&agrave; Washington phải c&oacute; h&agrave;nh động v&agrave; đồng &yacute; đối thoại\". &Ocirc;ng cũng ph&acirc;n t&iacute;ch rằng Triều Ti&ecirc;n sẽ cố sử dụng vũ lực để th&uacute;c đẩy đo&agrave;n kết người d&acirc;n trong nước trong qu&aacute; tr&igrave;nh chuyển giao quyền l&atilde;nh đạo.</p>\r\n<h3 class=\"SubTitle\">Kh&ocirc;ng ho&agrave;n to&agrave;n bất ngờ</h3>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, vụ tấn c&ocirc;ng h&ocirc;m nay của B&igrave;nh Nhưỡng kh&ocirc;ng phải l&agrave; h&agrave;nh động qu&aacute; bất ngờ. Tr&ecirc;n thực tế khu vực quanh h&ograve;n đảo Yeonpyeong từng l&agrave; hiện trường của 3 vụ giao tranh giữa hải qu&acirc;n hai miền v&agrave;o c&aacute;c năm 1999, 2002 v&agrave; 2009 khiến một số thuỷ thủ thiệt mạng. Th&aacute;ng 3 vừa qua, chiến hạm Cheonan của H&agrave;n Quốc cũng bị một ngư l&ocirc;i được cho l&agrave; mang dấu hiệu của Triều Ti&ecirc;n đ&aacute;nh ch&igrave;m khiến 46 thuỷ thủ thiệt mạng. Seoul buộc tội B&igrave;nh Nhưỡng đứng sau vụ tấn c&ocirc;ng, nhưng miền bắc b&aacute;c bỏ tr&aacute;ch nhiệm.</p>\r\n<p class=\"Normal\">Sự kiện t&agrave;u Cheonan đ&atilde; đ&aacute;nh dấu sự leo thang căng thẳng trở lại tr&ecirc;n b&aacute;n đảo Triều Ti&ecirc;n. Trong khi đ&oacute; tại miền bắc năm nay cũng chứng kiến những chuyển biến ch&iacute;nh trị mang t&iacute;nh lịch sử, khi &yacute; định chuyển giao quyền lực của Chủ tịch Kim Jong-il cho con trai &uacute;t chưa đầy 30 tuổi của m&igrave;nh, Kim Jong-un, ng&agrave;y c&agrave;ng trở n&ecirc;n r&otilde; r&agrave;ng.</p>\r\n<p class=\"Normal\">Hơn nữa, về mặt l&yacute; thuyết th&igrave; H&agrave;n Quốc v&agrave; Triều Ti&ecirc;n vẫn đang trong t&igrave;nh trạng chiến tranh với nhau. Nguy&ecirc;n nh&acirc;n v&igrave; cuộc chiến giai đoạn 1950-1953 giữa hai miền chỉ được kết th&uacute;c bằng một lệnh ngừng bắn chứ kh&ocirc;ng phải l&agrave; một ho&agrave; ước ch&iacute;nh thức.</p>\r\n<p class=\"Normal\">Trong khi đ&oacute;, vụ tấn c&ocirc;ng xảy ra khi H&agrave;n Quốc đang diễn tập qu&acirc;n sự trong khu vực, động th&aacute;i lu&ocirc;n khiến Triều Ti&ecirc;n tức giận. Theo lời một quan chức trong Hội đồng tham mưu trưởng li&ecirc;n qu&acirc;n H&agrave;n Quốc, sớm nay B&igrave;nh Nhưỡng đ&atilde; gửi một th&ocirc;ng điệp y&ecirc;u cầu miền nam dừng cuộc tập trận n&agrave;y, nhưng H&agrave;n Quốc vẫn tiếp tục. B&igrave;nh Nhưỡng doạ sẽ c&oacute; h&agrave;nh động trả đũa c&aacute;c cuộc tập trận của miền nam gần khu vực căng thẳng tr&ecirc;n biển, n&ecirc;n họ ph&aacute;o k&iacute;ch h&ograve;n đảo h&ocirc;m nay chỉ l&agrave; việc hiện thực ho&aacute; lời đe doạ từng đưa ra rất nhiều lần của m&igrave;nh.</p>\r\n<h3 class=\"SubTitle\">Kh&oacute; nổ ra chiến tranh</h3>\r\n<p class=\"Normal\">Diễn biến qu&acirc;n sự h&ocirc;m nay được coi l&agrave; nghi&ecirc;m trọng, nhưng kh&ocirc;ng c&oacute; nhiều khả năng sẽ ch&acirc;m ng&ograve;i cho cuộc chiến thực sự giữa hai miền Triều Ti&ecirc;n. Theo một nguồn tin từ văn ph&ograve;ng tổng thống H&agrave;n Quốc, &ocirc;ng Lee Myung-bak ra lệnh đ&aacute;p trả miền bắc một c&aacute;ch nghi&ecirc;m khắc, nhưng cũng k&ecirc;u gọi c&aacute;c quan chức dưới quyền phải đảm bảo \"t&igrave;nh h&igrave;nh kh&ocirc;ng leo thang\".</p>\r\n<p class=\"Normal\">Đồng minh ch&iacute;nh trị v&agrave; kinh tế quan trọng nhất của Triều Ti&ecirc;n l&agrave; Trung Quốc cũng k&ecirc;u gọi miền bắc b&igrave;nh tĩnh. Ph&aacute;t ng&ocirc;n vi&ecirc;n Bộ Ngoại giao Trung Quốc Hồng Lỗi cho biết Bắc Kinh \"b&agrave;y tỏ mối lo ngại về t&igrave;nh h&igrave;nh n&oacute;i tr&ecirc;n\", đồng thời k&ecirc;u gọi hai b&ecirc;n \"c&oacute; nhiều việc l&agrave;m hơn nữa để g&oacute;p phần cho ho&agrave; b&igrave;nh v&agrave; ổn định tr&ecirc;n b&aacute;n đảo\".</p>\r\n<p class=\"Normal\">Một quốc gia cũng c&oacute; ảnh hưởng lớn đến Triều Ti&ecirc;n l&agrave; Nga c&ugrave;ng chung quan điểm với Trung Quốc, khi b&agrave;y tỏ lo ngại về t&igrave;nh h&igrave;nh tr&ecirc;n b&aacute;n đảo sau vụ tấn c&ocirc;ng của B&igrave;nh Nhưỡng.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Đ&igrave;nh Nguyễn</strong></p>', 8, 2, 1160, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(352, 'Mở bán đợt 2 giá gốc căn hộ cao cấp Kim Tâm Hải', 'Mo-Ban-Dot-2-Gia-Goc-Can-Ho-Cao-Cap-Kim-Tam-Hai', 'Công ty cổ phẩn Kim Tâm Hải mở bán đợt II gồm 84 căn hộ thuộc chung cư cao cấp Kim Tâm Hải, TP HCM với giá từ 14,4 triệu đồng một m2.', 'hinh_1_300x379.jpg', NULL, 22, '<h1 class=\"Title\">Mở b&aacute;n đợt 2 gi&aacute; gốc căn hộ cao cấp Kim T&acirc;m Hải</h1>\r\n<h2 class=\"Lead\">C&ocirc;ng ty cổ phẩn Kim T&acirc;m Hải mở b&aacute;n đợt II gồm 84 căn hộ thuộc chung cư cao cấp Kim T&acirc;m Hải, TP HCM với gi&aacute; từ 14,4 triệu đồng một m2.</h2>\r\n<p class=\"Normal\">C&aacute;c căn hộ b&aacute;n trong đợt n&agrave;y diện t&iacute;ch từ 65 đến 116 m2 với 2 hoặc 3 ph&ograve;ng ngủ, đầy đủ tiện &iacute;ch, được b&aacute;n với gi&aacute; từ 14,4 triệu đồng một m2 (đ&atilde; c&oacute; nh&agrave; mẫu). Nh&acirc;n dịp mừng năm mới, chủ đầu tư thực hiện ch&iacute;nh s&aacute;ch ưu đ&atilde;i chiết khấu từ 1 đến 8% c&ugrave;ng bộ qu&agrave; tặng bao gồm TV 42 inch, tủ lạnh, m&aacute;y lạnh, m&aacute;y giặt cho kh&aacute;ch h&agrave;ng mua căn hộ. Phương thức thanh to&aacute;n gồm 9 đợt, đặt cọc 20 triệu đồng, k&yacute; hợp đồng thanh to&aacute;n 30%, ng&acirc;n h&agrave;ng Eximbank hỗ trợ vay 70%.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4F/A5/hinh_1_300x379.jpg\" border=\"1\" alt=\"\" width=\"300\" height=\"379\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chung cư cao cấp Kim T&acirc;m Hải tọa lạc tại 27 Trường Chinh (trục đường xuy&ecirc;n &Aacute; rộng 60 m), phường T&acirc;n Thới Nhất, quận 12, TP HCM. C&ocirc;ng tr&igrave;nh gồm 16 tầng với 168 căn hộ c&ugrave;ng nhiều tiện &iacute;ch: 1,5 tầng hầm để xe, 2 tầng thương mại (si&ecirc;u thị, khu ăn uống-shopping, thể dục thẩm mỹ&hellip;.).</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4F/A5/Hinh_5.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"325\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ngo&agrave;i ra, khu chung cư cao cấp n&agrave;y c&ograve;n được trang bị hệ thống điện dự ph&ograve;ng, 4 thang m&aacute;y v&agrave; 2 thang bộ tho&aacute;t hiểm, hệ thống camera, bảo vệ an ninh 24/24 trong v&agrave; ngo&agrave;i t&ograve;a nh&agrave;, hệ thống ph&ograve;ng ch&aacute;y chữa ch&aacute;y tự động...</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Kim T&acirc;m Hải</em>)</p>', 10, 3, 2502, 0, 1),
(353, 'Khung giá đất chi tiết năm 2011của Hà Nội', 'Khung-Gia-Dat-Chi-Tiet-Nam-2011Cua-Ha-Noi', 'Giá đất Hà Nội năm 2011 nằm trong mức tối thiểu là 2,34 triệu đồng mỗi m2 thuộc đường 72 qua phường Dương Nội, Hà Đông. Các phố Hàng Ngang, Hàng Đào có giá cao nhất là 81 triệu đồng như năm 2010.', 'dat-1.jpg', NULL, 22, '<h1 class=\"Title\">Khung gi&aacute; đất chi tiết năm 2011của H&agrave; Nội</h1>\r\n<h2 class=\"Lead\">Gi&aacute; đất H&agrave; Nội năm 2011 nằm trong mức tối thiểu l&agrave; 2,34 triệu đồng mỗi m2 thuộc đường 72 qua phường Dương Nội, H&agrave; Đ&ocirc;ng. C&aacute;c phố H&agrave;ng Ngang, H&agrave;ng Đ&agrave;o c&oacute; gi&aacute; cao nhất l&agrave; 81 triệu đồng như năm 2010.<br /><span style=\"font-size: 10px; font-weight: normal;\">UBND th&agrave;nh phố H&agrave; Nội vừa k&yacute; quyết định ban h&agrave;nh gi&aacute; c&aacute;c loại đất tr&ecirc;n địa b&agrave;n th&agrave;nh phố H&agrave; Nội năm 2011. Theo đ&oacute;, từ ng&agrave;y 1/1/2011, khung gi&aacute; mới sẽ l&agrave; cơ sở t&iacute;nh thuế đối với việc sử dụng đất, thuế thu nhập trong việc giao dịch chuyển nhượng quyền sử dụng đất.</span></h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4D/7B/dat-1.jpg\" border=\"1\" alt=\"Ảnh: Ho&agrave;ng H&agrave;\" width=\"430\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Mức gi&aacute; cao nhất trong năm 2011 vẫn giữ nguy&ecirc;n như năm 2010. Ảnh:<em>&nbsp;Ho&agrave;ng H&agrave;.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo đ&oacute;, gi&aacute; đất tại c&aacute;c trục đường ch&iacute;nh hướng t&acirc;m v&agrave;o Th&agrave;nh phố được điều chỉnh theo hướng giảm dần từ trung t&acirc;m H&agrave; Nội trở ra. Như vậy, năm 2011 gi&aacute; đất tại một số đường, phố sẽ cao hơn năm 2010, một số đường phố sẽ được giữ nguy&ecirc;n. Mức cao nhất thuộc về c&aacute;c phố H&agrave;ng Ngang, H&agrave;ng Đ&agrave;o với 81 triệu đồng mỗi m2. Gi&aacute; đất nằm trong mức tối thiểu l&agrave; 2.340.000 đồng mỗi m2 (đường 72 qua phường Dương Nội, quận H&agrave; Đ&ocirc;ng).</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"303\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><a href=\"http://vnexpress.net/Files/Customize/Khung-gia-dat/2011/2011.rar\">Click v&agrave;o đ&acirc;y để xem khung gi&aacute; đất năm 2011</a></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Những khu vực chưa được x&aacute;c định trong bảng gi&aacute;, hoặc c&aacute;c trường hợp mới ph&aacute;t sinh do x&acirc;y dựng đường, phố, khu đ&ocirc; thị mới, UBND th&agrave;nh phố giao Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường c&ugrave;ng c&aacute;c sở, ng&agrave;nh li&ecirc;n quan lập phương &aacute;n gi&aacute;, b&aacute;o c&aacute;o để th&agrave;nh phố quyết định.</p>\r\n<p class=\"Normal\">Đối với đất chưa sử dụng (đất chưa x&aacute;c định mục đ&iacute;ch sử dụng), khi cần c&oacute; gi&aacute; để t&iacute;nh tiền bồi thường th&igrave; căn cứ v&agrave;o gi&aacute; của loại đất liền kề c&oacute; mức gi&aacute; cao nhất. Khi đất chưa sử dụng được đưa v&agrave;o sử dụng th&igrave; căn cứ v&agrave;o gi&aacute; đất c&ugrave;ng loại, c&ugrave;ng mục đ&iacute;ch sử dụng để định mức gi&aacute; cụ thể</p>\r\n<p class=\"Normal\">Th&agrave;nh phố đề ra mục ti&ecirc;u khung gi&aacute; đất năm 2011 phải s&aacute;t thị trường. Trong trường hợp chưa s&aacute;t gi&aacute; thị trường th&igrave; Sở T&agrave;i ch&iacute;nh lập phương &aacute;n tr&igrave;nh th&agrave;nh phố để điều chỉnh trong giới hạn cho ph&eacute;p cao hơn hoặc thấp hơn kh&ocirc;ng qu&aacute; 20% mức gi&aacute; của khu đất c&ugrave;ng vị tr&iacute;. C&aacute;c trường hợp điều chỉnh cao hơn 20% mức gi&aacute; khu đất c&ugrave;ng vị tr&iacute; th&igrave; li&ecirc;n ng&agrave;nh phải lập phương &aacute;n b&aacute;o c&aacute;o UBND Th&agrave;nh phố để xin &yacute; kiến Thường trực Hội đồng nh&acirc;n d&acirc;n Th&agrave;nh phố trước khi quyết định.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 3002, 0, 1),
(354, 'Những sự kiện bất động sản tháng 12', 'Nhung-Su-Kien-Bat-Dong-San-Thang-12', 'Tháng cuối năm, hàng loạt dự án đồng thời ra mắt, xả hàng, mà nổi bật là dự án 4 tỷ USD ở Quảng Nam. Sắp tới, Việt Nam có thể có khu đô thị đại học, Quỹ tiết kiệm nhà ở.', 'xem-hang-1.jpg', NULL, 22, '<h1 class=\"Title\">Những sự kiện bất động sản th&aacute;ng 12</h1>\r\n<h2 class=\"Lead\">Th&aacute;ng cuối năm, h&agrave;ng loạt dự &aacute;n đồng thời ra mắt, xả h&agrave;ng, m&agrave; nổi bật l&agrave; dự &aacute;n 4 tỷ USD ở Quảng Nam. Sắp tới, Việt Nam c&oacute; thể c&oacute; khu đ&ocirc; thị đại học, Quỹ tiết kiệm nh&agrave; ở.<br /><span style=\"font-size: 10px; font-weight: normal;\"><strong><span style=\"color: #2f2f2f;\">Dự &aacute;n 4 tỷ USD ở Quảng Nam</span></strong></span></h2>\r\n<p class=\"Normal\">Ngay sau khi khu du lịch sinh th&aacute;i B&atilde;i biển Rồng c&oacute; vốn đầu tư 4,15 tỷ USD bị thu hồi giấy ph&eacute;p đầu tư, tỉnh Quảng Nam lập tức c&oacute; một dự &aacute;n khổng lồ kh&aacute;c. Khu nghỉ dưỡng cao cấp phức hợp Nam Hội An do C&ocirc;ng ty li&ecirc;n doanh đầu tư Genting VinaCapital l&agrave;m chủ đầu tư c&oacute; số vốn 4 tỷ USD l&agrave; dự &aacute;n thay thế. Dự kiến, chủ đầu tư sẽ x&acirc;y c&aacute;c hạng mục kh&aacute;ch sạn, khu resort, biệt thự v&agrave; khu vui chơi c&oacute; thưởng d&agrave;nh cho người nước ngo&agrave;i. Ri&ecirc;ng hoạt động vui chơi c&oacute; thưởng d&agrave;nh cho người nước ngo&agrave;i được bố tr&iacute; trong khu kh&aacute;ch sạn 5 sao.</p>\r\n<p class=\"Normal\"><strong><span style=\"color: #2f2f2f;\">Nhiều dự &aacute;n lớn đồng loạt xả h&agrave;ng ở H&agrave; Nội</span></strong></p>\r\n<p class=\"Normal\">Trong ng&agrave;y 17/12, dự &aacute;n Indochina Plaza Hanoi (IPH) đ&atilde; mở b&aacute;n căn hộ với gi&aacute; gốc 2.600 USD - 3.000 USD mỗi m2. Tiếp đến, Tập đo&agrave;n Nam Cường đ&atilde; mở b&aacute;n h&agrave;ng đợt 4, khoảng 200 căn hộ CT7A (T&ograve;a A), dự &aacute;n L&ecirc; Văn Lương Residentials thuộc khu đ&ocirc; thị mới Dương Nội, Q.H&agrave; Đ&ocirc;ng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/45/C0/xem-hang-1.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"360\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nhiều dự &aacute;n đồng loạt xả h&agrave;ng cuối năm. Ảnh:&nbsp;<em>Ho&agrave;ng Lan.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trước đ&oacute;, Dolphin Plaza ch&agrave;o b&aacute;n c&aacute;c căn hộ thuộc 4 khối th&aacute;p với mức gi&aacute; trung b&igrave;nh l&agrave; 37 triệu đồng mỗi m2. Ng&agrave;y 9/12, chủ đầu tư Khu đ&ocirc; thị mới Nam Ho&agrave;ng Đồng 1 (Lạng Sơn) cũng tung ra 600 l&ocirc; đất nền c&oacute; gi&aacute; từ 5,4 triệu đồng đến 9,4 triệu đồng mỗi m2 tại H&agrave; Nội. Dự &aacute;n 2 tỷ USD Ho&agrave;ng Đồng Lạng Sơn cũng ch&agrave;o b&aacute;n 48 căn liền kề c&oacute; diện t&iacute;ch từ 370 m2 đến 540 m2 đầu ti&ecirc;n. Chủ đầu tư dự &aacute;n StarCity L&ecirc; Văn Lương, H&agrave; Nội cũng đưa ra nhiều h&igrave;nh thức khuyến m&atilde;i như tặng g&oacute;i thiết bị bao gồm tủ lạnh, m&aacute;y giặt, l&ograve; vi s&oacute;ng trị gi&aacute; 50 triệu đồng cho tất cả kh&aacute;ch h&agrave;ng đăng k&yacute; mua căn hộ.</p>\r\n<p class=\"Normal\"><strong><span style=\"color: #2f2f2f;\">H&agrave; Nội sắp c&oacute; khu đ&ocirc; thị đại học</span></strong></p>\r\n<p class=\"Normal\">Theo quy hoạch chung Thủ đ&ocirc; đến 2030 v&agrave; tầm nh&igrave;n đến năm 2050, dự kiến H&agrave; Nội sẽ c&oacute; 8 khu đ&ocirc; thị đại học. Một số trường đại học cao đẳng trong nội th&agrave;nh sẽ phải di dời đến c&aacute;c khu đ&ocirc; thị n&agrave;y, dựa tr&ecirc;n c&aacute;c ti&ecirc;u ch&iacute; đất đai, ng&agrave;nh nghề đ&agrave;o tạo, năm th&agrave;nh lập v&agrave; x&acirc;y dựng.</p>\r\n<p class=\"Normal\">Khu đ&ocirc; thị đại học của H&agrave; Nội dự kiến ph&aacute;t triển theo nhiều m&ocirc; h&igrave;nh: Đ&ocirc; thị đại học, khu đại học v&agrave; cụm trường đại học, cao đẳng. Theo dự kiến, chỉ ti&ecirc;u t&iacute;nh to&aacute;n quỹ đất trong khu đại học tập trung, gồm: Đất học tập khoảng 20-30 m2 mỗi sinh vi&ecirc;n, đất k&yacute; t&uacute;c x&aacute; 10-15 m2 mỗi sinh vi&ecirc;n, đất thể dục thể thao khoảng 10 m2 mỗi sinh vi&ecirc;n, đất c&ocirc;ng cộng khoảng 5-10 m2 mỗi sinh vi&ecirc;n. Theo định hướng chung, ngo&agrave;i c&aacute;c trường đại học, khu đ&ocirc; thị c&ograve;n c&oacute; bệnh viện, nh&agrave; in, thư viện, ph&ograve;ng th&iacute; nghiệm, vườn ươm doanh nghiệp...</p>\r\n<p class=\"Normal\"><span style=\"color: #3f3f3f;\"><strong>Đại gia Indochina Land</strong>&nbsp;<strong>gia nhập thị trường nh&agrave; b&igrave;nh d&acirc;n</strong></span></p>\r\n<p class=\"Normal\">Trong năm tới, Indochina Land sẽ tập trung ph&aacute;t triển c&aacute;c dự &aacute;n nh&agrave; ở trung cấp tại H&agrave; Nội v&agrave; TP HCM thay v&igrave; chỉ đầu tư bất động sản cao cấp như trước đ&acirc;y. Đ&oacute; l&agrave; quan điểm chiến lược đầu tư của Indochina Land v&agrave;o thị trường Việt Nam vừa được c&ocirc;ng bố v&agrave;o hồi giữa th&aacute;ng 12.</p>\r\n<p class=\"Normal\">Indochina Land cam kết vẫn đầu tư l&acirc;u d&agrave;i v&agrave; chủ yếu tập trung v&agrave;o những khu vực thị trường mới nổi, thị trường c&ograve;n ở mức trung. Tuy nhi&ecirc;n, kh&ocirc;ng v&igrave; thế m&agrave; Indochina bỏ qua ph&acirc;n mảng cao cấp m&agrave; l&agrave; lĩnh vực đầu tư truyền thống của Indochina.</p>\r\n<p class=\"Normal\">Indochina Land nhấn mạnh, mặc d&ugrave; trong thời gian qua thị trường bất động sản Việt Nam trầm lắng, nhưng đ&acirc;y chỉ l&agrave; ngắn hạn. Thị trường sẽ s&ocirc;i động hơn trong 1-2 năm tới khi nền kinh tế phục hồi v&agrave; tăng trường ổn định. Do đ&oacute;, chiến lược đầu tư của Indochina Land v&agrave;o thị trường Việt Nam l&agrave; đa dạng h&oacute;a nhiều mảng, từ ph&acirc;n kh&uacute;c trung b&igrave;nh cho đến cao cấp.</p>\r\n<p class=\"Normal\"><strong><span style=\"color: #3f3f3f;\">Người d&acirc;n sẽ phải đ&oacute;ng Quỹ tiết kiệm nh&agrave; ở</span></strong></p>\r\n<p class=\"Normal\">Bộ X&acirc;y dựng vừa đề xuất Ch&iacute;nh phủ cho ph&eacute;p lập&nbsp;Quỹ tiết kiệm nh&agrave; ở&nbsp;với mục đ&iacute;ch giải b&agrave;i to&aacute;n về chỗ ở cho người lao động. Theo đ&oacute;, quỹ sẽ được th&agrave;nh lập với mục ti&ecirc;u cho người d&acirc;n vay tiền mua nh&agrave; ở với l&atilde;i suất thấp k&egrave;m những quy định về đối tượng v&agrave; điều kiện vay.</p>\r\n<p class=\"Normal\">Quỹ tiết kiệm nh&agrave; ở sẽ được x&acirc;y dựng giống như h&igrave;nh thức bảo hiểm x&atilde; hội với mức đ&oacute;ng g&oacute;p dự kiến từ 1- 2% tổng tiền lương hằng th&aacute;ng của người lao động. Những người nghỉ hưu sẽ được r&uacute;t về cộng với l&atilde;i suất mang t&iacute;nh hỗ trợ trượt gi&aacute;. Số tiền n&agrave;y sẽ cho những người thu nhập thấp vay mua nh&agrave; với l&atilde;i suất chỉ bằng 20-25% so với vay của ng&acirc;n h&agrave;ng thương mại.</p>\r\n<p class=\"Normal\">Mặc d&ugrave; đ&aacute;nh gi&aacute; cao mục ti&ecirc;u tốt đẹp trong việc giải quyết nhu cầu bức x&uacute;c về nh&agrave; ở của Bộ X&acirc;y dựng, song nhiều chuy&ecirc;n gia lo ngại Quỹ tiết kiệm nh&agrave; ở sẽ kh&oacute; đi v&agrave;o thực tế.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ho&agrave;ng Lan</strong></p>', 10, 3, 2714, 0, 1),
(434, 'Chai Coca-Cola khổng lồ tại Diamond Plaza', 'Chai-Coca-Cola-Khong-Lo-Tai-Diamond-Plaza', 'Ngay trong ngày đầu khai trương, chai Coca-Cola khổng lồ mang thông điệp chia sẻ “Dành Coca-Cola đầu năm cho người bạn yêu thương” tại Diamond Plaza, TP HCM đã thu hút sự chú ý của rất nhiều bạn trẻ.', '1 (1)_1.JPG', NULL, 22, '<h1 class=\"Title\">Chai Coca-Cola khổng lồ tại Diamond Plaza</h1>\r\n<h2 class=\"Lead\">Ngay trong ng&agrave;y đầu khai trương, chai Coca-Cola khổng lồ mang th&ocirc;ng điệp chia sẻ &ldquo;D&agrave;nh Coca-Cola đầu năm cho người bạn y&ecirc;u thương&rdquo; tại Diamond Plaza, TP HCM đ&atilde; thu h&uacute;t sự ch&uacute; &yacute; của rất nhiều bạn trẻ.</h2>\r\n<p class=\"Normal\">Năm nay, h&agrave;nh tr&igrave;nh du xu&acirc;n của c&aacute;c bạn trẻ dường như kh&ocirc;ng thể bỏ qua điểm vui xu&acirc;n độc đ&aacute;o của Coca-Cola tại Diamond Plaza. Nằm trong khu&ocirc;n khổ chương tr&igrave;nh &ldquo;D&agrave;nh Coca-Cola đầu năm cho người bạn y&ecirc;u thương&rdquo;, sau năm đầu ti&ecirc;n th&agrave;nh c&ocirc;ng v&agrave; thu h&uacute;t được rất nhiều bạn trẻ tham gia, chương tr&igrave;nh đ&atilde; mang đến những gi&acirc;y ph&uacute;t &ldquo;offline&rdquo; lẫn &ldquo;online&rdquo; th&uacute; vị từ cuộc thi &ldquo;ảo&rdquo; tr&ecirc;n<a class=\"Normal\" href=\"http://www.coca-cola.zing.vn/\">http://www.coca-cola.zing.vn/</a>&nbsp;đến s&acirc;n chơi &ldquo;thật&rdquo; tại H&agrave; Nội (Vincom City Tower), Đ&agrave; Nẵng (Vinh Trung Plaza) v&agrave; TP HCM (Diamond Plaza).</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/3c/1.JPG\" border=\"1\" alt=\"Ngay trong ng&agrave;y đầu khai trương, chai Coca-Cola khổng lồ mang th&ocirc;ng điệp chia sẻ &ldquo;D&agrave;nh Coca-Cola đầu năm cho người bạn y&ecirc;u thương&rdquo; tại Diamond Plaza đ&atilde; thu h&uacute;t sự ch&uacute; &yacute; của rất nhiều bạn trẻ.\" width=\"300\" height=\"428\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ngay trong ng&agrave;y đầu khai trương, chai Coca-Cola khổng lồ mang th&ocirc;ng điệp chia sẻ &ldquo;D&agrave;nh Coca-Cola đầu năm cho người bạn y&ecirc;u thương&rdquo; tại Diamond Plaza đ&atilde; thu h&uacute;t sự ch&uacute; &yacute; của rất nhiều bạn trẻ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/3c/2.JPG\" border=\"1\" alt=\"Sự xuất hiện của Phương Vy Idol với c&acirc;u chuyện &ldquo;D&agrave;nh Coca-Cola đầu năm cho Ba&rdquo; nhận được rất nhiều sự đồng cảm v&agrave; tạo &ldquo;động lực&rdquo; khiến c&aacute;c bạn trẻ mạnh dạn chia sẻ y&ecirc;u thương đến gia đ&igrave;nh, bạn b&egrave;.\" width=\"300\" height=\"450\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sự xuất hiện của Phương Vy Idol với c&acirc;u chuyện &ldquo;D&agrave;nh Coca-Cola đầu năm cho ba&rdquo; nhận được rất nhiều sự đồng cảm v&agrave; tạo &ldquo;động lực&rdquo; khiến c&aacute;c bạn trẻ mạnh dạn chia sẻ y&ecirc;u thương đến gia đ&igrave;nh, bạn b&egrave;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/3c/3.JPG\" border=\"1\" alt=\"Gi&acirc;y ph&uacute;t &ldquo;tranh thủ&rdquo; với &ldquo;Phim trường Coca-Cola mini&rdquo; của Phương Vy v&agrave; 03 bạn đoạt giải chung cuộc &ldquo;Mở Coca-Cola đầu năm &ndash; Khai xu&acirc;n hứng khởi&rdquo; tr&ecirc;n www.coca-cola.zing.vn.\" width=\"300\" height=\"450\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Gi&acirc;y ph&uacute;t &ldquo;tranh thủ&rdquo; với &ldquo;Phim trường Coca-Cola mini&rdquo; của Phương Vy v&agrave; 3 bạn đoạt giải chung cuộc &ldquo;Mở Coca-Cola đầu năm &ndash; Khai xu&acirc;n hứng khởi&rdquo; tr&ecirc;n&nbsp;<a class=\"Image\" href=\"http://www.coca-cola.zing.vn/\">www.coca-cola.zing.vn</a>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/3c/4.JPG\" border=\"1\" alt=\"Kh&ocirc;ng hẹn m&agrave; gặp, sự xuất hiện bất ngờ của c&aacute;c anh ch&agrave;ng La Thăng khiến kh&ocirc;ng kh&iacute; Xu&acirc;n tại Coca-Cola Diamond Plaza trở n&ecirc;n &ldquo;hot&rdquo; hơn bao giờ hết.\" width=\"400\" height=\"267\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Kh&ocirc;ng hẹn m&agrave; gặp, sự xuất hiện bất ngờ của c&aacute;c anh ch&agrave;ng La Thăng khiến kh&ocirc;ng kh&iacute; Xu&acirc;n tại Coca-Cola Diamond Plaza trở n&ecirc;n \"n&oacute;ng\" hơn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/3c/5.JPG\" border=\"1\" alt=\"C&oacute; nhiều c&aacute;ch để chia sẻ y&ecirc;u thương. Say m&ecirc; trổ t&agrave;i s&aacute;ng tạo với hoạt động &ldquo;T&ocirc; tượng Coca-Cola&rdquo; ch&iacute;nh l&agrave; một trong những c&aacute;ch để thể hiện &yacute; nghĩa chia sẻ đối với những bạn trẻ n&agrave;y.\" width=\"400\" height=\"267\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hoạt động &ldquo;T&ocirc; tượng Coca-Cola&rdquo; ch&iacute;nh l&agrave; một trong những c&aacute;ch m&agrave; c&aacute;c bạn trẻ thể hiện để chia sẻ y&ecirc;u thương.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/3c/6_(2).JPG\" border=\"1\" alt=\"Rất nhiều &ldquo;t&aacute;c phẩm&rdquo;&hellip; đẹp lạ được ra đời từ &yacute; nghĩa &ldquo;D&agrave;nh Coca-Cola đầu năm cho người bạn y&ecirc;u thương&rdquo;.\" width=\"400\" height=\"267\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Rất nhiều &ldquo;t&aacute;c phẩm&rdquo;, đẹp lạ được ra đời từ &yacute; nghĩa &ldquo;D&agrave;nh Coca-Cola đầu năm cho người bạn y&ecirc;u thương&rdquo;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/3c/7_(2).JPG\" border=\"1\" alt=\"Những lời ch&uacute;c tốt đẹp d&agrave;nh cho người th&acirc;n, bạn b&egrave;, những mong ước cho một năm mới su&ocirc;n sẻ, hạnh ph&uacute;c hay đơn giản l&agrave; lời tự nhắc nhở d&agrave;nh cho bản th&acirc;n kh&ocirc;ng bao giờ &ldquo;cũ&rdquo;.\" width=\"400\" height=\"267\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những lời ch&uacute;c tốt đẹp d&agrave;nh cho người th&acirc;n, bạn b&egrave;, những mong ước cho một năm mới su&ocirc;n sẻ, hạnh ph&uacute;c hay đơn giản l&agrave; lời tự nhắc nhở d&agrave;nh cho bản th&acirc;n kh&ocirc;ng bao giờ &ldquo;cũ&rdquo;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/3c/8_(2).JPG\" border=\"1\" alt=\"&ldquo;C&acirc;y nguyện ước&rdquo; Coca-Cola ng&agrave;y th&ecirc;m trĩu nặng với những lời ch&uacute;c người người gửi đến nhau nh&acirc;n dịp Xu&acirc;n mới T&acirc;n M&atilde;o.\" width=\"400\" height=\"267\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">&ldquo;C&acirc;y nguyện ước&rdquo; Coca-Cola ng&agrave;y th&ecirc;m trĩu nặng với những lời ch&uacute;c nh&acirc;n dịp xu&acirc;n mới T&acirc;n M&atilde;o.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5e/3c/9_(2).JPG\" border=\"1\" alt=\"C&ugrave;ng vui, c&ugrave;ng chơi, c&ugrave;ng cầu ch&uacute;c một năm mới may l&agrave;nh đến với người ngh&egrave;o bằng c&aacute;ch thả &ldquo;đồng xu nguyện ước&rdquo; v&agrave;o chai Coca-Cola khổng lồ ch&iacute;nh l&agrave; c&aacute;ch để mọi người chia sẻ y&ecirc;u thương, chia sẻ niềm vui trước thềm Xu&acirc;n mới.\" width=\"300\" height=\"450\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&ugrave;ng vui, c&ugrave;ng chơi, c&ugrave;ng cầu ch&uacute;c một năm mới may l&agrave;nh đến với người ngh&egrave;o bằng c&aacute;ch thả &ldquo;đồng xu nguyện ước&rdquo; v&agrave;o chai Coca-Cola khổng lồ ch&iacute;nh l&agrave; c&aacute;ch để mọi người chia sẻ y&ecirc;u thương, chia sẻ niềm vui trước thềm Xu&acirc;n mới.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Coca Cola</em>)</p>', 14, 3, 2150, 0, 1),
(435, 'Quà xuân Kinh Đô đến với học sinh vùng lũ', 'Qua-Xuan-Kinh-Do-Den-Voi-Hoc-Sinh-Vung-Lu', 'Chia sẻ với những thiếu thốn của bà con vùng lũ và các em học sinh, các hộ gia đình nghèo, các trẻ em có hoàn cảnh thiếu may mắn, Công ty Kinh Đô đã trực tiếp thăm và tặng quà Tết với tổng trị giá 700 triệu đồng.', 'Hinh-1-400x266_1.jpg', NULL, 22, '<h1 class=\"Title\">Qu&agrave; xu&acirc;n Kinh Đ&ocirc; đến với học sinh v&ugrave;ng lũ</h1>\r\n<h2 class=\"Lead\">Chia sẻ với những thiếu thốn của b&agrave; con v&ugrave;ng lũ v&agrave; c&aacute;c em học sinh, c&aacute;c hộ gia đ&igrave;nh ngh&egrave;o, c&aacute;c trẻ em c&oacute; ho&agrave;n cảnh thiếu may mắn, C&ocirc;ng ty Kinh Đ&ocirc; đ&atilde; trực tiếp thăm v&agrave; tặng qu&agrave; Tết với tổng trị gi&aacute; 700 triệu đồng.</h2>\r\n<p class=\"Normal\">Trong đ&oacute;, 2.000 phần qu&agrave; xu&acirc;n trị gi&aacute; 400 triệu đồng bao gồm b&aacute;nh kẹo Tết v&agrave; tiền l&igrave; x&igrave; gửi đến 2.000 em học sinh v&ugrave;ng lũ tại 2 tỉnh Quảng B&igrave;nh v&agrave; H&agrave; Tĩnh do ch&iacute;nh c&aacute;n bộ c&ocirc;ng nh&acirc;n vi&ecirc;n Kinh Đ&ocirc; v&agrave; c&aacute;c c&ocirc;ng ty th&agrave;nh vi&ecirc;n đ&oacute;ng g&oacute;p.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5d/8d/Hinh-1_400x271.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"271\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">&Ocirc;ng Nguyễn Quốc Nam - Ph&oacute; tổng gi&aacute;m đốc Tập đo&agrave;n Kinh Đ&ocirc; trao qu&agrave; Tết cho học sinh v&ugrave;ng lũ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trong hai ng&agrave;y 18 v&agrave; 19/1 vừa qua, Kinh Đ&ocirc; tổ chức thăm v&agrave; trực tiếp trao những phần qu&agrave; &yacute; nghĩa tr&ecirc;n cho c&aacute;c em học sinh tại c&aacute;c trường: tiểu học Sơn Lễ huyện Hương Kh&ecirc;, tiểu học Bồng Lĩnh v&agrave; tiểu học Hương Minh huyện Vũ Quang, tỉnh H&agrave; Tĩnh. Tại Quảng B&igrave;nh, Kinh Đ&ocirc; đ&atilde; thăm v&agrave; trao qu&agrave; Tết cho c&aacute;c em học sinh tại c&aacute;c trường tiểu học: V&otilde; Ninh 1, V&otilde; Ninh 2, T&acirc;n Ninh v&agrave; An Ninh 2 thuộc huyện Quảng Ninh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5d/8d/Hinh-2_400x264.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"264\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c em học sinh v&ugrave;ng lũ tỉnh Quảng B&igrave;nh nhận qu&agrave; v&agrave; l&igrave; x&igrave; Tết do CBCNV Tập đo&agrave;n Kinh Đ&ocirc; đ&oacute;ng g&oacute;p v&agrave; trao tặng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chương tr&igrave;nh thăm v&agrave; tặng qu&agrave; Tết của Kinh Đ&ocirc; được tổ chức từ ng&agrave;y 19/1 (16/12 &acirc;m lịch) với tổng trị gi&aacute; 700 triệu đồng. Cụ thể, 1.500 phần qu&agrave; Tết Kinh Đ&ocirc; sẽ được trao đến những gia đ&igrave;nh kh&oacute; khăn, c&aacute;c bệnh nhi ung thư đặc biệt l&agrave; trẻ em, học sinh ngh&egrave;o ph&iacute;a Bắc, 2.000 phần qu&agrave; xu&acirc;n gửi đến c&aacute;c em học sinh v&ugrave;ng lũ miền Trung, 2.500 phần qu&agrave; đến c&aacute;c hộ gia đ&igrave;nh ngh&egrave;o tr&ecirc;n địa b&agrave;n TP HCM, c&aacute;c tỉnh v&ugrave;ng s&acirc;u v&ugrave;ng xa. Những phần qu&agrave; Tết ngọt ng&agrave;o, được trao tận tay đến những ho&agrave;n cảnh kh&oacute; khăn, gửi gắm cả sự quan t&acirc;m, chia sẻ c&ugrave;ng lời ch&uacute;c Tết của C&ocirc;ng ty Kinh Đ&ocirc; v&agrave; tập thể c&aacute;n bộ c&ocirc;ng nh&acirc;n vi&ecirc;n với mong muốn mọi người th&ecirc;m ấm l&ograve;ng v&agrave; c&ugrave;ng đ&oacute;n những ng&agrave;y Tết vui tươi, &yacute; nghĩa.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Kinh Đ&ocirc;</em>)</p>', 14, 3, 3011, 0, 1),
(436, 'Việt Hà Beer trình làng hai sản phẩm mới', 'Viet-Ha-Beer-Trinh-Lang-Hai-San-Pham-Moi', 'Với phương thức nấu bia truyền thống kết hợp công nghệ lên men hiện đại, Việt Hà Beer ra mắt bia lon 330 ml dành cho gia đình và bia Gold 5 lít dành cho các doanh nghiệp.', '300.jpg', NULL, 22, '<h1 class=\"Title\">Việt H&agrave; Beer tr&igrave;nh l&agrave;ng hai sản phẩm mới</h1>\r\n<h2 class=\"Lead\">Với phương thức nấu bia truyền thống kết hợp c&ocirc;ng nghệ l&ecirc;n men hiện đại, Việt H&agrave; Beer ra mắt bia lon 330 ml d&agrave;nh cho gia đ&igrave;nh v&agrave; bia Gold 5 l&iacute;t d&agrave;nh cho c&aacute;c doanh nghiệp.</h2>\r\n<p class=\"Normal\">Tiếp nối th&agrave;nh c&ocirc;ng của d&ograve;ng bia hơi truyền thống tr&ecirc;n thị trường những năm qua, giữa năm 2010, Việt H&agrave; Beer đ&atilde; cho ra đời sản phẩm mới: bia tươi chai Pet với điểm nhấn l&agrave; c&ocirc;ng nghệ m&agrave;ng lọc bia tươi: Bia hơi thanh tr&ugrave;ng CFS v&agrave; vỏ chai Pet sử dụng một lần. Sự kết hợp của hai yếu tố bia tươi v&agrave; đ&oacute;ng chai, vừa giữ nguy&ecirc;n hương vị bia tươi dịu m&aacute;t, vừa tiện lợi cho người sử dụng, đ&atilde; đem đến th&agrave;nh c&ocirc;ng ngo&agrave;i mong đợi cho d&ograve;ng sản phẩm mới n&agrave;y, khẳng định hướng đổi mới đ&uacute;ng đắn của Việt H&agrave; Beer.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5d/e5/300.jpg\" border=\"1\" alt=\"\" width=\"305\" height=\"402\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">V&agrave;o dịp cuối năm n&agrave;y, Việt H&agrave; Beer tiếp tục tr&igrave;nh l&agrave;ng hai sản phẩm mới, đ&oacute; l&agrave;: bia lon 330 ml v&agrave; bia Gold 5 l&iacute;t. Với phương thức nấu bia truyền thống kết hợp c&ocirc;ng nghệ l&ecirc;n men hiện đại, 2 loại bia n&agrave;y ph&ugrave; hợp để l&agrave;m qu&agrave; tặng v&agrave;o dịp Tết.</p>\r\n<p class=\"Normal\">Song song với việc cải tiến d&acirc;y chuyền sản xuất v&agrave; s&aacute;ng tạo c&aacute;c loại bia mới, sau nhiều năm trung th&agrave;nh với m&agrave;u v&agrave;ng tươi đặc trưng, Việt H&agrave; đ&atilde; chọn gam m&agrave;u xanh cho bia lon 330ml. Sự đổi mới to&agrave;n diện trong sản phẩm cả về chủng loại bia lon, lẫn m&agrave;u sắc xanh tươi trẻ, đ&atilde; cho thấy sự ph&aacute; c&aacute;ch t&aacute;o bạo về thương hiệu, h&igrave;nh ảnh của Việt H&agrave; trong việc hướng tới đối tượng rộng hơn, thị trường lớn hơn.</p>\r\n<p class=\"Normal\">Với việc đầu tư d&acirc;y chuyền c&ocirc;ng suất 150 triệu l&iacute;t một năm v&agrave; một hệ thống ph&acirc;n phối rộng khắp bao gồm: hơn 2.000 điểm tại H&agrave; Nội v&agrave; tr&ecirc;n 4.000 điểm tại c&aacute;c tỉnh th&agrave;nh trong cả nước, c&aacute;c sản phẩm của Việt H&agrave; đảm bảo đ&aacute;p ứng nhu cầu ti&ecirc;u d&ugrave;ng của người Việt mọi l&uacute;c, mọi nơi.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/5d/e5/DSC06120_(2).JPG\" border=\"1\" alt=\"\" width=\"420\" height=\"315\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Chưa dừng lại ở đ&oacute;, Việt H&agrave; tung ra chương tr&igrave;nh khuyến m&atilde;i &ldquo;Giật nắp tr&uacute;ng v&agrave;ng&rdquo;. Theo đ&oacute;, khi mua lon bia 330 ml, kh&aacute;ch h&agrave;ng c&oacute; cơ hội tr&uacute;ng ngay một chỉ v&agrave;ng 4 số 9 gắn b&ecirc;n dưới nắp lon, thay v&igrave; phải gửi phiếu bốc thăm tr&uacute;ng thưởng hay nắp lon c&oacute; ghi giải thưởng về nh&agrave; sản xuất v&agrave; đợi lấy giải thưởng như c&aacute;c chương tr&igrave;nh khuyến m&atilde;i truyền thống. Đ&acirc;y cũng được coi l&agrave; điểm mới trong chiến lược b&aacute;n h&agrave;ng của thương hiệu n&agrave;y.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Việt H&agrave; Beer</em>)</p>', 14, 3, 562, 0, 1),
(571, '10 nhà bán lẻ hàng đầu thế giới 2010', '10-Nha-Ban-Le-Hang-Dau-The-Gioi-2010', 'Hãng nghiên cứu danh tiếng Deloitte vừa công bố danh sách 250 nhà bán lẻ hàng đầu thế giới năm 2010, trong đó Wal-Mart vẫn giữ vị trí số một.', 'Walmart-1.jpg', NULL, 22, '<h1 class=\"Title\">10 nh&agrave; b&aacute;n lẻ h&agrave;ng đầu thế giới 2010</h1>\r\n<h2 class=\"Lead\">H&atilde;ng nghi&ecirc;n cứu danh tiếng Deloitte vừa c&ocirc;ng bố danh s&aacute;ch 250 nh&agrave; b&aacute;n lẻ h&agrave;ng đầu thế giới năm 2010, trong đ&oacute; Wal-Mart vẫn giữ vị tr&iacute; số một.</h2>\r\n<p class=\"Normal\">Nguồn dữ liệu để Deloitte đưa ra bảng xếp hạng n&agrave;y l&agrave; b&aacute;o c&aacute;o t&agrave;i ch&iacute;nh của c&aacute;c h&atilde;ng trong t&agrave;i kh&oacute;a 2009 (kết th&uacute;c v&agrave;o th&aacute;ng 6/2010). Dưới đ&acirc;y l&agrave; danh s&aacute;ch 10 nh&agrave; b&aacute;n lẻ h&agrave;ng đầu.</p>\r\n<h3 class=\"SubTitle\">1. Wal-Mart</h3>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/44/Walmart-1.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trụ sở: Mỹ</p>\r\n<p class=\"Normal\">Số quốc gia đang hoạt động: 16</p>\r\n<p class=\"Normal\">Doanh thu trong t&agrave;i kh&oacute;a 2009: 404 tỷ USD</p>\r\n<p class=\"Normal\">Tăng trưởng lợi nhuận năm 2009: 3,6 %</p>\r\n<p class=\"Normal\">Wal-Mart l&agrave; nh&agrave; b&aacute;n lẻ h&agrave;ng đầu Bắc Mỹ cũng như tr&ecirc;n to&agrave;n thế giới. Tuy vậy, hoạt động của c&ocirc;ng ty n&agrave;y tại 15 quốc gia kh&aacute;c ngo&agrave;i Mỹ lại chỉ tạo ra 25% doanh thu hằng năm.</p>\r\n<h3 class=\"SubTitle\">2. Carrefour</h3>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/44/Carrefour.jpg\" border=\"1\" alt=\"Carrefour\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trụ sở: Ph&aacute;p</p>\r\n<p class=\"Normal\">Số quốc gia đang hoạt động: 36</p>\r\n<p class=\"Normal\">Doanh thu năm 2009: 122 tỷ USD</p>\r\n<p class=\"Normal\">Tăng trưởng lợi nhuận năm 2009: 0,5 %</p>\r\n<p class=\"Normal\">Hoạt động tr&ecirc;n 36 quốc gia v&agrave; l&agrave; nh&agrave; b&aacute;n lẻ h&agrave;ng đầu ch&acirc;u &Acirc;u, nhưng doanh thu năm 2009 của Carrefour bị sụt giảm.</p>\r\n<h3 class=\"SubTitle\">3. Metro AG</h3>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/44/Metro.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trụ sở: Đức</p>\r\n<p class=\"Normal\">Số quốc gia đang hoạt động: 33</p>\r\n<p class=\"Normal\">Doanh thu năm 2009: 91 tỷ USD</p>\r\n<p class=\"Normal\">Tăng trưởng lợi nhuận năm 2009: 0,8 %</p>\r\n<p class=\"Normal\">Đại gia đến từ nước Đức l&agrave; một trong số 2 c&ocirc;ng ty của Đức lọt v&agrave;o top 10, nhưng đồng thời cũng l&agrave; 1 trong 4 c&ocirc;ng ty g&oacute;p mặt trong danh s&aacute;ch n&agrave;y bị giảm doanh thu năm 2009.</p>\r\n<h3 class=\"SubTitle\">4. Tesco</h3>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/44/Tesco.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trụ sở: Anh</p>\r\n<p class=\"Normal\">Số quốc gia đang hoạt động: 13</p>\r\n<p class=\"Normal\">Doanh thu năm 2009: 90,43 tỷ USD</p>\r\n<p class=\"Normal\">Tăng trưởng lợi nhuận năm 2009: 4,1 %</p>\r\n<p class=\"Normal\">Giống Schwarz v&agrave; Aldi, nh&agrave; b&aacute;n lẻ của Anh n&agrave;y cũng c&oacute; doanh thu tăng trưởng mạnh với 4,1% năm 2009. Trong khi đ&oacute;, c&aacute;c c&ocirc;ng ty thuộc nh&oacute;m 250 chỉ c&oacute; mức tăng trưởng trung b&igrave;nh l&agrave; 1,3% .</p>\r\n<h3 class=\"SubTitle\">5. Schwarz Unternehmens Treuhand KG</h3>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/44/Schwarz.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trụ sở: Đức</p>\r\n<p class=\"Normal\">Số quốc gia đang hoạt động: 25</p>\r\n<p class=\"Normal\">Doanh thu năm 2009: 77,22 tỷ USD</p>\r\n<p class=\"Normal\">Schwarz l&agrave; 1 trong số 3 c&ocirc;ng ty trong top 10 c&oacute; tốc độ ph&aacute;t triển vượt tốc độ trung b&igrave;nh của top 250, d&ugrave; chỉ một ch&uacute;t, với 1,4%.</p>\r\n<h3 class=\"SubTitle\">6. The Kroger</h3>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/44/Kroger.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trụ sở: Mỹ</p>\r\n<p class=\"Normal\">Số quốc gia đang hoạt động: 1</p>\r\n<p class=\"Normal\">Doanh thu năm 2009: 76,73 tỷ USD</p>\r\n<p class=\"Normal\">Tăng trưởng lợi nhuận năm 2009: 0,1 %</p>\r\n<p class=\"Normal\">C&aacute;c nh&agrave; b&aacute;n lẻ thực phẩm đ&atilde; phục hồi sau cuộc khủng hoảng v&agrave; Kroger cũng kh&ocirc;ng phải l&agrave; ngoại lệ. Ph&acirc;n kh&uacute;c n&agrave;y đ&atilde; tăng cả về số lượng c&ocirc;ng ty trong top 250 lẫn doanh thu. Năm 2009, c&aacute;c nh&agrave; b&aacute;n lẻ thực phẩm chiếm hơn một nửa số c&ocirc;ng ty trong danh s&aacute;ch v&agrave; hơn hai phần ba tổng doanh thu.</p>\r\n<h3 class=\"SubTitle\">7. Costco Wholesale</h3>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/44/Cosco.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trụ sở: Mỹ</p>\r\n<p class=\"Normal\">Số quốc gia đang hoạt động: 9</p>\r\n<p class=\"Normal\">Doanh thu năm 2009: 71,42 tỷ USD</p>\r\n<p class=\"Normal\">Tăng trưởng lợi nhuận năm 2009: 1,5 %</p>\r\n<p class=\"Normal\">Costco đ&atilde; thăng hạng trong danh s&aacute;ch top 10 năm nay v&agrave; cũng leo l&ecirc;n vị tr&iacute; thứ 3 trong top 10 tại khu vực Bắc Mỹ.</p>\r\n<h3 class=\"SubTitle\">8. Aldi Einkauf GmbH &amp; Co.</h3>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/44/Aldi.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trụ sở: Đức</p>\r\n<p class=\"Normal\">Số quốc gia đang hoạt động: 18</p>\r\n<p class=\"Normal\">Doanh thu năm 2009: 67,70 tỷ USD</p>\r\n<p class=\"Normal\">Aldi l&agrave; một trong ba nh&agrave; b&aacute;n lẻ trong top 10 c&oacute; tốc độ tăng trưởng cao hơn mức trung b&igrave;nh của top 250 với 3,8%.</p>\r\n<h3 class=\"SubTitle\">9. The Home Depot</h3>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/44/home-depot.jpg\" border=\"1\" alt=\"The home depot\" width=\"480\" height=\"293\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trụ sở: Mỹ</p>\r\n<p class=\"Normal\">Số quốc gia đang hoạt động: 5</p>\r\n<p class=\"Normal\">Doanh thu năm 2009: 66,17 tỷ USD</p>\r\n<p class=\"Normal\">Tăng trưởng lợi nhuận năm 2009: 4,0 %</p>\r\n<p class=\"Normal\">Home Depot đ&atilde; tụt 2 bậc trong danh s&aacute;ch n&agrave;y năm nay, phản &aacute;nh sự sụp đổ của thị trường nh&agrave; đất Mỹ v&agrave; vẫn đang tiếp tục trượt dốc trong doanh số b&aacute;n đồ gia dụng.</p>\r\n<h3 class=\"SubTitle\">10. Target</h3>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/57/44/target.jpg\" border=\"1\" alt=\"The target\" width=\"480\" height=\"345\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trụ sở: US</p>\r\n<p class=\"Normal\">Số quốc gia đang hoạt động: 1</p>\r\n<p class=\"Normal\">Doanh thu năm 2009: 65,35 tỷ USD</p>\r\n<p class=\"Normal\">Tăng trưởng lợi nhuận năm 2009: 3,8 %</p>\r\n<p class=\"Normal\">Đứng thứ 10 trong danh s&aacute;ch năm nay, nhưng theo danh s&aacute;ch &ldquo;sa s&uacute;t&rdquo; của Deloitte t&iacute;nh theo ph&acirc;n kh&uacute;c sản phẩm, c&ocirc;ng ty n&agrave;y lại xếp ngay thứ 2 chỉ sau Metro AG của Đức.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>H&agrave; Thu</strong>&nbsp;(theo&nbsp;<em>CNBC</em>)</p>', 12, 3, 1073, 0, 1),
(572, 'Kinh tế Nhật Bản dựa vào người già', 'Kinh-Te-Nhat-Ban-Dua-Vao-Nguoi-Gia', 'Tại Nhật Bản, không hiếm các ông cụ tuổi thất thập lai hy vẫn ngày ngày đến nhiệm sở để làm việc. Đây là một trong những thuận lợi đối với nước này trong thời buổi kinh tế khó khăn. ', 'articleLarge.jpg', NULL, 22, '<h1 class=\"Title\">Kinh tế Nhật Bản dựa v&agrave;o người gi&agrave;</h1>\r\n<h2 class=\"Lead\">Tại Nhật Bản, kh&ocirc;ng hiếm c&aacute;c &ocirc;ng cụ tuổi thất thập lai hy vẫn ng&agrave;y ng&agrave;y đến nhiệm sở để l&agrave;m việc. Đ&acirc;y l&agrave; một trong những thuận lợi đối với nước n&agrave;y trong thời buổi kinh tế kh&oacute; khăn.&nbsp;<br /><span style=\"font-size: 10px; font-weight: normal;\">Nền kinh tế Nhật Bản đang loay hoay chưa t&igrave;m được lối tho&aacute;t trong mối nguy giảm ph&aacute;t v&agrave; khủng hoảng nợ c&ocirc;ng. Tuy nhi&ecirc;n, nếu so với những nước c&ugrave;ng cảnh ngộ ở khu vực đồng tiền chung ch&acirc;u &Acirc;u, quốc gia ch&acirc;u &Aacute; n&agrave;y vẫn c&oacute; nhiều thuận lợi.</span></h2>\r\n<p class=\"Normal\">Một trong số c&aacute;c thuận lợi đ&oacute; l&agrave; Nhật Bản sở hữu một lực lượng lao động chăm chỉ, cần mẫn, d&ugrave; đến tuổi thất thập vẫn chưa chịu nghỉ hưu, đặc biết l&agrave; c&aacute;nh nam giới.</p>\r\n<p class=\"Normal\">Theo số liệu thống kể ở Nhật từ 2008,<em>&nbsp;Forbes</em>&nbsp;cho biết c&oacute; tới 92,5% nam giới tuổi từ 55 đến 59 vẫn c&oacute; mặt trong lực lượng lao động. Con số n&agrave;y cao hơn rất nhiều so với 74,2% tại Li&ecirc;n minh ch&acirc;u &Acirc;u.</p>\r\n<p class=\"Normal\">Khoảng c&aacute;ch c&agrave;ng lớn khi so s&aacute;nh ở độ tuổi 60 - 64, với hai tỷ lệ lần lượt l&agrave; 76,4% v&agrave; 42% ở Nhật v&agrave; EU. Thậm ch&iacute; c&aacute;c \"b&ocirc; l&atilde;o\" Nhật Bản tuổi từ 70 đến 74 vẫn hăng h&aacute;i đến cơ quan hằng ng&agrave;y. Ở độ tuổi n&agrave;y, trong khi chỉ 5,4% nam giới ở ch&acirc;u &Acirc;u c&ograve;n đi l&agrave;m, th&igrave; tại Nhật tỷ lệ n&agrave;y l&agrave; 30,5%.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/articleLarge.jpg\" border=\"1\" alt=\"Người gi&agrave; Nhật Bản\" width=\"380\" height=\"287\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Kh&ocirc;ng &iacute;t người Nhật d&ugrave; t&oacute;c đ&atilde; bạc trắng nhưng vẫn chăm chỉ đi l&agrave;m. Ảnh:<em>Nytimes</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nhiều nh&agrave; nghi&ecirc;n cứu đ&atilde; bỏ c&ocirc;ng để t&igrave;m hiểu về \"hiện tượng lạ\" tại đất nước mặt trời mọc. C&oacute; &yacute; kiến cho rằng sự thất vọng về t&igrave;nh h&igrave;nh kinh tế, x&atilde; hội khiến những người gi&agrave; đi l&agrave;m để cảm thấy khu&acirc;y khỏa, t&igrave;m niềm vui. B&ecirc;n cạnh đ&oacute;, l&acirc;u nay người Nhật vẫn nổi tiếng l&agrave; cần c&ugrave; hơn so với c&aacute;c d&acirc;n tộc kh&aacute;c ở ch&acirc;u &Acirc;u. Ngo&agrave;i ra, tuổi thọ trung b&igrave;nh thuộc h&agrave;ng \"top\" thế giới cũng gi&uacute;p cho người gi&agrave; Nhật đủ sức khỏe để tiếp tục đến cơ quan d&ugrave; đ&atilde; l&ecirc;n l&atilde;o.</p>\r\n<p class=\"Normal\">Nếu như c&aacute;c nh&agrave; kinh tế x&atilde; hội Nhật đang lo ngại với t&igrave;nh trạng thiếu hụt lao động trẻ v&igrave; d&acirc;n số ng&agrave;y c&agrave;ng gi&agrave; đi, họ c&oacute; thể cảm thấy được an ủi phần n&agrave;o với sự cần mẫn của tầng lớp lao động lớn tuổi.</p>\r\n<p class=\"Normal\">Theo như Gi&aacute;o sư Thomas Sowell (Mỹ) đ&atilde; viết trong ấn bản thứ tư của cuốn \"Basic Economics\" th&igrave; \"Kh&ocirc;ng chỉ tuổi nghỉ hưu của c&aacute;c nước kh&aacute;c nhau, m&agrave; mức lương hưu cũng rất kh&aacute;c nhau tại từng quốc gia\". Trong khi ở Mỹ, người nghỉ hưu nhận được trợ cấp bằng 40% mức lương họ từng c&oacute;, th&igrave; ở Nhật, con số n&agrave;y thấp hơn 40%. Tại c&aacute;c nước ch&acirc;u &Acirc;u, lương hưu cao khiến người gi&agrave; kh&ocirc;ng cần phải lo lắng về cuộc sống sau khi \"về vườn\". Người về hưu ở H&agrave; Lan, T&acirc;y Ban Nha nhận được số tiền bằng 80% thu nhập v&agrave; ở Hy Lạp, tỷ lệ n&agrave;y l&agrave; 96%.</p>\r\n<p class=\"Normal\">Gi&aacute;o sư Sowell kết luận: \"Kh&ocirc;ng c&ograve;n nghi ngờ g&igrave; nữa, mức lương hưu ảnh hưởng đến quyết định thời điểm nghỉ hưu của người lao động\".</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Thanh B&igrave;nh</strong></p>', 12, 3, 2086, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(573, 'Mỹ phẩm cho nam giới hút khách tại Trung Quốc', 'My-Pham-Cho-Nam-Gioi-Hut-Khach-Tai-Trung-Quoc', 'Ngành công nghiệp mỹ phẩm dành cho phái mạnh đang rất thành công ở Trung Quốc khi nhu cầu làm đẹp của nam giới ngày càng cao.', 'cham_soc_da_1.jpg', NULL, 22, '<h1 class=\"Title\">Mỹ phẩm cho nam giới h&uacute;t kh&aacute;ch tại Trung Quốc</h1>\r\n<h2 class=\"Lead\">Ng&agrave;nh c&ocirc;ng nghiệp mỹ phẩm d&agrave;nh cho ph&aacute;i mạnh đang rất th&agrave;nh c&ocirc;ng ở Trung Quốc khi nhu cầu l&agrave;m đẹp của nam giới ng&agrave;y c&agrave;ng cao.</h2>\r\n<p class=\"Normal\">Ban đầu chỉ l&agrave; d&ugrave;ng l&eacute;n mỹ phẩm của vợ, nhưng giờ th&igrave; anh Ben Shiyuan đ&atilde; sử dụng cả nước dưỡng da, sữa rửa mặt v&agrave; cả kem dưỡng ẩm mỗi s&aacute;ng. Anh Shiyuan - một quan chức 39 tuổi sống tại Bắc Kinh cho biết: &ldquo;Đ&oacute; mới chỉ l&agrave; phần nổi của tảng băng ch&igrave;m m&agrave; th&ocirc;i. T&ocirc;i c&ograve;n đắp mặt nạ b&ugrave;n, m&aacute;t-xa mặt h&agrave;ng tuần, d&ugrave;ng kem che khuyết điểm để giấu vết th&acirc;m quanh mắt v&agrave; phấn nền nếu da t&ocirc;i tr&ocirc;ng c&oacute; vẻ nhợt nhạt&rdquo;.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/56/B5/cham_soc_da_1.jpg\" border=\"1\" alt=\"Một người đ&agrave;n &ocirc;ng đang trong qu&aacute; tr&igrave;nh chăm s&oacute;c da tại một triển l&atilde;m mỹ phẩm ở Vũ H&aacute;n, Hồ Bắc\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một người đ&agrave;n &ocirc;ng đang trong qu&aacute; tr&igrave;nh chăm s&oacute;c da tại một triển l&atilde;m mỹ phẩm ở Vũ H&aacute;n, Hồ Bắc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Ben Shiyuan chỉ l&agrave; một trong số rất nhiều người được gọi l&agrave; &ldquo;dị t&iacute;nh luyến &aacute;i&rdquo; - thuật ngữ Trung Quốc d&ugrave;ng để chỉ những người đ&agrave;n &ocirc;ng th&iacute;ch d&ugrave;ng mỹ phẩm v&agrave; chăm ch&uacute;t cho vẻ ngo&agrave;i của m&igrave;nh. Ch&iacute;nh những người n&agrave;y đ&atilde; biến Trung Quốc trở th&agrave;nh một thị trường b&eacute;o bở cho ng&agrave;nh c&ocirc;ng nghiệp mỹ phẩm nam giới.</p>\r\n<p class=\"Normal\">Theo c&aacute;c nh&agrave; tư vấn tại Euromonitor International, doanh thu từ c&aacute;c sản phẩm chăm s&oacute;c sức khỏe v&agrave; sắc đẹp cho ph&aacute;i mạnh tại Trung Quốc đ&atilde; vượt qua Bắc Mỹ v&agrave;o năm 2010 v&agrave; c&oacute; lẽ sẽ tăng gấp 5 lần cho đến năm 2014.</p>\r\n<p class=\"Normal\">Nh&agrave; ph&acirc;n t&iacute;ch Kevin Zhu của Euromonitor International c&ograve;n cho biết th&ecirc;m: &ldquo;Đ&agrave;n &ocirc;ng Trung Quốc đ&atilde; cảm thấy thoải m&aacute;i hơn nhiều trong việc mua mỹ phẩm cho ri&ecirc;ng m&igrave;nh. Họ kh&ocirc;ng cần phải mượn vợ hay bạn g&aacute;i nữa&rdquo;.</p>\r\n<p class=\"Normal\">C&aacute;c nh&agrave; x&atilde; hội học cho hay sự tự tin n&agrave;y cũng đi k&egrave;m với nhu cầu l&agrave;m m&igrave;nh nổi bật hơn trong c&ocirc;ng sở. C&aacute;c dữ liệu mới được c&ocirc;ng bố của c&ocirc;ng ty tư vấn CTR cho thấy gần 40% đ&agrave;n &ocirc;ng th&agrave;nh thị d&ugrave;ng c&aacute;c sản phẩm chăm s&oacute;c da v&agrave; mỹ phẩm kh&aacute;c trong nửa đầu năm 2010, 60% trong số đ&oacute; l&agrave; c&aacute;c doanh nh&acirc;n.</p>\r\n<p class=\"Normal\">Anh Shiyuan n&oacute;i: &ldquo;Chăm s&oacute;c vẻ bề ngo&agrave;i của m&igrave;nh kh&ocirc;ng phải l&agrave; việc l&agrave;m nữ t&iacute;nh, đặc biệt l&agrave; đối với những người c&oacute; tuổi như t&ocirc;i trong ng&agrave;nh c&ocirc;ng nghiệp bị thống trị bởi những người trẻ tuổi v&agrave; đầy nhiệt huyết&rdquo;. Anh đang l&agrave; gi&aacute;m đốc cấp cao tại một c&ocirc;ng ty về mạng x&atilde; hội v&agrave; thừa nhận rằng m&igrave;nh phải d&ugrave;ng mascara của Lancome trong c&aacute;c buổi họp hay thuyết tr&igrave;nh quan trọng.</p>\r\n<p class=\"Normal\">Anh giải th&iacute;ch rằng: &ldquo; Ngoại h&igrave;nh kh&aacute; gi&uacute;p t&ocirc;i được người kh&aacute;c nể trọng. Đặc biệt l&agrave; khi l&agrave;m việc với c&aacute;c nh&acirc;n vi&ecirc;n trẻ. N&oacute; cũng cho đối t&aacute;c thấy rằng t&ocirc;i l&agrave; người nghi&ecirc;m t&uacute;c, ch&uacute; &yacute; đến tiểu tiết v&agrave; c&oacute; tư tưởng tiến bộ&rdquo;.</p>\r\n<p class=\"Normal\">Mỹ phẩm cũng gi&uacute;p c&aacute;c ứng cử vi&ecirc;n trở n&ecirc;n ấn tượng hơn trong mắt nh&agrave; tuyển dụng. Mặc d&ugrave; yếu tố ngoại h&igrave;nh chỉ chiếm 20 &ndash; 30% một buổi phỏng vấn, nhưng theo b&agrave; Judy Zhu &ndash; gi&aacute;m đốc C&ocirc;ng ty tư vấn việc l&agrave;m quốc tế (CTR) th&igrave;: &ldquo;Khi c&aacute;c ứng cử vi&ecirc;n c&oacute; năng lực tương đương nhau, th&igrave; ngoại h&igrave;nh kh&aacute; sẽ l&agrave; một lợi thế&rdquo;.</p>\r\n<p class=\"Normal\">Lời khuy&ecirc;n của c&aacute;c chuy&ecirc;n gia mỹ phẩm cho nam giới được đăng tải tr&ecirc;n rất nhiều tạp ch&iacute; về phong c&aacute;ch sống, kể cả c&aacute;c chi nh&aacute;nh của tạp ch&iacute; thời trang nổi tiếng như Bazaar Men\'s Style v&agrave; GQ. Zow Wen &ndash; bi&ecirc;n tập vi&ecirc;n của tờ GQ tại Trung Quốc trả lời phỏng vấn của h&atilde;ng th&ocirc;ng tấn&nbsp;<em>Bloomberg&nbsp;</em>rằng: &ldquo;Ch&uacute;ng t&ocirc;i c&oacute; hẳn một b&agrave;i b&aacute;o về mỹ phẩm d&agrave;nh cho nam giới trong mỗi ấn bản, v&agrave; c&aacute;c độc giả phản hồi tr&ecirc;n Internet rằng họ muốn c&oacute; 2 hay 3 b&agrave;i b&aacute;o như thế nữa&rdquo;.</p>\r\n<p class=\"Normal\">V&agrave; d&ugrave; mục đ&iacute;ch l&agrave; g&igrave; th&igrave; đ&agrave;n &ocirc;ng Trung Quốc c&agrave;ng d&agrave;nh nhiều thời gian đứng trước gương th&igrave; c&aacute;c h&atilde;ng mỹ phẩm lớn c&agrave;ng được lợi.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/56/B5/cham_soc_mong.jpg\" border=\"1\" alt=\"Một người đ&agrave;n &ocirc;ng đang chăm s&oacute;c m&oacute;ng tại khu quảng c&aacute;o của một thẩm mỹ viện ở Nam Kinh, Giang T&ocirc;\" width=\"450\" height=\"600\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một người đ&agrave;n &ocirc;ng đang chăm s&oacute;c m&oacute;ng tại khu quảng c&aacute;o của một thẩm mỹ viện ở Nam Kinh, Giang T&ocirc;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\"><strong><span style=\"color: #4f4f4f;\">Thị trường chăm s&oacute;c da cho nam giới tại Trung Quốc c&oacute; gi&aacute; trị ước t&iacute;nh l&agrave; 269,6 triệu USD trong năm 2010</span></strong>, nhỉnh hơn một ch&uacute;t so với 227,4 triệu USD tại Bắc Mỹ. Theo dự b&aacute;o của Euromonitor, thị trường Trung Quốc sẽ tăng trưởng 29% h&agrave;ng năm trong giai đoạn 2009 &ndash; 2014, con số n&agrave;y ở Bắc Mỹ l&agrave; 5,7% v&agrave; ở ch&acirc;u &Acirc;u l&agrave; 7,9%.</p>\r\n<p class=\"Normal\">L\'Oreal SA &ndash; c&ocirc;ng ty mỹ phẩm lớn nhất thế giới &ndash; đ&atilde; giới thiệu sản phẩm Biotherm Homme v&agrave;o thị trường Trung Quốc năm 2003 trước khi quảng c&aacute;o cho sản phẩm nổi tiếng Men Expert 3 năm sau đ&oacute;. Gi&aacute; b&aacute;n lẻ của sản phẩm n&agrave;y l&agrave; hơn 100 NDT (tương đương 15 USD).</p>\r\n<p class=\"Normal\">C&aacute;c số liệu của Euromonitor cho thấy L&rsquo;Oreal chiếm 32% thị phần tại Trung Quốc năm 2009. &Ocirc;ng Alexis Perakis-Valat &ndash; CEO của L&rsquo;Oreal Trung Quốc cho biết: &ldquo;Ch&uacute;ng t&ocirc;i ước t&iacute;nh ph&acirc;n kh&uacute;c mỹ phẩm cho nam giới sẽ tăng trưởng với tốc độ gấp đ&ocirc;i tốc độ của to&agrave;n bộ thị trường mỹ phẩm ở Trung Quốc. V&agrave; đ&oacute; mới chỉ l&agrave; sự khởi đầu. Đ&agrave;n &ocirc;ng Trung Quốc rất hiện đại v&agrave; kh&ocirc;ng ki&ecirc;ng kị g&igrave; hết. Họ rất thực tế v&agrave; họ cũng nhận thấy rằng ngoại h&igrave;nh kh&aacute; l&agrave; một c&aacute;ch tuyệt vời để thể hiện bản th&acirc;n cũng như cải thiện c&aacute;c mối quan hệ với gia đ&igrave;nh, bạn b&egrave; v&agrave; đồng nghiệp&rdquo;.</p>\r\n<p class=\"Normal\">Năm ngo&aacute;i, c&ocirc;ng ty n&agrave;y cũng đ&atilde; bắt đầu b&aacute;n Garnier &ndash; một nh&atilde;n hiệu nhắm v&agrave;o những người mới d&ugrave;ng mỹ phẩm tại c&aacute;c th&agrave;nh phố hạng ba v&agrave; hạng tư tại Trung Quốc. C&aacute;c c&ocirc;ng ty kh&aacute;c cũng mới gia nhập thị trường n&agrave;y trong v&agrave;i năm gần đ&acirc;y l&agrave; Beiersdorf AG với Nivea, Shiseido của Nhật với Aupres JS v&agrave; Proctor and Gamble (P&amp;G) với Olay for Men.</p>\r\n<p class=\"Normal\">C&aacute;c nh&atilde;n hiệu trong nước cũng kh&aacute; th&agrave;nh c&ocirc;ng. C&ocirc;ng ty Shanghai Jahwa United gần đ&acirc;y đ&atilde; ra mắt sản phẩm c&oacute; chứa c&aacute;c loại thực vật mọc tr&ecirc;n sa mạc để cải thiện sức chống chịu của da trong những ho&agrave;n cảnh khắc nghiệt.</p>\r\n<p class=\"Normal\">Kenvin Zhu của Euromonitor cho biết: &ldquo;Đột ph&aacute; v&agrave; ph&aacute;t triển đ&atilde; l&agrave;m thị trường Trung Quốc tăng trưởng. Đ&atilde; qua rồi c&aacute;i thời chỉ c&oacute; một v&agrave;i sản phẩm rửa v&agrave; dưỡng ẩm đơn giản. Giờ l&agrave; l&uacute;c thống trị của c&aacute;c d&ograve;ng sản phẩm phức tạp hơn như kem chống l&atilde;o h&oacute;a hay chống &ocirc; xi h&oacute;a&rdquo;.</p>\r\n<p class=\"Normal\">Tốc độ tăng trưởng h&agrave;ng năm của to&agrave;n bộ thị trường mỹ phẩm đ&atilde; tăng vọt từ 44,83 tỷ USD năm 2002 l&ecirc;n 66,6 tỷ USD năm 2009 v&agrave; được dự đo&aacute;n chạm mức 77,84 tỷ USD năm 2012.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, nếu so s&aacute;nh với mỹ phẩm cho nữ giới tại Trung Quốc, th&igrave; thị trường cho nam giới vẫn c&ograve;n tương đối chậm ph&aacute;t triển, v&agrave; theo nh&agrave; ph&acirc;n t&iacute;ch Zhu th&igrave; điều đ&oacute; c&oacute; nghĩa l&agrave; c&ograve;n rất nhiều khoảng trống cho c&aacute;c sản phẩm mới.</p>\r\n<p class=\"Normal\">Anh cũng dự đo&aacute;n rằng c&aacute;c sản phẩm với gi&aacute; th&agrave;nh hợp l&yacute; v&agrave; được ph&acirc;n phối rộng r&atilde;i sẽ tăng trưởng mạnh nhất. C&aacute;c nghi&ecirc;n cứu của P&amp;G cũng cho thấy khoảng 65% đ&agrave;n &ocirc;ng Trung Quốc đang sử dụng sản phẩm chăm s&oacute;c da.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n, theo anh Tian Xun &ndash; bi&ecirc;n tập vi&ecirc;n tại tạp ch&iacute; thời trang cho nam giới Esquire &ndash; trong khi doanh thu của c&aacute;c sản phẩm dưỡng ẩm v&agrave; lotion c&oacute; thể sẽ tăng, th&igrave; lại c&oacute; rất &iacute;t nam giới chọn mua mặt nạ dưỡng da. &Ocirc;ng Alexander Dony &ndash; gi&aacute;m đốc điều h&agrave;nh bộ phận mỹ phẩm cho nam giới tại P&amp;G Trung Quốc cho biết: &ldquo;Thị trường n&agrave;y tại Trung Quốc mới bắt đầu ph&aacute;t triển v&agrave; người ti&ecirc;u d&ugrave;ng vẫn đang trong qu&aacute; tr&igrave;nh tiếp nhận c&aacute;c kh&aacute;i niệm về l&agrave;m đẹp&rdquo;. Chiến lược của P&amp;G l&agrave; x&acirc;y dựng một chuỗi c&aacute;c d&ograve;ng sản phẩm cho nam giới như chăm s&oacute;c da, cạo r&acirc;u, chăm s&oacute;c to&agrave;n th&acirc;n v&agrave; chăm s&oacute;c t&oacute;c để thỏa m&atilde;n nhu cầu ng&agrave;y c&agrave;ng cao của đ&agrave;n &ocirc;ng Trung Quốc.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>H&agrave; Thu</strong>&nbsp;(theo&nbsp;<em>Chinadaily</em>)</p>', 12, 3, 2358, 0, 1),
(773, 'Nỗi đau con trẻ trong vụ thảm án \'dìm xác mẹ xuống sông\'', '', 'Từ ngày cha mẹ bị bắt sau cái chết bí ẩn của bà nội, chị em Trâm mỗi đứa một nơi, căn nhà đóng cửa bỏ hoang. Ngoài nỗi nhớ cha mẹ đến thắt ruột, những đứa trẻ còn hứng chịu búa rìu dư luận vì đấng sinh thành bị buộc vào tội ác tày đình.', 'bai (7).JPG', NULL, 22, '<p class=\"Title\">Nỗi đau con trẻ trong vụ thảm &aacute;n \'d&igrave;m x&aacute;c mẹ xuống s&ocirc;ng\'</p>\r\n<p class=\"Lead\">Từ ng&agrave;y cha mẹ bị bắt sau c&aacute;i chết b&iacute; ẩn của b&agrave; nội, chị em Tr&acirc;m mỗi đứa một nơi, căn nh&agrave; đ&oacute;ng cửa bỏ hoang. Ngo&agrave;i nỗi nhớ cha mẹ đến thắt ruột, những đứa trẻ c&ograve;n hứng chịu b&uacute;a r&igrave;u dư luận v&igrave; đấng sinh th&agrave;nh bị buộc v&agrave;o tội &aacute;c t&agrave;y đ&igrave;nh.</p>\r\n<p class=\"Normal\">C&ocirc; g&aacute;i trẻ một tay vịn chặt cậu em, một tay &ocirc;m mặt ngăn d&ograve;ng nước mắt, d&otilde;i theo d&aacute;ng cha mẹ được dẫn giải l&ecirc;n cầu thang tiến về ph&ograve;ng xử &aacute;n của t&ograve;a ph&uacute;c thẩm TAND TP HCM. Thấy cha chực ng&atilde; nh&agrave;o khi bước l&ecirc;n bậc tam cấp v&igrave; chiếc x&iacute;ch ch&acirc;n qu&aacute; chặt, hai chị em c&ugrave;ng bật kh&oacute;c nức nở, chạy &ugrave;a đến nhưng đ&atilde; bị ngăn lại bởi h&agrave;ng r&agrave;o cảnh s&aacute;t.<br /><br />Từ h&agrave;ng ghế bị c&aacute;o, hai vợ chồng Huỳnh Văn Quy&ecirc;n (48 tuổi) v&agrave; L&ecirc; Thị T&aacute;m (43 tuổi, c&ugrave;ng ngụ Vĩnh Long) chốc chốc lại quay người xuống dưới nh&igrave;n hai đứa con nhỏ lẫn trong những khu&ocirc;n mặt th&acirc;n quen của người th&acirc;n. Như hiểu &aacute;nh mắt của họ, Tr&acirc;m khẽ kh&agrave;ng l&ecirc;n tiếng: \"Ch&uacute;ng con vẫn khỏe, em ba bận thi kh&ocirc;ng đến được\". Chỉ n&oacute;i được đến đ&oacute;, nước mắt lại chảy tr&agrave;n tr&ecirc;n gương mặt c&ocirc; g&aacute;i.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/96/96/bai.JPG\" border=\"1\" alt=\"\" width=\"300\" height=\"400\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Phi&ecirc;n t&ograve;a bắt đầu cũng l&agrave; l&uacute;c Tr&acirc;m run rẩy chắp tay nguyện cầu cho cha mẹ được giải oan.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Phi&ecirc;n t&ograve;a bắt đầu cũng l&agrave; l&uacute;c Huyền Tr&acirc;m run rẩy chắp tay nguyện cầu, kh&ocirc;ng m&agrave;ng đến những &aacute;nh mắt thương cảm của mọi người đang hướng về m&igrave;nh. C&ocirc; cứ ngồi như thế suốt thời gian chủ tọa c&ocirc;ng bố nội dung vụ &aacute;n, nước mắt quện loang cả vạt &aacute;o sơ mi m&agrave;u hồng nhạt.</p>\r\n<p class=\"normal\">B&agrave; Dương Thị T&aacute;m (78 tuổi) sống với vợ chồng người con trai &uacute;t l&agrave; Quy&ecirc;n v&agrave; T&aacute;m, l&agrave;m nghề b&aacute;n b&uacute;n. Do b&agrave; T&aacute;m thường khắt khe, hay rầy la con ch&aacute;u n&ecirc;n cuộc sống gia đ&igrave;nh c&oacute; nhiều bất h&ograve;a. M&acirc;u thuẫn c&agrave;ng l&ecirc;n cao khi b&agrave; T&aacute;m c&oacute; &yacute; định b&aacute;n đất chia đều cho c&aacute;c con g&aacute;i nhưng Quy&ecirc;n kh&ocirc;ng đồng &yacute;.</p>\r\n<p class=\"normal\">Rạng s&aacute;ng ng&agrave;y 7/2/2007, trước khi đi bỏ mối b&uacute;n cho bạn h&agrave;ng, Quy&ecirc;n lại xảy ra c&atilde;i v&atilde; với mẹ. Do qu&aacute; tức giận, người n&agrave;y đ&atilde; b&oacute;p cổ mẹ v&agrave; gọi vợ đến phụ gi&uacute;p. Nghĩ mẹ đ&atilde; chết, vợ chồng Quy&ecirc;n khi&ecirc;ng x&aacute;c xuống xuồng, buộc bao gạch v&agrave;o thi thể nạn nh&acirc;n rồi chở ra s&ocirc;ng d&igrave;m x&aacute;c.</p>\r\n<p class=\"normal\">Với kết luận tr&ecirc;n, TAND tỉnh Vĩnh Long tuy&ecirc;n phạt Quy&ecirc;n mức &aacute;n chung th&acirc;n, T&aacute;m nhận 13 năm t&ugrave; c&ugrave;ng về tội &ldquo;giết người&rdquo;.</p>\r\n<p class=\"normal\">Cũng giống như những lần x&eacute;t xử trước, vợ chồng Quy&ecirc;n vẫn một mực k&ecirc;u oan, rằng những bản cung nhận tội giết mẹ trong hồ sơ vụ &aacute;n l&agrave; do bị c&aacute;n bộ điều tra &eacute;p cung. N&oacute;i về người h&agrave;ng x&oacute;m được x&aacute;c định l&agrave; nh&acirc;n chứng đ&atilde; nh&igrave;n thấy vợ chồng m&igrave;nh khi&ecirc;ng x&aacute;c mẹ, cả Quy&ecirc;n v&agrave; T&aacute;m đều cho rằng do người n&agrave;y c&oacute; m&acirc;u thuẫn với gia đ&igrave;nh m&igrave;nh trước đ&oacute; n&ecirc;n lời khai kh&ocirc;ng đ&aacute;ng tin cậy.</p>\r\n<p class=\"normal\">&ldquo;Ch&uacute;ng t&ocirc;i kh&ocirc;ng giết mẹ. Ra t&ograve;a l&agrave; cơ hội cho ch&uacute;ng t&ocirc;i n&oacute;i hết sự thật, xin HĐXX xem x&eacute;t giải oan cho ch&uacute;ng t&ocirc;i&hellip;&rdquo;, cặp vợ chồng tha thiết khẩn cầu.</p>\r\n<p class=\"Normal\">Thế nhưng, sau gần 5 giờ l&agrave;m việc căng thẳng, một lần nữa, cơ quan x&eacute;t xử vẫn chưa c&oacute; kết luận cuối c&ugrave;ng. Ng&agrave;y 3/3, t&ograve;a ph&uacute;c thẩm đ&atilde; trả hồ sơ vụ &aacute;n, giao về cấp sơ thẩm điều tra x&eacute;t xử lại v&igrave; h&agrave;ng loạt sai phạm tố tụng v&agrave; những chứng cứ buộc tội chưa được l&agrave;m s&aacute;ng tỏ.</p>\r\n<p class=\"Normal\">Lại một cuộc chia ly ch&oacute;ng v&aacute;nh nhưng đầy nước mắt của chị em Tr&acirc;m với đấng sinh th&agrave;nh. Chỉ c&aacute;ch nhau v&agrave;i bước ch&acirc;n nhưng hai đứa trẻ kh&ocirc;ng thể đến gần cha mẹ. Khi chiếc xe t&ugrave; mất h&uacute;t sau c&aacute;nh cổng t&ograve;a &aacute;n, Tr&acirc;m đứng thẫn thờ như h&oacute;a đ&aacute;, hai mắt đỏ hoe.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/96/96/bai%202.JPG\" border=\"1\" alt=\"\" width=\"400\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hơn 1.000 ng&agrave;y xa c&aacute;ch, chị em c&ocirc; chỉ được nh&igrave;n thấy cha mẹ ở 3 phi&ecirc;n x&eacute;t xử.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Bằng giọng nghẹn ng&agrave;o, c&ocirc; g&aacute;i trẻ kể về tấm bi kịch đ&atilde; xảy đến gia đ&igrave;nh m&igrave;nh hơn 3 năm về trước.</p>\r\n<p class=\"Normal\">Ng&agrave;y l&agrave;m đ&aacute;m ma b&agrave; nội, cha c&ocirc; rất đau khổ v&igrave; trong d&ograve;ng tộc ai cũng nghĩ l&agrave; b&agrave; tự tử. Trước đ&oacute;, đ&atilde; v&agrave;i lần b&agrave; nội tuy&ecirc;n bố với mọi người nếu sau n&agrave;y sức khỏe k&eacute;m sẽ tự chết chứ kh&ocirc;ng muốn l&agrave;m phiền con ch&aacute;u. C&aacute;c c&ocirc; ch&uacute; tưởng b&agrave; lẩn thẩn, ra sức khuy&ecirc;n nhủ dỗ d&agrave;nh nhưng cũng c&oacute; lần b&agrave; đ&atilde; lao đầu v&agrave;o xe &ocirc;t&ocirc; khiến con ch&aacute;u một phen h&uacute; v&iacute;a. Nhưng lần n&agrave;y b&agrave; nội chết thật, l&agrave; người c&oacute; nghĩa vụ phụng dưỡng b&agrave; n&ecirc;n cha c&ocirc; tự tr&aacute;ch m&igrave;nh đ&atilde; kh&ocirc;ng tr&ograve;n bổn phận l&agrave;m con.</p>\r\n<p class=\"Normal\">Chưa ngu&ocirc;i nỗi đau mất b&agrave;, khi Tr&acirc;m đang ở Cần Thơ theo học năm nhất ng&agrave;nh Du lịch th&igrave; nhận được điện b&aacute;o cha bị bắt. Nh&aacute;o nh&agrave;o về đến qu&ecirc;, c&ocirc; đau đớn chứng kiến c&ocirc;ng an đến bắt lu&ocirc;n cả mẹ c&ocirc; v&igrave; cho rằng họ giết chết b&agrave; nội. Từ đ&oacute;, gia đ&igrave;nh c&ocirc; tan n&aacute;t. C&ocirc; về ở với &ocirc;ng b&agrave; ngoại c&ograve;n hai đứa em được người b&aacute;c lớn mang về nu&ocirc;i. Căn nh&agrave; ng&agrave;y xưa vốn tr&agrave;n ngập tiếng cười giờ bỏ hoang, lạnh lẽo.</p>\r\n<p class=\"Normal\">&ldquo;Đ&oacute; l&agrave; khoảng thời gian kinh khủng nhất của 3 chị em t&ocirc;i. Nếu kh&ocirc;ng c&oacute; người th&acirc;n an ủi động vi&ecirc;n, chắc chẳng đứa n&agrave;o c&oacute; t&acirc;m tr&iacute; học bởi kh&ocirc;ng chịu nổi tiếng gi&egrave;m pha của người đời v&agrave; mặc cảm với thầy c&ocirc;, bạn b&egrave;. Người ta thấy c&ocirc;ng an bắt cha mẹ t&ocirc;i th&igrave; quả quyết họ l&agrave; hung thủ&hellip;&rdquo;, Tr&acirc;m bật kh&oacute;c nức nở.</p>\r\n<p class=\"Normal\">Ng&agrave;y qua ng&agrave;y, chị em c&ocirc; mong ng&oacute;ng tin tức của cha mẹ đến mỏi m&ograve;n nhưng cơ quan điều tra viện đủ l&yacute; do kh&ocirc;ng cho gặp. L&uacute;c th&igrave; vụ &aacute;n đang ở giai đoạn điều tra, l&uacute;c lại cho rằng hồ sơ đ&atilde; chuyển l&ecirc;n t&ograve;a tối cao n&ecirc;n kh&ocirc;ng c&ograve;n thẩm quyền giải quyết. Vậy l&agrave;, hơn 1.000 ng&agrave;y xa c&aacute;ch, chị em c&ocirc; chỉ được nh&igrave;n thấy cha mẹ ở 3 phi&ecirc;n x&eacute;t xử nhưng cũng chẳng thể n&oacute;i được g&igrave;.</p>\r\n<p class=\"Normal\">&ldquo;Chị em t&ocirc;i v&agrave; cả hai b&ecirc;n nội ngoại tha thiết mong những người l&agrave;m ph&aacute;p luật h&atilde;y t&igrave;m ra sự thật, để chị em t&ocirc;i c&oacute; một gia đ&igrave;nh đ&uacute;ng nghĩa&rdquo;, Tr&acirc;m khẩn nguyện đầy x&oacute;t xa.</p>\r\n<p class=\"Normal\"><strong>Vũ Mai</strong></p>', 26, 6, 3, 0, 1),
(774, 'Nhát dao oan nghiệt đoạn tuyệt 15 năm vợ chồng', 'Nhat-Dao-Oan-Nghiet-Doan-Tuyet-15-Nam-Vo-Chong', 'Hương không ngờ, chỉ trong tích tắc, con dao trên tay cô đã cướp đi mạng sống người chồng đầu gối tay ấp gần 15 năm. Nỗi ân hận giằng xé tâm can cô khi mỗi đêm, đứa con thơ lên tiếng hỏi: \"Cha ở đâu hả mẹ?\".', 'bicao.jpg', NULL, 22, '<p class=\"Title\">Nh&aacute;t dao oan nghiệt đoạn tuyệt 15 năm vợ chồng</p>\r\n<p class=\"Lead\">Hương kh&ocirc;ng ngờ, chỉ trong t&iacute;ch tắc, con dao tr&ecirc;n tay c&ocirc; đ&atilde; cướp đi mạng sống người chồng đầu gối tay ấp gần 15 năm. Nỗi &acirc;n hận giằng x&eacute; t&acirc;m can c&ocirc; khi mỗi đ&ecirc;m, đứa con thơ l&ecirc;n tiếng hỏi: \"Cha ở đ&acirc;u hả mẹ?\".</p>\r\n<p class=\"Normal\">Người đ&agrave;n b&agrave; thấp b&eacute;, khu&ocirc;n mặt lam lũ, chất chứa nỗi muộn phiền ngồi lọt thỏm trước v&agrave;nh m&oacute;ng ngựa tại TAND H&agrave; Nội v&agrave;o s&aacute;ng 5/3. Căn ph&ograve;ng xử &aacute;n rộng th&ecirc;nh thang nhưng chỉ c&oacute; bố mẹ đẻ v&agrave; bố mẹ chồng bị c&aacute;o đến dự.</p>\r\n<p class=\"Normal\">Hiếm c&oacute; phi&ecirc;n t&ograve;a vụ giết người n&agrave;o m&agrave; người nh&agrave; bị c&aacute;o v&agrave; người nh&agrave; nạn nh&acirc;n lại th&acirc;n thiết như vậy. Kh&ocirc;ng c&oacute; cảnh s&aacute;t dẫn giải, cũng kh&ocirc;ng c&oacute; luật sư b&agrave;o chữa, chủ tọa phi&ecirc;n t&ograve;a v&agrave; vị đại diện Viện kiểm s&aacute;t cũng d&ugrave;ng những lời lẽ nhẹ nh&agrave;ng khi thẩm vấn bị c&aacute;o. Nguyễn Gi&aacute;ng Hương, 36 tuổi được tại ngoại trong thời gian chờ t&ograve;a xử &aacute;n.</p>\r\n<p class=\"Normal\">Từng d&ograve;ng nước mắt nhạt nh&ograve;a rơi tr&ecirc;n khu&ocirc;n mặt của người đ&agrave;n b&agrave; gần tứ tuần. Hương nấc l&ecirc;n từng hồi khi nghe vị đại diện viện kiểm s&aacute;t c&ocirc;ng bố c&aacute;o trạng truy tố c&ocirc; về tội danh giết người trong trạng th&aacute;i tinh thần bị k&iacute;ch động mạnh.</p>\r\n<p class=\"Normal\">Lấy nhau từ năm 1995, Hương v&agrave; anh L&ecirc; Khắc Lợi đ&atilde; c&oacute; với nhau hai mặt con đủ nếp đủ tẻ. Cuộc sống của đ&ocirc;i vợ chồng c&oacute; lẽ sẽ y&ecirc;n ấm v&igrave; Hương l&agrave; một người đ&agrave;n b&agrave; chịu kh&oacute;, biết lo toan cho cuộc sống gia đ&igrave;nh. Hai vợ chồng mở một đại l&yacute; b&aacute;n b&aacute;o. H&agrave;ng ng&agrave;y, từ tờ mờ s&aacute;ng, vợ chồng c&ocirc; đi đưa b&aacute;o cho c&aacute;c sạp tr&ecirc;n địa b&agrave;n H&agrave; Nội.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/bicao.jpg\" border=\"1\" alt=\"Bị c&aacute;o Nguyễn Gi&aacute;ng Hương tại t&ograve;a. Ảnh: Anh Thư\" width=\"350\" height=\"480\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bị c&aacute;o Nguyễn Gi&aacute;ng Hương tại t&ograve;a. Ảnh:&nbsp;<em>Anh Thư</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Nhưng đồng tiền nhọc nhằn kiếm được, chồng c&ocirc; lại nướng v&agrave;o c&aacute;c canh bạc v&agrave; những cơn say triền mi&ecirc;n. Thương 2 đứa con thơ dại, sợ điều tiếng l&aacute;ng giềng, Hương nhiều lần \"đ&oacute;ng cửa\" khuy&ecirc;n can chồng nhưng anh ta vẫn chứng n&agrave;o tật nấy. Cuộc sống của hai vợ chồng c&agrave;ng th&ecirc;m căng thẳng.</p>\r\n<p class=\"Normal\">S&aacute;ng 30/6/2009, trong chuếnh cho&aacute;ng hơi men, anh Lợi t&igrave;m về nh&agrave; v&agrave; y&ecirc;u cầu Hương đưa tiền để chiều lấy b&aacute;o. Người vợ đ&atilde; cự lại. C&ocirc; hỏi số tiền thu về h&ocirc;m qua nhưng anh n&oacute;i đ&atilde; cho bạn vay hết. Vợ chồng bắt đầu c&atilde;i nhau. Anh Lợi chửi bới v&agrave; dọa đập ph&aacute; hết đồ đạc trong nh&agrave;.</p>\r\n<p class=\"Normal\">Phẫn uất v&agrave; bị dồn n&eacute;n l&acirc;u ng&agrave;y, Hương liền lấy con dao gọt hoa quả tr&ecirc;n mặt tủ gần đ&oacute; giơ l&ecirc;n dọa. Sau đ&oacute;, anh Lợi x&ocirc;ng đến vặn tay người vợ l&agrave;m con dao rơi xuống đất.</p>\r\n<p class=\"Normal\">Trong cơn cuồng nộ, người chồng lấy chiếc m&aacute;y t&iacute;nh x&aacute;ch tay tr&ecirc;n b&agrave;n đập xuống nền nh&agrave; đẩy cuộc c&atilde;i v&atilde; l&ecirc;n đến tột đỉnh. Hương v&ugrave;ng dậy, nhặt con dao chĩa về ph&iacute;a chồng v&agrave; g&agrave;o l&ecirc;n trong nỗi bức x&uacute;c tột c&ugrave;ng: \"M&agrave;y c&oacute; để cho mẹ con tao y&ecirc;n kh&ocirc;ng?\".</p>\r\n<p class=\"Normal\">Anh Lợi tiếp tục x&ocirc; đến, nh&aacute;t dao oan nghiệt trong tầm tay người vợ đ&acirc;m tr&uacute;ng v&agrave;o ph&iacute;a ngực tr&aacute;i. Hương b&agrave;ng ho&agrave;ng l&ugrave;i lại, luống cuống r&uacute;t con dao ra khỏi ngực chồng. Anh Lợi d&ugrave;ng tay bịt chặt vết thương đang xối m&aacute;u v&agrave; khuỵu xuống.</p>\r\n<p class=\"Normal\">Nghe tiếng hai vợ chồng x&ocirc; x&aacute;t, bố chồng Hương ở dưới nh&agrave; chạy l&ecirc;n, hốt hoảng tri h&ocirc; người đưa đi cấp cứu. Anh Lợi tử vong tr&ecirc;n đường đến bệnh viện do mất qu&aacute; nhiều m&aacute;u.</p>\r\n<p class=\"Normal\">&Ocirc;m nỗi &acirc;n hận, gi&agrave;y v&ograve;, Hương xin lỗi bố mẹ chồng rồi ra đầu th&uacute; tại cơ quan điều tra.</p>\r\n<p class=\"Normal\">Trước t&ograve;a, vợ chồng &ocirc;ng Phong (bố mẹ chồng Hương) một mực xin t&ograve;a &aacute;n mở lượng khoan hồng đối với bị c&aacute;o. &Ocirc;ng n&oacute;i trong sự x&uacute;c động: \"Gần 15 năm l&agrave;m d&acirc;u nh&agrave; t&ocirc;i, Hương l&agrave; đứa ngoan ngo&atilde;n, kh&ocirc;ng g&acirc;y ra điều tiếng g&igrave;. N&oacute; lấy phải người chồng cờ bạc, rượu ch&egrave;, đ&atilde; vất vả lắm rồi. Lỗi cũng do con trai t&ocirc;i...\".</p>\r\n<p class=\"Normal\">Được n&oacute;i lời sau c&ugrave;ng, Hương nức nở trong nước mắt, xin lỗi bố mẹ chồng, v&agrave; xin t&ograve;a mở lượng khoan hồng để Hương được nu&ocirc;i con nhỏ v&agrave; phụng dưỡng bố mẹ chồng.</p>\r\n<p class=\"Normal\">T&ograve;a nghị &aacute;n kh&aacute; l&acirc;u. Hương ngồi thẫn thờ trước v&agrave;nh m&oacute;ng ngựa, &aacute;nh mắt c&ocirc; xa xăm, chờ đợi, chất chứa đầy nỗi đau. Tương lai của gia đ&igrave;nh c&ocirc; kh&ocirc;ng biết sẽ đi về đ&acirc;u khi giờ đ&acirc;y, người đ&agrave;n &ocirc;ng trụ cột trong gia đ&igrave;nh đ&atilde; mất, bố mẹ chồng c&ocirc; cũng đ&atilde; gi&agrave; cả, lại c&ograve;n hai đứa con thơ dại...</p>\r\n<p class=\"Normal\">Sau khi xem x&eacute;t, HĐXX tuy&ecirc;n phạt Hương 3 năm t&ugrave; treo. Hương l&uacute;i h&uacute;i bước theo hai người mẹ đang kho&aacute;c vai nhau, vui mừng v&igrave; mức &aacute;n.</p>\r\n<p class=\"Normal\">Nắng v&agrave;ng trải ngập khắp s&acirc;n t&ograve;a.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Anh Thư</strong></p>', 26, 6, 1655, 0, 1),
(775, 'Những phiên tòa đẫm nước mắt của các ông Tây', 'Nhung-Phien-Toa-Dam-Nuoc-Mat-Cua-Cac-Ong-Tay', 'Tống tiền bạn chát bằng clip khỏa thân, đóng giả thương gia chiếm đoạt tiền ngân hàng, cướp giật để lấy tiền mua cơm... Trước vành móng ngựa, các bị cáo người nước ngoài khóc như đứa trẻ.', 'Bai (8).jpg', NULL, 22, '<p class=\"Title\">Những phi&ecirc;n t&ograve;a đẫm nước mắt của c&aacute;c &ocirc;ng T&acirc;y</p>\r\n<p class=\"Lead\">Tống tiền bạn ch&aacute;t bằng clip khỏa th&acirc;n, đ&oacute;ng giả thương gia chiếm đoạt tiền ng&acirc;n h&agrave;ng, cướp giật để lấy tiền mua cơm... Trước v&agrave;nh m&oacute;ng ngựa, c&aacute;c bị c&aacute;o người nước ngo&agrave;i kh&oacute;c như đứa trẻ.</p>\r\n<p class=\"Normal\">Một ng&agrave;y cuối năm, lẫn trong đ&aacute;m phạm nh&acirc;n bước xuống từ chiếc xe b&iacute;t b&ugrave;ng l&agrave; một người đ&agrave;n &ocirc;ng ngoại quốc với d&aacute;ng vẻ bảnh bao. Mặc những &aacute;nh mắt t&ograve; m&ograve; đang đổ dồn v&agrave;o m&igrave;nh, mặc sự hối th&uacute;c của cảnh s&aacute;t dẫn giải, Hussain Ajmal (28 tuổi, quốc tịch Pakistan) vẫn quay quắt kiếm t&igrave;m trong đ&aacute;m người lố nhố dưới s&acirc;n t&ograve;a rộng lớn. Cuối c&ugrave;ng, đ&ocirc;i mắt buồn b&atilde; của người n&agrave;y dừng lại tại g&oacute;c khuất của bậc tam cấp dẫn l&ecirc;n sảnh ch&iacute;nh, nơi một c&ocirc; g&aacute;i nhỏ nhắn đứng &ocirc;m mặt kh&oacute;c từ l&uacute;c n&agrave;o. C&ocirc; l&agrave; vợ của Ajmal.</p>\r\n<p class=\"Normal\">Suốt thời gian kiểm s&aacute;t vi&ecirc;n c&ocirc;ng bố c&aacute;o trạng, rồi đến lượt người phi&ecirc;n dịch dịch lại bản buộc tội, Ajmal li&ecirc;n tục quay đầu nh&igrave;n vợ đầy đau khổ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/92/99/Bai.jpg\" border=\"1\" alt=\"\" width=\"300\" height=\"400\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hussain Ajmal gục xuống v&agrave;nh m&oacute;ng ngựa van xin được tha tội. Ảnh:&nbsp;<em>Vũ Mai.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo VKS, sau khi tốt nghiệp đại học, Hussain Ajmal sang Việt Nam l&agrave;m việc v&agrave; kết h&ocirc;n với một c&ocirc; g&aacute;i Việt rất xinh xắn. Tuy nhi&ecirc;n, do c&oacute; lối sống ph&oacute;ng t&uacute;ng, người n&agrave;y thường hay l&ecirc;n mạng Internet chat v&agrave; l&agrave;m quen với những c&ocirc; g&aacute;i kh&aacute;c, trong đ&oacute; c&oacute; Nhung.</p>\r\n<p class=\"Normal\">Sau một thời gian ngắn &ldquo;lả lơi&rdquo;, Nhung tin lời &ldquo;người đ&agrave;n &ocirc;ng đẹp trai, chưa vợ&rdquo; n&agrave;y n&ecirc;n đ&atilde; c&oacute; t&igrave;nh cảm thật sự. Do vậy, khi Ajmal n&oacute;i những lời đường mật v&agrave; tha thiết mong được nh&igrave;n thấy th&acirc;n thể &ldquo;người y&ecirc;u&rdquo; qua webcam, Nhung đồng &yacute; m&agrave; kh&ocirc;ng biết người n&agrave;y đ&atilde; lưu to&agrave;n bộ h&igrave;nh ảnh &ldquo;n&oacute;ng&rdquo; của m&igrave;nh v&agrave;o m&aacute;y t&iacute;nh.</p>\r\n<p class=\"Normal\">Cuối th&aacute;ng 4/09, Ajmal nhập cảnh v&agrave;o Việt Nam v&agrave; hẹn Nhung ra gặp mặt. Sau một l&uacute;c n&oacute;i chuyện, người đ&agrave;n &ocirc;ng đ&ograve;i được &ldquo;y&ecirc;u&rdquo; nhưng c&ocirc; từ chối. V&agrave;i lần &ldquo;đ&ograve;i&rdquo; kh&ocirc;ng được, Ajmal tức giận tuy&ecirc;n bố sẽ tung to&agrave;n bộ c&aacute;c clip khỏa th&acirc;n của Nhung l&ecirc;n mạng v&agrave; gửi cho gia đ&igrave;nh c&ocirc; biết.</p>\r\n<p class=\"Normal\">Hoảng sợ trước lời hăm dọa của Ajmal, Nhung đồng &yacute; sẽ &ldquo;chiều&rdquo; người n&agrave;y nhưng b&iacute; mật b&aacute;o c&ocirc;ng an. Chiều ng&agrave;y 3/6/2009, gặp nhau tại qu&aacute;n c&agrave; ph&ecirc;, Ajmal bất ngờ thay đổi y&ecirc;u cầu, buộc Nhung phải đưa 500 USD thay cho việc &ldquo;l&ecirc;n giường&rdquo;. Khi người n&agrave;y đang nhận tiền của nạn nh&acirc;n th&igrave; bị c&ocirc;ng an bắt quả tang.</p>\r\n<p class=\"Normal\">Tr&igrave;nh b&agrave;y với t&ograve;a, Ajmal khổ sở c&ocirc;ng nhận h&agrave;nh vi phạm tội của m&igrave;nh nhưng cho rằng đ&oacute; l&agrave; tiền m&igrave;nh đ&atilde; chuyển v&agrave;o t&agrave;i khoản của Nhung trước đ&oacute;, rằng đ&oacute; l&agrave; &ldquo;ph&iacute;&rdquo; để được xem Nhung khỏa th&acirc;n qua webcam trong những lần chat sex.</p>\r\n<p class=\"Normal\">&ldquo;Khi t&ocirc;i kh&ocirc;ng tiếp tục chuyển tiền nữa th&igrave; c&ocirc; ta quay sang chửi rủa t&ocirc;i thậm tệ. V&igrave; tức giận n&ecirc;n t&ocirc;i mới h&agrave;nh động như thế. T&ocirc;i kh&ocirc;ng biết như vậy l&agrave; vi phạm ph&aacute;p luật Việt Nam, xin h&atilde;y tha cho t&ocirc;i&hellip;&rdquo;, người đ&agrave;n &ocirc;ng bật kh&oacute;c nức nở, chắp tay van xin HĐXX.</p>\r\n<p class=\"Normal\">Được ph&eacute;p gặp vợ trong giờ nghị &aacute;n ngắn ngủi, Ajmal vẫn kh&oacute;c như một đứa trẻ v&agrave; kh&ocirc;ng ngớt van xin: &ldquo;Kh&ocirc;ng g&igrave; c&oacute; thể thay đổi được t&igrave;nh y&ecirc;u của anh d&agrave;nh cho em. H&atilde;y tha lỗi cho anh&hellip;&rdquo;. C&ograve;n người vợ, c&ocirc; ngồi như h&oacute;a đ&aacute;, chỉ c&oacute; những d&ograve;ng nước mắt l&agrave; kh&ocirc;ng ngừng tu&ocirc;n chảy tr&ecirc;n gương mặt hốc h&aacute;c, xanh xao.</p>\r\n<p class=\"Normal\">H&ocirc;m đ&oacute;, TAND TP HCM đ&atilde; tuy&ecirc;n phạt Hussain Ajmal 1 năm t&ugrave; về tội &ldquo;cưỡng đoạt t&agrave;i sản&rdquo;.</p>\r\n<p class=\"Normal\"><span style=\"color: #4f4f4f;\"><strong>Đ&oacute;i qu&aacute; h&oacute;a liều</strong></span></p>\r\n<p class=\"Normal\">S&aacute;ng ng&agrave;y 25/9/2009, ph&ograve;ng xử B của TAND TP HCM khiến nhiều người giật m&igrave;nh lao tới bởi tiếng kh&oacute;c tu tu của một thanh ni&ecirc;n.</p>\r\n<p class=\"Normal\">Trước v&agrave;nh m&oacute;ng ngựa, George Obed (19 tuổi, quốc tịch Nigeria) đang quỳ mọp dưới đất, hai tay v&aacute;i như tế sao, c&ograve;n miệng th&igrave; kh&ocirc;ng ngừng g&agrave;o kh&oacute;c. Mặc vị chủ tọa ra hiệu đứng dậy, người n&agrave;y vẫn li&ecirc;n tục van xin: &ldquo;Nh&agrave; t&ocirc;i rất ngh&egrave;o, theo bạn qua Việt Nam bu&ocirc;n b&aacute;n quần &aacute;o kiếm tiền sinh sống chứ kh&ocirc;ng phải để cướp giật. Nhưng l&agrave;m ăn thất bại, kh&ocirc;ng c&oacute; tiền ăn cơm, đ&oacute;i qu&aacute; mới l&agrave;m liều. Xin đừng bỏ t&ugrave; t&ocirc;i&rdquo;.</p>\r\n<p class=\"Normal\">Theo c&aacute;o trạng, rạng s&aacute;ng ng&agrave;y 27/11/2008, George Obed c&ugrave;ng đồng hương l&agrave; Leonard Chigbogu (26 tuổi) đang đi tr&ecirc;n đường th&igrave; ph&aacute;t hiện một b&aacute;c t&agrave;i xe &ocirc;m chở kh&aacute;ch l&agrave; người Canada. Thấy b&agrave; kh&aacute;ch c&oacute; đeo một t&uacute;i x&aacute;ch, cả hai nảy sinh &yacute; định cướp giật t&agrave;i sản n&ecirc;n b&aacute;m theo.</p>\r\n<p class=\"Normal\">Đến đường Đề Th&aacute;m (quận 1), Leonard liền điều khiển xe &eacute;p s&aacute;t để George ngồi ph&iacute;a sau giật t&uacute;i của nạn nh&acirc;n. Nhưng do b&agrave; n&agrave;y qu&agrave;ng d&acirc;y qua cổ n&ecirc;n George kh&ocirc;ng giật được m&agrave; c&ograve;n l&agrave;m cho cả hai xe ng&atilde; xuống đường.</p>\r\n<p class=\"Normal\">Nghe tiếng tri h&ocirc;, người d&acirc;n tại khu vực đ&atilde; đuổi theo bắt được George, ri&ecirc;ng Leonard chạy tho&aacute;t, sau đ&oacute; nhanh ch&oacute;ng rời khỏi Việt Nam.</p>\r\n<p class=\"Normal\">D&ugrave; chấp nhận sự ăn năn hối cải của bị c&aacute;o ngoại quốc n&agrave;y nhưng v&igrave; h&agrave;nh vi phạm tội nguy hiểm, HĐXX đ&atilde; phạt George Obed mức &aacute;n 2 năm t&ugrave; về tội &ldquo;cướp giật t&agrave;i sản&rdquo;.</p>\r\n<p class=\"Normal\">&lsquo;<span style=\"color: #4f4f4f;\"><strong>Thương gia&rsquo; Ph&aacute;p đến Việt Nam g&acirc;y &aacute;n</strong></span></p>\r\n<p class=\"Normal\">Giữa năm 2009, đứng trước v&agrave;nh m&oacute;ng ngựa l&agrave; người đ&agrave;n &ocirc;ng da m&agrave;u cao lớn mang t&ecirc;n Nzinga Dongard Jean (35 tuổi, quốc tịch Ph&aacute;p). D&ugrave; chẳng hiểu chủ tọa n&oacute;i g&igrave; khi c&ocirc;ng bố quyền của bị c&aacute;o tại phi&ecirc;n x&eacute;t xử, nhưng &ocirc;ng n&agrave;y chỉ chực kh&oacute;c. Vẻ sợ h&atilde;i hiện r&otilde; trong &aacute;nh mắt cầu cứu của Jean khi li&ecirc;n tục hướng về ph&iacute;a vị l&atilde;nh sự.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A1/92/99/bai%202.JPG\" border=\"1\" alt=\"\" width=\"400\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nzinga Dongard Jean vẫn chưa kh&ocirc; nước mắt khi ra xe về trại giam.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&aacute;o trạng thể hiện, ng&agrave;y 26/11/2007, chi nh&aacute;nh Valmy của tập đo&agrave;n Ng&acirc;n h&agrave;ng Societe Generale tại Ph&aacute;p nhận được lệnh chuyển 50.000 Euro cho &ldquo;thương gia&rdquo; Debreu Stanislas tại Ng&acirc;n h&agrave;ng Ph&aacute;t triển v&agrave; đầu tư Việt Nam (quận 1). Sau khi kiểm tra mọi thủ tục, ph&iacute;a Ph&aacute;p đ&atilde; thực hiện giao dịch v&agrave; Debreu Stanislas đ&atilde; nhận đủ số tiền tr&ecirc;n.</p>\r\n<p class=\"Normal\">V&agrave;i ng&agrave;y sau, tập đo&agrave;n ng&acirc;n h&agrave;ng n&agrave;y tiếp tục nhận th&ecirc;m lệnh chuyển 79.000 Euro cho vị &ldquo;thương gia&rdquo; kia nhưng chủ t&agrave;i khoản chuyển tiền ph&aacute;t hiện đ&atilde; bị giả nh&acirc;n th&acirc;n, chữ k&yacute;. Do vậy, ph&iacute;a Ph&aacute;p y&ecirc;u cầu c&ocirc;ng an Việt Nam v&agrave;o cuộc điều tra. Ng&agrave;y 7/12/2007, khi Debreu Stanislas đến Ng&acirc;n h&agrave;ng ngoại thương Việt Nam (quận 10) l&agrave;m thủ tục r&uacute;t 79.000 Euro th&igrave; bị cảnh s&aacute;t VN bắt giữ.</p>\r\n<p class=\"Normal\">Tại cơ quan điều tra, người n&agrave;y khai t&ecirc;n thật l&agrave; Nzinga Dongard Jean (35 tuổi, quốc tịch Ph&aacute;p). Trước đ&oacute;, khi sống ở Campuchia, Jean c&oacute; quen một người Parkistan v&agrave; được thu&ecirc; l&agrave;m giả passport mang t&ecirc;n &ldquo;thương gia&rdquo; Debreu Stanislas để đi r&uacute;t 50.000 Euro tại Việt Nam với tiền c&ocirc;ng 1.500 USD. Thấy c&ocirc;ng việc qu&aacute; nh&agrave;n m&agrave; lại c&oacute; tiền ti&ecirc;u x&agrave;i, Jean nhận l&agrave;m th&ecirc;m lần nữa th&igrave; bị ph&aacute;t hiện.</p>\r\n<p class=\"Normal\">&ldquo;V&igrave; muốn c&oacute; tiền trang trải cuộc sống n&ecirc;n t&ocirc;i mới l&agrave;m như thế. Họ n&oacute;i, lấy tiền của ng&acirc;n h&agrave;ng Ph&aacute;p th&igrave; kh&ocirc;ng vi phạm ph&aacute;p luật Việt Nam. T&ocirc;i kh&ocirc;ng biết tội m&igrave;nh nặng như thế n&agrave;y, xin h&atilde;y khoan hồng cho t&ocirc;i&hellip;&rdquo;, Jean kh&oacute;c ngất khi biết m&igrave;nh bị đề nghị mức &aacute;n cao.</p>\r\n<p class=\"Normal\">H&ocirc;m ấy, TAND TP HCM đ&atilde; tuy&ecirc;n phạt người n&agrave;y mức &aacute;n 12 năm t&ugrave; về tội &ldquo;lừa đảo chiếm đoạt t&agrave;i sản&rdquo;.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Vũ Mai</strong></p>', 26, 6, 2210, 0, 1),
(800, 'Độc đáo môn vật tự do kiểu Pakistan', 'Doc-Dao-Mon-Vat-Tu-Do-Kieu-Pakistan', 'Đeo trên cổ những vòng đá nặng hàng chục kg khi tập luyện, vùi mình trong bùn đất lúc thi đấu, môn võ kushti thu hút đông đảo người Pakistan tham gia suốt hàng nghìn năm qua. Ảnh trên AFP.', '1 (4)_1.jpg', NULL, 22, '<p class=\"Title\">Độc đ&aacute;o m&ocirc;n vật tự do kiểu Pakistan</p>\r\n<p class=\"Lead\">Đeo tr&ecirc;n cổ những v&ograve;ng đ&aacute; nặng h&agrave;ng chục kg khi tập luyện, v&ugrave;i m&igrave;nh trong b&ugrave;n đất l&uacute;c thi đấu, m&ocirc;n v&otilde; kushti thu h&uacute;t đ&ocirc;ng đảo người Pakistan tham gia suốt h&agrave;ng ngh&igrave;n năm qua. Ảnh tr&ecirc;n&nbsp;<em>AFP</em>.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/1.jpg\" alt=\"\" width=\"490\" height=\"349\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Kushti l&agrave; m&ocirc;n vật tự do theo kiểu Pakistan rất phổ biến ở quốc gia Nam &Aacute; n&agrave;y. M&ocirc;n v&otilde; cổ truyền n&agrave;y đ&atilde; c&oacute; từ h&agrave;ng ngh&igrave;n năm qua v&agrave; được xem l&agrave; một m&ocirc;n thể thao đại ch&uacute;ng của Pakistan.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/15.jpg\" alt=\"\" width=\"490\" height=\"389\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nơi tập luyện v&agrave; thi đấu truyền thống của c&aacute;c đ&ocirc; vật kushti chỉ đơn giản l&agrave; một khoảnh s&acirc;n bằng đất. Trong ảnh l&agrave; một sới vật nhỏ ở Lahore, Pakistan.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/3.jpg\" alt=\"\" width=\"490\" height=\"344\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một đ&ocirc; vật lấy đất phủ l&ecirc;n người trước khi tập luyện. Đặc trưng của kushti l&agrave; c&aacute;c đ&ocirc; vật phải thi đấu trong t&igrave;nh trạng lấm lem b&ugrave;n đất.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/10.jpg\" alt=\"\" width=\"490\" height=\"361\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c đ&ocirc; vật tự t&iacute;ch lũy thể lực theo nhiều c&aacute;ch từ đơn giản như n&acirc;ng tạ con hay cuốc đất để l&agrave;m tơi xốp khoảnh đất d&ugrave;ng l&agrave;m sới...</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/17.jpg\" alt=\"\" width=\"490\" height=\"348\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">... đến chạy bộ, trồng c&acirc;y chuối</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/4.jpg\" alt=\"\" width=\"490\" height=\"380\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">... hay phức tạp hơn ch&uacute;t với việc vật lộn để đeo l&ecirc;n cổ một chiếc v&ograve;ng bằng đ&aacute; nặng trịch rồi ngồi thiền nhằm luyện sức dẻo dai.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/9.jpg\" alt=\"\" width=\"490\" height=\"326\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">K&eacute;o một kh&uacute;c gỗ nặng để đ&aacute;nh dấu phần sới h&igrave;nh vu&ocirc;ng d&agrave;nh cho thi đấu cũng l&agrave; một c&aacute;ch tập luyện của nhiều đ&ocirc; vật.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/18.jpg\" alt=\"\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/19.jpg\" alt=\"\" width=\"490\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&aacute;c thể v&otilde; của kushti kết hợp khả năng xoay sở kh&eacute;o l&eacute;o với sức mạnh cơ bắp.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/6.jpg\" alt=\"\" width=\"490\" height=\"318\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Thời xa xưa, c&aacute;c thế v&otilde; của kushti được người Pakistan &aacute;p dụng v&agrave;o c&aacute;c cuộc chiến chống giặc ngoại x&acirc;m, bảo vệ đất nước.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/5.jpg\" alt=\"\" width=\"490\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một đ&ocirc; vật được người c&ugrave;ng tập m&aacute;t-xa sau khi thi đấu.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/1D/4D/12.jpg\" alt=\"\" width=\"490\" height=\"376\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Từ m&ocirc;n thể thao c&oacute; t&iacute;nh chất d&acirc;n d&atilde; n&agrave;y, Pakistan đ&atilde; giới thiệu ra thế giới nhiều đ&ocirc; vật cừ kh&ocirc;i m&agrave; mới nhất l&agrave; Azhar Hussain (hạng c&acirc;n 55 kg) v&agrave; Muhammad Inam (hạng 84 kg), hai vận động vi&ecirc;n vừa đoạt HC v&agrave;ng vật tự do ở Đại hội thể thao khối Thịnh vượng chung 2010 tại Delhi, Ấn Độ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Kha</strong></p>', 24, 5, 1442, 0, 1),
(802, 'Tiệc ánh sáng ở Lễ khai mạc Đại hội khối Thịnh vượng', 'Tiec-Anh-Sang-O-Le-Khai-Mac-Dai-Hoi-Khoi-Thinh-Vuong', 'Gạt sang một bên nhiều chỉ trích về công tác chuẩn bị, nước chủ nhà Ấn Độ đã tổ chức một đêm khai mạc sống động cho Đại hội thể thao khối Thịnh vượng chung - Commonweath Games 2010 ở Delhi hôm qua. Ảnh trên AFP.', '17.jpg', NULL, 22, '<p class=\"Title\">Tiệc &aacute;nh s&aacute;ng ở Lễ khai mạc Đại hội khối Thịnh vượng</p>\r\n<p class=\"Lead\">Gạt sang một b&ecirc;n nhiều chỉ tr&iacute;ch về&nbsp;c&ocirc;ng t&aacute;c chuẩn bị, nước chủ nh&agrave; Ấn Độ đ&atilde; tổ chức một đ&ecirc;m khai mạc sống động cho Đại hội thể thao khối Thịnh vượng chung - Commonweath Games 2010 ở Delhi h&ocirc;m qua. Ảnh tr&ecirc;n&nbsp;<em>AFP</em>.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/12/22/17.jpg\" alt=\"\" width=\"490\" height=\"358\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ph&aacute;o hoa rực s&aacute;ng b&ecirc;n ngo&agrave;i s&acirc;n Jawaharlal Nehru - s&acirc;n vận động ch&iacute;nh v&agrave; l&agrave; nơi tổ chức lễ khai mạc, bế mạc của Đại hội. 7000 vận động vi&ecirc;n từ 71 quốc gia v&ugrave;ng l&atilde;nh thổ sẽ tranh t&agrave;i ở 17 m&ocirc;n thể thao trong 11 ng&agrave;y diễn ra đại hội.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/12/22/8.jpg\" alt=\"\" width=\"490\" height=\"326\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/12/22/11.jpg\" alt=\"\" width=\"341\" height=\"497\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">B&ecirc;n trong s&acirc;n, ban tổ chức cho treo một quả khinh kh&iacute; cầu để tạo điểm nhấn độc đ&aacute;o cho bữa tiệc &aacute;nh s&aacute;ng mừng ng&agrave;y Đại hội khai mạc.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/12/22/9.jpg\" alt=\"\" width=\"490\" height=\"322\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Quả cầu như một m&agrave;n h&igrave;nh khổng lồ phản chiếu c&aacute;c hoạt động lễ hội b&ecirc;n dưới.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/12/22/18.jpg\" alt=\"\" width=\"490\" height=\"372\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/12/22/000T_TRDV838625.jpg\" alt=\"\" width=\"341\" height=\"512\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những danh lam thắng cảnh như đền Taj Mahal v&agrave; h&igrave;nh tượng một nh&agrave; tu h&agrave;nh ngồi thiền tr&ecirc;n đ&agrave;i sen được dựng l&ecirc;n bằng &aacute;nh s&aacute;ng lazer.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/12/22/1.jpg\" alt=\"\" width=\"490\" height=\"326\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bollywood - kinh đ&ocirc; điện ảnh Ấn Độ - được nước chủ nh&agrave; giới thiệu với niềm tự h&agrave;o qua một tấm pa-n&ocirc; khổ lớn.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/12/22/6.jpg\" alt=\"\" width=\"490\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"middle\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/12/22/12.jpg\" alt=\"\" width=\"341\" height=\"512\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Văn h&oacute;a d&acirc;n gian của Ấn Độ được c&aacute;c nghệ sĩ tr&igrave;nh diễn sinh động qua giai điệu v&agrave; những m&agrave;n m&uacute;a đặc sắc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Kha</strong></p>', 24, 5, 2998, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(803, '\'Tiến sĩ búa thép\' khoe người tình nhỏ ở Lễ hội bia Đức', '', 'Võ sĩ quyền anh hạng nặng Wladimir Klitschko và cô bạn gái nhỏ nhắn Hayden Panettiere là khách mời danh dự trong bữa tiệc do cựu tay vợt Boris Becker tổ chức nhân dịp khai mạc lễ hội bia Oktoberfest ở Munich, Đức.', '1 (6).jpg', NULL, 22, '<p class=\"Title\">\'Tiến sĩ b&uacute;a th&eacute;p\' khoe người t&igrave;nh nhỏ ở Lễ hội bia Đức</p>\r\n<p class=\"Lead\">V&otilde; sĩ quyền anh hạng nặng Wladimir Klitschko v&agrave; c&ocirc; bạn g&aacute;i nhỏ nhắn Hayden Panettiere l&agrave; kh&aacute;ch mời danh dự trong bữa tiệc do cựu tay vợt Boris Becker tổ chức nh&acirc;n dịp khai mạc lễ hội bia Oktoberfest ở Munich, Đức.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"100%\">\r\n<tbody>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0E/BE/1.jpg\" alt=\"\" width=\"490\" height=\"628\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">V&otilde; sĩ người Ukraine v&agrave; giai nh&acirc;n Hollywood s&oacute;ng đ&ocirc;i đến dự tiệc.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0E/BE/5.jpg\" alt=\"\" width=\"490\" height=\"379\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Phu nh&acirc;n của Becker, Lilly Kerssenberg tiếp chuyện Hayden.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0E/BE/8.jpg\" alt=\"\" width=\"490\" height=\"655\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sao nữ Hollywood cười đ&ugrave;a với chủ nh&acirc;n của buổi tiệc.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0E/BE/9.jpg\" alt=\"\" width=\"490\" height=\"647\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Wladimir v&agrave; Hayden được v&iacute; như một đ&ocirc;i đũa lệch của l&agrave;ng thể thao - giải tr&iacute;. V&otilde; sĩ 34 tuổi c&acirc;n nặng gấp đ&ocirc;i v&agrave; cao hơn người t&igrave;nh b&eacute; nhỏ những 0,46 m&eacute;t.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0E/BE/2.jpg\" alt=\"\" width=\"490\" height=\"650\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hayden bắt đầu hẹn h&ograve; với Wladimir từ cuối năm 2009, sau khi c&ocirc; chia tay bạn trai cũ cũng l&agrave; diễn vi&ecirc;n, Milo Ventimigilia. Họ sau đ&oacute; lu&ocirc;n quấn qu&yacute;t b&ecirc;n nhau mỗi khi c&oacute; thời gian rỗi.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0E/BE/3.jpg\" alt=\"\" width=\"490\" height=\"601\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Mỹ nh&acirc;n Hollywood trong bộ v&aacute;y truyền thống của xứ Bavaria.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0E/BE/11.jpg\" alt=\"\" width=\"490\" height=\"581\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Người đẹp vừa bước qua tuổi 21.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0E/BE/6.jpg\" alt=\"\" width=\"490\" height=\"393\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Oktoberfest l&agrave; lễ hội bia truyền thống diễn ra trong khoảng 16 đến 18 ng&agrave;y từ cuối th&aacute;ng 9 sang đầu th&aacute;ng 10 ở bang Bavaria, Đức. Người tham dự được thưởng thức những loại bia hảo hạng nhất v&agrave; đắm ch&igrave;m trong kh&ocirc;ng gian văn h&oacute;a truyền thống của tiểu bang n&agrave;y.</td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\"><img src=\"http://vnexpress.net/Files/Subject/3B/A2/0E/BE/4.jpg\" alt=\"\" width=\"490\" height=\"464\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hayden nhiệt t&igrave;nh tham gia nhảy m&uacute;a trong bữa tiệc.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Phương Minh</strong></p>', 24, 5, 3, 0, 1),
(939, 'Federer thoát hiểm ở vòng hai', 'Federer-Thoat-Hiem-O-Vong-Hai', 'Nhà đương kim vô địch đơn nam đã gặp rất nhiều khó khăn trong trận đấu tối thứ tư tại giải quần vợt Australia mở rộng, khi anh vượt qua Gilles Simon với tỷ số 6-2, 6-3, 4-6, 4-6, 6-3.', 'federer (3).jpg', NULL, 22, '<h1 class=\"Title\">Federer tho&aacute;t hiểm ở v&ograve;ng hai</h1>\r\n<h2 class=\"Lead\">Nh&agrave; đương kim v&ocirc; địch đơn nam đ&atilde; gặp rất nhiều kh&oacute; khăn trong trận đấu tối thứ tư tại giải quần vợt Australia mở rộng, khi anh vượt qua Gilles Simon với tỷ số 6-2, 6-3, 4-6, 4-6, 6-3.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/federer.jpg\" border=\"1\" alt=\"Federer đ&atilde; gặp thử th&aacute;ch lớn đầu ti&ecirc;n tr&ecirc;n đường chinh phục danh hiệu đơn nam Australia mở rộng thứ tư trong sự nghiệp.\" width=\"400\" height=\"277\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Federer đ&atilde; gặp thử th&aacute;ch lớn đầu ti&ecirc;n tr&ecirc;n đường chinh phục danh hiệu đơn nam Australia mở rộng thứ năm trong sự nghiệp.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Sau chiến thắng kh&aacute; dễ trong hai set đầu, Federer đột nhi&ecirc;n đ&aacute;nh mất sự chủ động v&agrave;o tay Simon. V&agrave; tay vợt người Ph&aacute;p đ&atilde; tranh thủ cơ hội san bằng tỷ số. Set cuối diễn ra căng thẳng đến mức Federer thường tỏ ra phấn kh&iacute;ch, vung nắm đấm v&agrave;o kh&ocirc;ng kh&iacute; sau mỗi lần ăn điểm. Khi vượt l&ecirc;n ở game thứ s&aacute;u trong set n&agrave;y, Federer n&acirc;ng tỷ số l&ecirc;n 4-2, trước khi đi đến chiến thắng cuối c&ugrave;ng.</p>\r\n<p class=\"Normal\">Thất bại tối thứ tư đ&atilde; khiến Simon bỏ lỡ cơ hội buộc Federer bị loại lần đầu ti&ecirc;n trước v&ograve;ng ba tại một giải Grand Slam kể từ Ph&aacute;p mở rộng 2003 (31 giải li&ecirc;n tục). Trong hai lần gặp nhau trước, Simon đều thắng Federer.</p>\r\n<p class=\"Normal\">\"Thật kh&ocirc;ng dễ g&igrave; khi Simon chơi với quyết t&acirc;m đ&aacute;nh bại t&ocirc;i. R&otilde; r&agrave;ng anh ấy l&agrave; một trong những tay vợt h&agrave;ng đầu thế giới\", Federer ph&aacute;t biểu sau chiến thắng. \"T&ocirc;i cảm thấy m&igrave;nh thật may mắn. Hy vọng ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng phải gặp nhau nữa&rdquo;.</p>\r\n<p class=\"Normal\">Ở c&aacute;c trận v&ograve;ng hai kh&aacute;c, c&aacute;c hạt giống mạnh cũng t&igrave;m được c&aacute;ch đi tiếp. Hạt giống số ba Novak Djokovic gi&agrave;nh chiến thắng 7-5, 6-7 (10), 6-0, 6-2 trước Ivan Dodig.</p>\r\n<p class=\"Normal\">Djokovic tỏ ra nhỉnh hơn nhưng Dodig chống đỡ rất hiệu quả trong hai set đầu bằng những quả giao b&oacute;ng v&agrave; những c&uacute; đ&aacute;nh cuối s&acirc;n. Trong hai set sau, hạt giống số ba t&igrave;m được c&aacute;ch h&oacute;a giải lối đ&aacute;nh của đối phương, kết hợp những pha l&ecirc;n lưới v&ocirc;l&ecirc; với những c&uacute; đ&aacute;nh về hai g&oacute;c s&acirc;n để lgi&agrave;nh điểm.</p>\r\n<p class=\"Normal\">Ở v&ograve;ng ba, Djokovic sẽ gặp đồng đội ở đội tuyển Serbia, Viktor Troicki. Hạt giống số 29 Troicki đ&atilde; loại Nicolas Mahut với tỷ số 6-4, 6-2, 1-6, 6-3.</p>\r\n<p class=\"Normal\">&Aacute; qu&acirc;n Wimbledon năm ngo&aacute;i, Tomas Berdych phải cần tới 4 quả match-point để đ&aacute;nh bại Philipp Kohlschreiber 4-6, 6-2, 6-3, 6-4. Hạt giống số t&aacute;m Andy Roddick đ&atilde; thực hiện th&agrave;nh c&ocirc;ng 17 c&uacute; ace để thắng 7-6, 6-2, 6-3 trước Igor Kunitsyn. Lần thứ 9 li&ecirc;n tục cựu số một thế giới Roddick lọt v&agrave;o v&ograve;ng ba Australia mở rộng.</p>\r\n<p class=\"Normal\">Đồng đội của Roddick ở đội tuyển Mỹ, Mardy Fish đ&atilde; phải rời cuộc chơi sớm khi chịu thua 1-6, 6-3, 6-3, 6-3 trước Tommy Robredo.</p>\r\n<p class=\"Normal\">Hạt giống số 9 Fernando Verdasco đ&atilde; xuất sắc lội ngược d&ograve;ng để đ&aacute;nh bại Janko Tipsarevic với tỷ số 2-6, 4-6, 6-4, 7-6, 6-0. Verdasco chỉ mất 1 điểm trong c&aacute;c game cầm giao b&oacute;ng của anh ở set cuối.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Th&uacute;y An</strong></p>', 22, 5, 1091, 0, 1),
(940, 'Kim Clijsters tiếp tục chứng tỏ uy lực', 'Kim-Clijsters-Tiep-Tuc-Chung-To-Uy-Luc', 'Tay vợt nữ hạt giống số ba Clijsters càng lúc càng thể hiện được sức mạnh của ứng viên sáng giá ở giải Australia Mở rộng 2011. Trong khi đó, Jelena Jankovic bất ngờ phải dừng bước.', 'kim (1).jpg', NULL, 22, '<h1 class=\"Title\">Kim Clijsters tiếp tục chứng tỏ uy lực</h1>\r\n<h2 class=\"Lead\">Tay vợt nữ hạt giống số ba Clijsters c&agrave;ng l&uacute;c c&agrave;ng thể hiện được sức mạnh của ứng vi&ecirc;n s&aacute;ng gi&aacute; ở giải Australia Mở rộng 2011. Trong khi đ&oacute;, Jelena Jankovic bất ngờ phải dừng bước.</h2>\r\n<p class=\"Normal\">Ở v&ograve;ng hai diễn ra h&ocirc;m qua, Clijsters lại gi&agrave;nh chiến thắng thuyết phục chỉ sau hai set với tỷ số 6-1 v&agrave; 6-3 khi gặp Carla Suarez Navarro.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/C1/kim.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"304\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Clijsters đang c&oacute; một phong độ đ&aacute;ng nể.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Sau khi ăn điểm trong game giao b&oacute;ng đầu ti&ecirc;n, Suarez Navarro ho&agrave;n to&agrave;n đ&aacute;nh mất thế trận. Tay vợt số 62 thế giới kh&ocirc;ng thể chống đỡ trước những quả giao b&oacute;ng v&agrave; những c&uacute; đ&aacute;nh thuận tay uy lực nhắm v&agrave;o c&aacute;c g&oacute;c s&acirc;n của Clijsters. Set đầu ti&ecirc;n diễn ra chỉ trong 22 ph&uacute;t.</p>\r\n<p class=\"Normal\">Trong set thứ hai, Navarro nỗ lực hơn v&agrave; c&oacute; l&uacute;c đ&atilde; thu hẹp được khoảng c&aacute;ch xuống 3-4. Nhưng sau đ&oacute; c&ocirc; vẫn phải chịu thua Clijsters - tay vợt đ&atilde; hạ nh&agrave; cựu số một thế giới Dinara Safina với c&aacute;c tỷ số ấn tượng 6-0 v&agrave; 6-0 ở v&ograve;ng một.</p>\r\n<p class=\"Normal\">Ở v&ograve;ng 3 Clijsters sẽ gặp Maria Jose Martinez Sanchez hoặc Alize Cornet. Clijsters từng về nh&igrave; ở Australia Mở rộng 2004.</p>\r\n<p class=\"Normal\">Cũng ở v&ograve;ng hai h&ocirc;m qua, phải kh&aacute; mất c&ocirc;ng&nbsp;<strong><span style=\"color: #2f2f2f;\">Venus Williams</span></strong>&nbsp;mới c&oacute; thể đ&aacute;nh bại Sandra Zahlavova với tỷ số 6-7 (6-8), 6-0 v&agrave; 6-4.</p>\r\n<p class=\"Normal\">Venus sớm cảm thấy đau ở ch&acirc;n v&agrave; đ&oacute; c&oacute; thể l&agrave; nguy&ecirc;n nh&acirc;n khiến tay vợt được xếp hạng hạt giống số 4 thất bại ở set đầu ti&ecirc;n. C&ocirc; sau đ&oacute; đ&atilde; phải nhờ đến sự gi&uacute;p đỡ của bộ phận y tế, v&agrave; trở lại trận đấu với c&aacute;i ch&acirc;n phải được băng b&oacute;.</p>\r\n<p class=\"Normal\">Bị đau nhưng Venus sau đ&oacute; vẫn cố gắng v&agrave; gi&agrave;nh chiến thắng trong hai set c&ograve;n lại.</p>\r\n<p class=\"Normal\">Nh&agrave; v&ocirc; địch Australia Mở rộng 2008,&nbsp;<strong><span style=\"color: #2f2f2f;\">Maria Sharapova</span></strong>, cũng đ&atilde; gi&agrave;nh suất v&agrave;o v&ograve;ng 3 sau khi đ&aacute;nh bại Virginie Razzano 7-6 (7-3) v&agrave; 6-3.</p>\r\n<p class=\"Normal\">Tuy nhi&ecirc;n th&ecirc;m một bất ngờ lớn đ&atilde; xảy đến với nội dung d&agrave;nh cho nữ, v&agrave; một lần nữa đ&oacute; l&agrave; nỗi buồn mang t&ecirc;n Serbia. Một ng&agrave;y sau khi Ana Ivanovic bị loại, h&ocirc;m qua đến lượt&nbsp;<span style=\"color: #2f2f2f;\"><strong>Jelena Jankovic</strong></span><span style=\"color: #000000;\">&nbsp;phải sớm n&oacute;i lời chia tay giải.</span></p>\r\n<p class=\"Normal\"><span style=\"color: #000000;\">Tay vợt hạt giống số 7 bị Shuai Peng của Trung Quốc đ&aacute;nh bại với c&aacute;c tỷ số 7-6 (7-3) v&agrave; 6-3.</span></p>\r\n<p class=\"Normal\">Jankovic c&oacute; lợi thế dẫn trước 5-2 trong set đầu ti&ecirc;n, nhưng đ&atilde; kh&ocirc;ng biết ph&aacute;t huy. C&ocirc; để tay vợt xếp hạng 54 thế giới gỡ h&ograve;a 6-6 rồi gi&agrave;nh chiến thắng trong loạt tie-break. Trong set thứ hai Jankovic - từng lọt v&agrave;o b&aacute;n kết năm 2008 - thể hiện k&eacute;m hơn v&agrave; nhanh ch&oacute;ng chịu g&aacute;c vợt.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>H&agrave; Uy&ecirc;n</strong></p>', 22, 5, 811, 0, 1),
(941, 'Nguyễn Ánh 9 khóc khi đệm đàn cho Ý Lan', 'Nguyen-Anh-9-Khoc-Khi-Dem-Dan-Cho-Y-Lan', 'Nhạc sĩ \'Cô đơn\' xúc động khi nghe con gái nữ danh ca Thái Thanh hát bài nhạc tình \'Người đi qua đời tôi\' tối 16/2. 40 năm trước, hằng đêm ông vẫn đệm đàn cho Thái Thanh hát ở phòng trà.', 'y-lan-8.jpg', NULL, 22, '<div class=\"folder-header\">\r\n<div class=\"parentfolder-title fl\"><span style=\"font-size: 20px; font-weight: bold;\">Nguyễn &Aacute;nh 9 kh&oacute;c khi đệm đ&agrave;n cho &Yacute; Lan</span></div>\r\n</div>\r\n<div class=\"content\">\r\n<div>\r\n<h2 class=\"Lead\">Nhạc sĩ \'C&ocirc; đơn\' x&uacute;c động khi nghe con g&aacute;i nữ danh ca Th&aacute;i Thanh h&aacute;t b&agrave;i nhạc t&igrave;nh \'Người đi qua đời t&ocirc;i\' tối 16/2. 40 năm trước, hằng đ&ecirc;m &ocirc;ng vẫn đệm đ&agrave;n cho Th&aacute;i Thanh h&aacute;t ở ph&ograve;ng tr&agrave;.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/67/ff/y-lan-8.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"362\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\">Nhạc sĩ Nguyễn &Aacute;nh 9 l&agrave; vị kh&aacute;ch mời đặc biệt trong đ&ecirc;m nhạc \"Khung trời &Yacute; Lan\", diễn ra tối 16/2, tại ph&ograve;ng tr&agrave; Đồng Dao, TP HCM. Đ&ecirc;m nhạc do doanh nh&acirc;n Dương Quốc Nam của thương hiệu Phố Xinh tổ chức tặng ca sĩ &Yacute; Lan nh&acirc;n dịp chị về qu&ecirc; ăn Tết.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"left\">\r\n<tbody>\r\n<tr>\r\n<td class=\"BreakLine\">&nbsp;</td>\r\n</tr>\r\n<tr>\r\n<td>&nbsp;</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/67/ff/y-lan-6.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"369\" /></td>\r\n</tr>\r\n<tr>\r\n<td>\r\n<p class=\"Normal\">&Yacute; Lan tr&acirc;n trọng mời nhạc sĩ l&ecirc;n đệm đ&agrave;n cho chị h&aacute;t. B&agrave;i h&aacute;t&nbsp;<em>Người đi qua đời t&ocirc;i&nbsp;</em>với giọng h&aacute;t khắc khoải c&ugrave;ng từng nốt nhạc du dương của hai người tạo n&ecirc;n kh&ocirc;ng gian đầy cảm x&uacute;c của đ&ecirc;m nhạc.</p>\r\n<p class=\"Normal\">Sau khi đệm xong ca kh&uacute;c, nhạc sĩ Nguyễn &Aacute;nh kh&ocirc;ng n&eacute;n nổi x&uacute;c động, rơi nước mắt v&agrave; chia sẻ: \"C&aacute;ch đ&acirc;y 40 năm t&ocirc;i đ&atilde; đệm đ&agrave;n cho Th&aacute;i Thanh h&aacute;t ca kh&uacute;c n&agrave;y, giờ đ&acirc;y t&ocirc;i đệm đ&agrave;n cho con g&aacute;i c&ocirc; ấy h&aacute;t. Đ&oacute; l&agrave; một mối duy&ecirc;n đẹp m&agrave; t&ocirc;i c&oacute; được trong &acirc;m nhạc. &Yacute; Lan đ&atilde; gi&uacute;p t&ocirc;i sống lại những k&yacute; ức thời trẻ\".</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/67/ff/y-lan-4.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"537\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\">Ca sĩ &Yacute; Lan cũng x&uacute;c động kh&ocirc;ng k&eacute;m v&agrave; b&agrave;y tỏ niềm cảm ơn s&acirc;u sắc đến người nhạc sĩ đ&atilde; gắn b&oacute; với cả hai mẹ con c&ocirc;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/67/ff/y-lan-12.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"391\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\">Xuất hiện trong đ&ecirc;m nhạc của &Yacute; Lan c&ograve;n c&oacute; người chồng m&agrave; chị lu&ocirc;n miệng gọi th&acirc;n thiết l&agrave; \"anh Tuấn\". Anh Tuấn lu&ocirc;n song h&agrave;nh c&ugrave;ng chị trong c&aacute;c chuyến lưu diễn bởi theo anh, kh&ocirc;ng g&igrave; vui bằng nh&igrave;n thấy vợ m&igrave;nh được sống trong niềm hạnh ph&uacute;c tr&ecirc;n s&acirc;n khấu với những t&igrave;nh cảm nồng hậu từ kh&aacute;n giả.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/67/ff/y-lan-10.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"359\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\">Cả hai d&agrave;nh tặng kh&aacute;n giả tiết mục song ca&nbsp;<em>Ngậm ng&ugrave;i</em>&nbsp;v&agrave;&nbsp;<em>C&ograve;n ch&uacute;t g&igrave; để nhớ</em>. Người nghe v&ocirc; c&ugrave;ng ngỡ ng&agrave;ng v&agrave; th&iacute;ch th&uacute; trước sự kết hợp đầy ăn &yacute; của cặp vợ chồng n&agrave;y.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/67/ff/y-lan-7.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"569\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\">Trong đ&ecirc;m nhạc, &Yacute; Lan h&aacute;t lại những ca kh&uacute;c được nhiều kh&aacute;n giả y&ecirc;u mến trong suốt 20 năm ca h&aacute;t của c&ocirc;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/67/ff/y-lan-11.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"543\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\">Nhiều người trầm trồ bởi vẻ đẹp vượt thời gian của &Yacute; Lan.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<br />\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Dung L&acirc;m</strong><br /><em>Ảnh</em>:&nbsp;<strong>Huy T&acirc;n</strong></p>\r\n</div>\r\n</div>', 17, 4, 1108, 0, 1),
(1180, '\'Đường Sơn đại địa chấn\' trượt đề cử Oscar', '', 'Bộ phim gây xúc động của đạo diễn Phùng Tiểu Cương đại diện cho điện ảnh Trung Quốc đi Oscar năm nay đã không lọt vào danh sách rút gọn của giải \'Phim nước ngoài hay nhất\'. ', '1101664804.jpg', NULL, 22, '<h1 class=\"Title\">\'Đường Sơn đại địa chấn\' trượt đề cử Oscar</h1>\r\n<h2 class=\"Lead\">Bộ phim g&acirc;y x&uacute;c động của đạo diễn Ph&ugrave;ng Tiểu Cương đại diện cho điện ảnh Trung Quốc đi Oscar năm nay đ&atilde; kh&ocirc;ng lọt v&agrave;o danh s&aacute;ch r&uacute;t gọn của giải \'Phim nước ngo&agrave;i hay nhất\'.&nbsp;<br /><span style=\"font-size: 10px; font-weight: normal;\">Viện H&agrave;n l&acirc;m khoa học v&agrave; nghệ thuật điện ảnh Mỹ vừa c&ocirc;ng bố t&ecirc;n của 9 bộ phim truyện nước ngo&agrave;i lọt v&agrave;o danh s&aacute;ch r&uacute;t gọn tranh giải&nbsp;<em>Phim nước ngo&agrave;i hay nhất</em>tại Oscar năm nay.&nbsp;<em>Đường Sơn đại địa chấn</em>&nbsp;- bộ phim từng g&acirc;y cơn sốt ph&ograve;ng v&eacute; tại nhiều nước ch&acirc;u &Aacute; năm ngo&aacute;i v&agrave; được kỳ vọng sẽ đem về vinh quang cho điện ảnh Trung Quốc tại Oscar - kh&ocirc;ng c&oacute; t&ecirc;n trong danh s&aacute;ch n&agrave;y.</span></h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/FF/1101664804.jpg\" border=\"1\" alt=\"Một cảnh trong phim \'Kokuhaku (Confessions)\' của điện ảnh Nhật Bản.\" width=\"490\" height=\"326\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một cảnh trong phim \'Kokuhaku (Confessions)\' của điện ảnh Nhật Bản.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Bộ phim Nhật Bản&nbsp;<em>Kokuhaku (Confessions)&nbsp;</em>l&agrave; đại diện duy nhất của điện ảnh ch&acirc;u &Aacute; lọt v&agrave;o danh s&aacute;ch r&uacute;t gọn. Nh&agrave; sản xuất Genki Kawamura ph&aacute;t biểu: \"Ch&uacute;ng t&ocirc;i rất hạnh ph&uacute;c khi được l&agrave; một trong 9 phim được lựa chọn. Tuy nhi&ecirc;n, ch&uacute;ng t&ocirc;i đều hy vọng v&agrave; đang cầu nguyện phim sẽ đi xa hơn nữa l&agrave; lọt v&agrave;o danh s&aacute;ch cuối c&ugrave;ng 5 t&aacute;c phẩm tranh giải&nbsp;<em>Phim truyện nước ngo&agrave;i hay nhất</em>\".</p>\r\n<p class=\"Normal\"><em>In A Better World</em>&nbsp;- t&aacute;c phẩm của điện ảnh Đan Mạch vừa gi&agrave;nh giải&nbsp;<em>Phim n&oacute;i tiếng nước ngo&agrave;i hay nhất</em>&nbsp;tại&nbsp;Quả cầu v&agrave;ng&nbsp;h&ocirc;m 16/1 - cũng l&agrave; một trong số 9 c&aacute;i t&ecirc;n được lựa chọn.&nbsp;<em>Biutiful</em>&nbsp;- bộ phim được đ&aacute;nh gi&aacute; cao của Mexico v&agrave; T&acirc;y Ban Nha c&oacute; sự tham gia của t&agrave;i tử Javier Bardem - hiện l&agrave; ứng vi&ecirc;n đ&aacute;ng gờm nhất cho danh hiệu n&agrave;y.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/FF/biutiful-movie.jpg\" border=\"1\" alt=\"\" width=\"490\" height=\"375\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\"Biutiful\" (Mexico / T&acirc;y Ban Nha) đang l&agrave; ứng cử vi&ecirc;n nặng k&yacute; nhất cho giải \"Phim nước ngo&agrave;i hay nhất\" tại Oscar năm nay.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Trong số c&aacute;c hạng mục của Oscar h&agrave;ng năm,&nbsp;<em>Phim nước ngo&agrave;i hay nhất</em>&nbsp;lu&ocirc;n l&agrave; giải g&acirc;y ra nhiều tranh c&atilde;i nhất v&igrave; c&aacute;c quy tắc phức tạp về thời gian ph&aacute;t h&agrave;nh, tr&igrave;nh chiếu trong nước cũng như thể lệ tham dự v&agrave; lựa chọn. Việt Nam từng gửi&nbsp;<em>&Aacute;o lụa H&agrave; Đ&ocirc;ng,&nbsp;M&ugrave;a len tr&acirc;u,&nbsp;Chuyện của Pao</em>&nbsp;v&agrave;&nbsp;<em>Đừng đốt</em>&nbsp;đi Oscar tranh giải n&agrave;y nhưng đều kh&ocirc;ng lọt v&agrave;o v&ograve;ng 5 đề cử cuối c&ugrave;ng.</p>\r\n<p class=\"Normal\">Hai c&aacute;i t&ecirc;n đ&igrave;nh đ&aacute;m của điện ảnh ch&acirc;u &Aacute; trong năm qua v&agrave; cũng được kh&aacute;n giả đặt nhiều hy vọng nhiều nhất l&agrave;&nbsp;<em>Đường Sơn đại địa chấn</em>&nbsp;(Trung Quốc) v&agrave;&nbsp;<em>Uncle Boonmee Who Can Recall His Past Lives</em>&nbsp;(Th&aacute;i Lan) - phim gi&agrave;nh giải C&agrave;nh cọ v&agrave;ng của LHP Quốc tế Cannes m&ugrave;a h&egrave; năm ngo&aacute;i - đều kh&ocirc;ng c&oacute; mặt trong danh s&aacute;ch r&uacute;t gọn.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/FF/1234.jpg\" border=\"1\" alt=\"\" width=\"490\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">\"Uncle Boonmee Who Can Recall His Past Lives\" (Th&aacute;i Lan) v&agrave; \"Đường Sơn đại địa chấn\" (Trung Quốc) kh&ocirc;ng c&oacute; mặt trong danh s&aacute;ch r&uacute;t gọn.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">T&ecirc;n của 5 đề cử cuối c&ugrave;ng cho giải&nbsp;<em>Phim nước ngo&agrave;i hay nhất</em>&nbsp;sẽ được c&ocirc;ng bố v&agrave;o tuần tới. Năm ngo&aacute;i, bộ phim&nbsp;<em>El Secreto de sus Ojos (The Secret in Their Eyes)</em>&nbsp;của điện ảnh Argentina đ&atilde; gi&agrave;nh chiến thắng ở hạng mục n&agrave;y.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"480\" align=\"center\" bgcolor=\"#cfcfcf\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class=\"Normal\">Danh s&aacute;ch r&uacute;t gọn của giải Phim nước ngo&agrave;i hay nhất tại Oscar năm nay:</p>\r\n<p class=\"Normal\">-&nbsp;<em>Outside The Law</em>&nbsp;(Algeria)<br />-&nbsp;<em>Incendies&nbsp;</em>(Canada)<br />-&nbsp;<em>In A Better World</em>&nbsp;(Đan Mạch)<br />-&nbsp;<em>Biutiful</em>&nbsp;(Mexico / T&acirc;y Ban Nha)<br />-&nbsp;<em>Dogtooth</em>&nbsp;(Hy Lạp)<br />-&nbsp;<em>Confessions</em>&nbsp;(Nhật Bản)<br />-&nbsp;<em>Life Above All</em>&nbsp;(Nam Phi)<br />-&nbsp;<em>Even The Rain</em>&nbsp;(T&acirc;y Ban Nha)<br />-&nbsp;<em>Simple Simon</em>&nbsp;(Thụy Điển)</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguy&ecirc;n Minh</strong></p>', 19, 4, 3, 0, 1),
(1181, 'Anne Hathaway trở thành \'miêu nữ\' trong \'Batman 3\'', '', 'Ngôi sao phim \'Yêu và tình dược\' được chọn vào vai phản diện Catwoman trong phim \'The Dark Knight Rises\' của đạo diễn Christopher Nolan. Cô cũng vừa nhận một vai trong phim truyền hình \'Glee\'.', 'page.jpg', NULL, 22, '<h1 class=\"Title\">Anne Hathaway trở th&agrave;nh \'mi&ecirc;u nữ\' trong \'Batman 3\'</h1>\r\n<h2 class=\"Lead\">Ng&ocirc;i sao phim \'Y&ecirc;u v&agrave; t&igrave;nh dược\' được chọn v&agrave;o vai phản diện Catwoman trong phim \'The Dark Knight Rises\' của đạo diễn Christopher Nolan. C&ocirc; cũng vừa nhận một vai trong phim truyền h&igrave;nh \'Glee\'.<br /><br /></h2>\r\n<p class=\"Normal\">S&aacute;ng 19/1, h&atilde;ng Warner Bros. c&ocirc;ng bố diễn vi&ecirc;n sẽ thể hiện vai phản diện Catwoman trong phần tiếp theo của&nbsp;<em>Người dơi</em>. Anne Hathaway đ&atilde; vượt qua h&agrave;ng loạt ng&ocirc;i sao hạng A kh&aacute;c l&agrave; Keira Knightley, Rachel Weisz, Naomi Watts v&agrave; cả Natalie Portman để gi&agrave;nh quyền kho&aacute;c l&ecirc;n m&igrave;nh bộ đồ da m&agrave;u đen b&oacute; s&aacute;t nổi tiếng của nh&acirc;n vật Selina Kyle - Mi&ecirc;u nữ.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/page.jpg\" border=\"1\" alt=\"Vai Mi&ecirc;u nữ từng được hai minh tinh Michelle Pfeiffer v&agrave; Halle Berry thể hiện rất ấn tượng.\" width=\"490\" height=\"375\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Vai Mi&ecirc;u nữ từng được hai minh tinh Michelle Pfeiffer v&agrave; Halle Berry thể hiện rất ấn tượng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">H&atilde;ng cũng tiết lộ rằng t&agrave;i tử&nbsp;<em>Inception</em>&nbsp;- Tom Hardy - sẽ thể hiện vai Bane, nh&acirc;n vật phản diện thứ hai trong&nbsp;<em>The Dark Knight Rises</em>. Tuy nhi&ecirc;n, nhiều lời đồn cho rằng c&ograve;n c&oacute; một nh&acirc;n vật phản diện nữa l&agrave; Riddler. Đạo diễn Christopher Nolan vẫn chưa x&aacute;c nhận Riddler v&agrave; Bane l&agrave; một hay Riddler sẽ l&agrave; nh&acirc;n vật phản diện thứ ba trong phần n&agrave;y.</p>\r\n<p class=\"Normal\">Sắp tới, Anne Hathaway cũng sẽ xuất hiện với một vai kh&aacute;ch mời trong&nbsp;<em>Glee</em>&nbsp;- bộ phim truyền h&igrave;nh ca nhạc nổi tiếng của h&atilde;ng Fox đ&atilde; hai năm li&ecirc;n tiếp gi&agrave;nh Quả cầu v&agrave;ng. Nữ diễn vi&ecirc;n 28 tuổi từng b&agrave;y tỏ hy vọng được đ&oacute;ng&nbsp;<em>Glee&nbsp;</em>trong chương tr&igrave;nh truyền h&igrave;nh của MC Jimmy Fallon. Tại lễ trao giải Quả cầu v&agrave;ng vừa diễn ra h&ocirc;m 16/1, nh&agrave; sản xuất của bộ phim l&agrave; Ryan Murphy đ&atilde; ch&iacute;nh thức mời Anne.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/59/AC/wenn3170218.jpg\" border=\"1\" alt=\"Anne Hathaway quyến rũ trong bộ v&aacute;y dạ hội của Giorgio Armani tại lễ trao giải Quả cầu v&agrave;ng h&ocirc;m 16/1. Ảnh: WEN.\" width=\"400\" height=\"600\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Anne Hathaway quyến rũ trong bộ v&aacute;y dạ hội của Giorgio Armani tại lễ trao giải Quả cầu v&agrave;ng h&ocirc;m 16/1. Ảnh:&nbsp;<em>WEN.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">\"C&ocirc; ấy đi ngang qua b&agrave;n ch&uacute;ng t&ocirc;i v&agrave; dừng lại tr&ograve; chuyện. Khi t&ocirc;i hỏi \'C&ocirc; c&oacute; thực sự muốn tham gia kh&ocirc;ng?\', Anne đ&atilde; trả lời: \'T&ocirc;i rất muốn\'. V&agrave; thế l&agrave; t&ocirc;i n&oacute;i \'OK, vậy ch&uacute;ng ta l&agrave;m th&ocirc;i\'. T&ocirc;i sẽ phải n&oacute;i chuyện với Anne về những ca kh&uacute;c c&ocirc; ấy muốn thể hiện, v&agrave; t&ocirc;i chắc rằng Anne đ&atilde; c&oacute; &yacute; tưởng. Sẽ rất tuyệt vời nếu c&ocirc; ấy xuất hiện trong&nbsp;<em>Glee</em>\", Ryan Murphy tiết lộ.</p>\r\n<p class=\"Normal\">Tr&ecirc;n thực tế, Anne Hathaway đ&atilde; định sẵn cho m&igrave;nh &yacute; tưởng về một nh&acirc;n vật trong<em>Glee</em>. Trong b&agrave;i phỏng vấn mới nhất, c&ocirc; t&acirc;m sự muốn được thể hiện vai người d&igrave; của nh&acirc;n vật Kurt bị đồng t&iacute;nh trong phim. Người d&igrave; n&agrave;y cũng l&agrave; người đồng t&iacute;nh nữ (lesbian) v&agrave; sẽ gi&uacute;p đứa ch&aacute;u m&igrave;nh đối mặt với những vấn đề về t&acirc;m l&yacute;.</p>\r\n<p class=\"Normal\">C&ugrave;ng với t&agrave;i tử James Franco, Anne Hathaway sẽ l&agrave; người&nbsp;dẫn chương tr&igrave;nh&nbsp;của lễ trao giải Oscar năm nay sắp diễn ra v&agrave;o ng&agrave;y 25/2.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>N.M</strong></p>', 19, 4, 3, 0, 1),
(1182, '\'The King\'s Speech\' dẫn đầu đề cử \'Oscar\' của Anh', '', 'Bộ phim tâm lý, lịch sử kể về cuộc đời Vua George VI giành được 14 đề cử BAFTA. \'Thiên nga đen\' của Natalie Portman về nhì với 12 đề cử, trong khi đó \'The Social Network\' chỉ được 6 đề cử.', 'the_king_s_speech17.jpg', NULL, 22, '<h1 class=\"Title\">\'The King\'s Speech\' dẫn đầu đề cử \'Oscar\' của Anh</h1>\r\n<h2 class=\"Lead\">Bộ phim t&acirc;m l&yacute;, lịch sử kể về cuộc đời Vua George VI gi&agrave;nh được 14 đề cử BAFTA. \'Thi&ecirc;n nga đen\' của Natalie Portman về nh&igrave; với 12 đề cử, trong khi đ&oacute; \'The Social Network\' chỉ được 6 đề cử.&nbsp;<br /><span style=\"font-size: 10px; font-weight: normal;\">Viện H&agrave;n l&acirc;m điện ảnh v&agrave; truyền h&igrave;nh Anh quốc vừa c&ocirc;ng bố danh s&aacute;ch đề cử giải thưởng&nbsp;<em>BAFTA</em>&nbsp;lần thứ 64. Bộ phim xuất sắc của điện ảnh Anh năm qua -&nbsp;<em>The King\'s Speech</em>&nbsp;- nhận được nhiều đề cử nhất, bao gồm c&aacute;c hạng mục quan trọng như&nbsp;<em>Phim hay nhất, Nam diễn vi&ecirc;n ch&iacute;nh xuất sắc, Kịch bản gốc xuất sắc, Đạo diễn xuất sắc</em>&nbsp;hay&nbsp;<em>Nam - nữ diễn vi&ecirc;n phụ xuất sắc</em>.</span></h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/the_king_s_speech17.jpg\" border=\"1\" alt=\"Một cảnh trong phim \'The King\'s Speech\'.\" width=\"490\" height=\"387\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Một cảnh trong phim \'The King\'s Speech\'.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Bốn trong số năm c&aacute;i t&ecirc;n được đề cử cho giải&nbsp;<em>Phim hay nhất</em>&nbsp;tr&ugrave;ng với hạng mục<em>Phim t&acirc;m l&yacute; hay nhất</em>&nbsp;của Quả cầu v&agrave;ng vừa diễn ra h&ocirc;m 16/1, chỉ c&oacute;&nbsp;<em>The Fighter</em>&nbsp;bị thay thế bằng phim miền T&acirc;y&nbsp;<em>True Grit</em>. Tương tự ở giải&nbsp;<em>Đạo diễn xuất sắc</em>, David O. Russell (phim&nbsp;<em>The Fighter</em>) kh&ocirc;ng g&oacute;p mặt m&agrave; thay v&agrave;o đ&oacute; l&agrave; Danny Boyle (phim&nbsp;<em>127 Hours</em>).</p>\r\n<p class=\"Normal\"><em>The Social Network</em>&nbsp;- bộ phim vừa&nbsp;gi&agrave;nh 4 giải&nbsp;Quả cầu v&agrave;ng - được đề cử ở 6 hạng mục, bao gồm&nbsp;<em>Phim hay nhất, Đạo diễn xuất sắc, Kịch bản chuyển thể xuất sắc, Dựng phim xuất sắc, Nam diễn vi&ecirc;n ch&iacute;nh v&agrave; phụ xuất sắc</em>.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/page.jpg\" border=\"1\" alt=\"Natalie Portman v&agrave; Colin Firth dường như kh&ocirc;ng c&oacute; đối thủ ở hạng mục \'Diễn vi&ecirc;n ch&iacute;nh xuất sắc\'.\" width=\"490\" height=\"370\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Natalie Portman v&agrave; Colin Firth gần như kh&ocirc;ng c&oacute; đối thủ ở hạng mục \'Diễn vi&ecirc;n ch&iacute;nh xuất sắc\'.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Natalie Portman v&agrave; Colin Firth - hai c&aacute;i t&ecirc;n vừa được vinh danh giải&nbsp;<em>Nam v&agrave; nữ diễn vi&ecirc;n ch&iacute;nh xuất sắc</em>&nbsp;tại Quả cầu v&agrave;ng - cũng được ti&ecirc;n đo&aacute;n sẽ gi&agrave;nh chiến thắng ở hạng mục tương tự tại BAFTA năm nay.</p>\r\n<p class=\"Normal\">BAFTA được coi như giải Oscar của Anh. C&ugrave;ng với Quả cầu v&agrave;ng, đ&acirc;y cũng l&agrave; một trong những giải thưởng tiền Oscar uy t&iacute;n nhất. C&aacute;c giải về diễn xuất của BAFTA v&agrave; Oscar thường c&oacute; tỷ lệ tr&ugrave;ng lặp rất lớn. Năm 2006, bốn diễn vi&ecirc;n chiến thắng tại BAFTA sau đ&oacute; cũng gi&agrave;nh được vinh quang tại nh&agrave; h&aacute;t Kodak.</p>\r\n<p class=\"Normal\">Lễ trao giải BAFTA lần thứ 64 sẽ được diễn ra tại Nh&agrave; h&aacute;t Ho&agrave;ng gia ở London (Anh) v&agrave;o ng&agrave;y 13/2 - trước Oscar hai tuần.</p>\r\n<table border=\"1\" cellspacing=\"0\" cellpadding=\"3\" width=\"480\" align=\"center\" bgcolor=\"#cfcfcf\">\r\n<tbody>\r\n<tr>\r\n<td>\r\n<p class=\"Normal\"><strong>* Đề cử giải BAFTA 2011:</strong></p>\r\n<p class=\"Normal\"><em>Phim hay nhất:</em></p>\r\n<p class=\"Normal\">- Black Swan<br />- The King\'s Speech<br />- Inception<br />- True Grit<br />- The Social Network</p>\r\n<p class=\"Normal\"><em>Phim nổi bật của điện ảnh Anh:</em></p>\r\n<p class=\"Normal\">- 127 Hours<br />- Another Year<br />- Four Lions<br />- The King\' Speech<br />- Made in Dagenham</p>\r\n<p class=\"Normal\"><em>Nh&agrave; l&agrave;m phim nổi bật của điện ảnh Anh:</em></p>\r\n<p class=\"Normal\">- Clio Barnard v&agrave; Tracy O\'riordan (đạo diễn, sản xuất phim The Arbor)<br />- Banksy v&agrave; Jaimie D\'cruz (đạo diễn, sản xuất phim Exit Through The Gift Shop)<br />- Chris Morris (đạo diễn, bi&ecirc;n kịch phim Four Lions)<br />- Gareth Edwards (đạo diễn, bi&ecirc;n kịch phim Monsters)<br />- Nick Whitfield (đạo diễn, bi&ecirc;n kịch phim Skeletons)</p>\r\n<p class=\"Normal\"><em>Đạo diễn xuất sắc:</em></p>\r\n<p class=\"Normal\">- Danny Boyle (127 Hours)<br />- Darren Aronofsky (Black Swan)<br />- David Fincher (The Social Network)<br />- Tom Hooper (The King\'s Speech)<br />- Christopher Nolan (Inception)</p>\r\n<p class=\"Normal\"><em>Kịch bản gốc hay nhất:</em></p>\r\n<p class=\"Normal\">- Black Swan<br />- The Fighter<br />- Inception<br />- The Kids Are All Right<br />- The King\'s Speech</p>\r\n<p class=\"Normal\"><em>Kịch bản chuyển thể xuất sắc:</em></p>\r\n<p class=\"Normal\">- 127 Hours<br />- The Girl With The Dragon Tattoo<br />- The Social Network<br />- Toy Story 3<br />- True Grit</p>\r\n<p class=\"Normal\"><em>Phim n&oacute;i tiếng nước ngo&agrave;i hay nhất:</em></p>\r\n<p class=\"Normal\">- Biutiful<br />- The Girl With The Dragon Tattoo<br />- I Am Love<br />- Of Gods and Men<br />- The Secret in Their Eyes</p>\r\n<p class=\"Normal\"><em>Phim hoạt h&igrave;nh hay nhất:</em></p>\r\n<p class=\"Normal\">- Despicable Me<br />- How to Train Your Dragon<br />- Toy Story 3</p>\r\n<p class=\"Normal\"><em>Nam diễn vi&ecirc;n ch&iacute;nh xuất sắc:</em></p>\r\n<p class=\"Normal\">- Javier Bardem (Biutiful)<br />- Colin Firth (The King\'s Speech)<br />- Jeff Bridges (True Grit)<br />- Jesse Eisenberg (The Social Network)<br />- James Franco (127 Hours)</p>\r\n<p class=\"Normal\"><em>Nữ diễn vi&ecirc;n ch&iacute;nh xuất sắc:</em></p>\r\n<p class=\"Normal\">- Natalie Portman (Black Swan)<br />- Annette Bening (The Kids Are All Right)<br />- Julianne Moore (The Kids Are All Right)<br />- Noomi Rapace (The Girl With The Dragon Tattoo)<br />- Hailee Steinfeld (True Grit)</p>\r\n<p class=\"Normal\"><em>Nam diễn vi&ecirc;n phụ xuất sắc:</em></p>\r\n<p class=\"Normal\">- Christian Bale (The Fighter)<br />- Andrew Garfield (The Social Network)<br />- Pete Postlethwaite (The Town)<br />- Mark Ruffalo (The Kids Are All Right)<br />- Geoffrey Rush (The King\'s Speech)</p>\r\n<p class=\"Normal\"><em>Nữ diễn vi&ecirc;n phụ xuất sắc:</em></p>\r\n<p class=\"Normal\">- Amy Adams (The Fighter)<br />- Helena Bonham Carter (The King\'s Speech)<br />- Barbara Hershey (Black Swan)<br />- Miranda Richardson (Made in Dagenham)<br />- Lesley Manville (Another Year)</p>\r\n<p class=\"Normal\"><em>Nhạc phim hay nhất:</em></p>\r\n<p class=\"Normal\">- 127 Hours<br />- Alice in Wonderland<br />- How to Train Your Dragon<br />- The King\'s Speech<br />- Inception</p>\r\n<p class=\"Normal\"><em>Quay phim xuất sắc:</em></p>\r\n<p class=\"Normal\">- 127 Hours<br />- Black Swan<br />- Inception<br />- The King\'s Speech<br />- True Grit</p>\r\n<p class=\"Normal\"><em>Dựng phim xuất sắc:</em></p>\r\n<p class=\"Normal\">- The Social Network<br />- 127 Hours<br />- The King\'s Speech<br />- Black Swan<br />- Inception</p>\r\n<p class=\"Normal\"><em>Thiết kế sản xuất:</em></p>\r\n<p class=\"Normal\">- Alice in Wonderland<br />- Black Swan<br />- Inception<br />- The King\'s Speech<br />- True Grit</p>\r\n<p class=\"Normal\"><em>Thiết kế phục trang:</em></p>\r\n<p class=\"Normal\">- Alice in Wonderland<br />- Black Swan<br />- The King\'s Speech<br />- True Grit<br />- Made in Dagenham</p>\r\n<p class=\"Normal\"><em>&Acirc;m thanh xuất sắc:</em></p>\r\n<p class=\"Normal\">- 127 Hours<br />- Black Swan<br />- Inception<br />- The King\'s Speech<br />- True Grit</p>\r\n<p class=\"Normal\"><em>Hiệu ứng đặc biệt:</em></p>\r\n<p class=\"Normal\">- Alice in Wonderland<br />- Black Swan<br />- Harry Potter and the Deathly Hallows: Part I<br />- Inception<br />- Toy Story 3</p>\r\n<p class=\"Normal\"><em>Trang điểm đẹp nhất:</em></p>\r\n<p class=\"Normal\">- Alice in Wonderland<br />- Black Swan<br />- The King\'s Speech<br />- Harry Potter and the Deathly Hallows: Part I<br />- Made in Dagenham</p>\r\n<p class=\"Normal\"><em>Phim hoạt h&igrave;nh ngắn xuất sắc:</em></p>\r\n<p class=\"Normal\">- The Eagleman<br />- Matter Fisher<br />- Thursday</p>\r\n<p class=\"Normal\"><em>Phim ngắn xuất sắc:</em></p>\r\n<p class=\"Normal\">- Connect<br />- Lin<br />- Rite<br />- Turning<br />- Until The River Runs Red</p>\r\n<p class=\"Normal\"><em>Ng&ocirc;i sao triển vọng:</em></p>\r\n<p class=\"Normal\">- Gemma Arterton<br />- Andrew Garfield<br />- Tom Hardy<br />- Aaron Johnson<br />- Emma Stone</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>N.M.</strong></p>', 19, 4, 3, 0, 1),
(1328, 'Laptop Dell XPS lý tưởng cho giải trí', 'Laptop-Dell-Xps-Ly-Tuong-Cho-Giai-Tri', 'Với Dell XPS, người dùng có thể giải trí với các bộ phim chất lượng cao, thưởng thức những bản nhạc hay và tự thiết kế những tấm thiệp độc đáo.', 'H_400.jpg', NULL, 22, '<h1 class=\"Title\">Laptop Dell XPS l&yacute; tưởng cho giải tr&iacute;</h1>\r\n<h2 class=\"Lead\">Với Dell XPS, người d&ugrave;ng c&oacute; thể giải tr&iacute; với c&aacute;c bộ phim chất lượng cao, thưởng thức những bản nhạc hay v&agrave; tự thiết kế những tấm thiệp độc đ&aacute;o.</h2>\r\n<p class=\"Normal\">Laptop Dell XPS c&oacute; m&agrave;n h&igrave;nh 1080 p, độ ph&acirc;n giải HD c&ugrave;ng với chế độ hiển thị TrueLife chống mỏi mắt gi&uacute;p người d&ugrave;ng c&oacute; thể thưởng thức trọn vẹn bộ phim. Đồng thời, sản phẩm t&iacute;ch hợp bộ vi xử l&yacute; Intel Core i5 processor gi&uacute;p việc tr&igrave;nh chiếu tốt hơn những bộ phim chất lượng cao (6-10 GB/phim). Dell XPS c&ograve;n được t&iacute;ch hợp c&ocirc;ng nghệ 3DTV Play mới, cho ph&eacute;p kết nối với m&agrave;n h&igrave;nh 3D th&ocirc;ng qua cổng HDMI để xem những phi&ecirc;n bản phim 3D hoặc c&aacute;c game 3D.</p>\r\n<p class=\"Normal\">Sản phẩm hỗ trợ hai loa JBL 12 Watt ph&iacute;a tr&ecirc;n b&agrave;n ph&iacute;m (c&aacute;c XPS 15 v&agrave; 17 đều c&oacute; loa si&ecirc;u trầm ở ph&iacute;a dưới). Loa JBL l&agrave; hệ thống loa t&iacute;ch hợp c&ugrave;ng laptop lớn, chủ động khắc phục những hạn chế vốn c&oacute; trong laptop như &iacute;t khả năng t&ugrave;y chỉnh v&agrave; kh&ocirc;ng gian b&ecirc;n trong laptop kh&aacute; chật hẹp để đặt loa, từ đ&oacute; cung cấp &acirc;m thanh chất lượng cao đến người d&ugrave;ng. Loa JBL với phần mềm c&ocirc;ng nghệ s&oacute;ng MaxxAudio sản xuất một số c&aacute;c &acirc;m sắc mang lại nhiều x&uacute;c cảm mạnh mẽ cho người nghe.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/56/48/H_400.jpg\" border=\"1\" alt=\"Dell SPS ph&ugrave; hợp với c&aacute;c nhu cầu giải tr&iacute;.\" width=\"400\" height=\"308\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\" align=\"center\">Dell XPS ph&ugrave; hợp với c&aacute;c nhu cầu giải tr&iacute;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">T&iacute;ch hợp card đồ họa rời Nvidia graphics, Dell XPS cho ph&eacute;p người d&ugrave;ng tự thiết kế một số thiệp th&ocirc;ng dụng như thiệp th&ocirc;ng dụng như Photo Card Maker, Ecard Magic, Cool EasyCard&hellip;Nvidia cho hiệu năng v&agrave; khả năng xử l&yacute; nhanh, m&agrave;u sắc ch&acirc;n thật l&agrave;m cho những đường n&eacute;t phối hợp h&agrave;i h&ograve;a, th&iacute;ch hợp để tạo thiệp điện tử v&agrave; thiệp in&hellip;</p>\r\n<p class=\"Normal\">Dell XPS t&iacute;ch hợp sẵn webcam, kết nối Wi-Fi chuẩn N 802.11n, hỗ trợ băng th&ocirc;ng 100 &ndash; 200 MBps với khả năng phối hợp nhiều dạng kết nối kh&ocirc;ng d&acirc;y sẽ ngăn chặn t&igrave;nh trạng đang tr&ograve; chuyện trực tuyến m&agrave; bị mất kết nối.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Dell</em>)</p>', 37, 9, 2371, 0, 1),
(1329, 'Điện thoại vuông của Motorola giá 7,5 triệu đồng ở VN', 'Dien-Thoai-Vuong-Cua-Motorola-Gia-7,5-Trieu-Dong-O-Vn', 'Smartphone có kiểu dáng xoay độc đáo trong năm 2010, chạy hệ điều hành Android vừa xuất hiện ở Hà Nội.', 'Motorola_1.jpg', NULL, 22, '<h1 class=\"Title\">Điện thoại vu&ocirc;ng của Motorola gi&aacute; 7,5 triệu đồng ở VN</h1>\r\n<h2 class=\"Lead\">Smartphone c&oacute; kiểu d&aacute;ng xoay độc đ&aacute;o trong năm 2010, chạy hệ điều h&agrave;nh Android vừa xuất hiện ở H&agrave; Nội.</h2>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"208\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Motorola_1.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"252\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Motorola_2.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"252\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" align=\"center\">Hộp của m&aacute;y.</td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Motorola_4.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"252\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" align=\"center\">Phụ kiện kh&aacute; đơn giản bao gồm c&aacute;p sạc v&agrave; tai nghe.</td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Motorola_5.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"252\" /></td>\r\n</tr>\r\n<tr>\r\n<td>M&aacute;y c&oacute; thiết kế nhỏ k&iacute;ch cỡ 67 x 67 x 17 mm, nằm gọn trong l&ograve;ng b&agrave;n tay.</td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Motorola_6.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"252\" /></td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Motorola_7.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"252\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Normal\" align=\"center\">Mặt sau c&oacute; camera 3,2 megapixel hỗ trợ đ&egrave;n flash LED.</td>\r\n</tr>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Motorola_10.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"252\" /></td>\r\n</tr>\r\n<tr>\r\n<td>Nh&igrave;n ngang tr&ocirc;ng m&aacute;y kh&aacute; d&agrave;y. Mặt trước l&agrave; m&agrave;n h&igrave;nh cảm ứng 2,8 inch 265 ngh&igrave;n m&agrave;u, độ ph&acirc;n giải 320 x 240 pixel.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Thế Mạnh</strong></p>', 37, 9, 2997, 0, 1),
(1330, 'Microsoft cho ra mắt công cụ phát triển web miễn phí', 'Microsoft-Cho-Ra-Mat-Cong-Cu-Phat-Trien-Web-Mien-Phi', 'WebMatrix là công cụ giúp người sử dụng và các nhà phát triển web ở mọi cấp độ dễ dàng kiến tạo, chỉnh sửa và xuất bản trang web.', 'web-1.jpg', NULL, 22, '<h1 class=\"Title\">Microsoft cho ra mắt c&ocirc;ng cụ ph&aacute;t triển web miễn ph&iacute;</h1>\r\n<h2 class=\"Lead\">WebMatrix l&agrave; c&ocirc;ng cụ gi&uacute;p người sử dụng v&agrave; c&aacute;c nh&agrave; ph&aacute;t triển web ở mọi cấp độ dễ d&agrave;ng kiến tạo, chỉnh sửa v&agrave; xuất bản trang web.</h2>\r\n<p class=\"Normal\">Microsoft cũng x&acirc;y dựng bộ gi&aacute;o tr&igrave;nh hướng dẫn bằng video k&egrave;m thủ thuật v&agrave; c&aacute;c th&ocirc;ng tin hỗ trợ kh&aacute;c tại địa chỉ&nbsp;<em>microsoft.com/web/webmatrix/learn</em>. D&ugrave; l&agrave; sinh vi&ecirc;n, chuy&ecirc;n vi&ecirc;n trẻ hay c&ocirc;ng ty chuy&ecirc;n về web, WebMatrix mang đến phương thức hiệu quả để x&acirc;y dựng website m&atilde; nguồn mở với những c&ocirc;ng cụ cần thiết để vận h&agrave;nh như m&aacute;y chủ web, cơ sở dữ liệu, định khung cho trang cũng như c&ocirc;ng cụ t&igrave;m kiếm tối ưu h&oacute;a.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"139\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3b/a2/56/4c/web-1.jpg\" border=\"1\" alt=\"\" width=\"356\" height=\"285\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">S. Somasegar, Ph&oacute; chủ tịch cao cấp thuộc bộ phận lập tr&igrave;nh tại Microsoft, chia sẻ: \"Nền tảng web của ch&uacute;ng t&ocirc;i cung cấp hệ sinh th&aacute;i ho&agrave;n hảo gồm sản phẩm, đối t&aacute;c v&agrave; c&ocirc;ng nghệ nhằm gi&uacute;p chuy&ecirc;n gia lập tr&igrave;nh th&agrave;nh c&ocirc;ng tr&ecirc;n web. WebMatrix đưa ra giải ph&aacute;p tinh vi m&agrave; lại miễn ph&iacute; v&agrave; dễ sử dụng cho nh&agrave; ph&aacute;t triển web ở mọi tr&igrave;nh độ\".</p>\r\n<p class=\"Normal\">WebMatrix được hỗ trợ bởi hệ thống gần 40 đối t&aacute;c ứng dụng m&atilde; nguồn mở, tiền đề cần thiết cho sự ph&aacute;t triển th&agrave;nh c&ocirc;ng tr&ecirc;n web. &Ocirc;ng Nguyễn Xu&acirc;n Ho&agrave;ng, Tổng gi&aacute;m đốc c&ocirc;ng ty MISA, nhận định: \"Sự th&acirc;n thiện v&agrave; bộ c&ocirc;ng cụ đầy sức mạnh của WebMatrix sẽ gi&uacute;p đỡ v&agrave; tăng năng suất rất nhiều cho c&aacute;c chuy&ecirc;n gia web. T&ocirc;i tin rằng cộng đồng lập tr&igrave;nh tại thị trường Việt Nam sẽ đ&oacute;n nhận bộ c&ocirc;ng cụ n&agrave;y\".</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Ch&acirc;u An</strong></p>', 37, 9, 704, 0, 1),
(1377, 'Đón năm mới tại nhà hàng Dìn Ký', 'Don-Nam-Moi-Tai-Nha-Hang-Din-Ky', 'Từ 18h đến 24h ngày 31/12, nhà hàng hải sản - buffet Dìn Ký sẽ mang đến cho các bạn một đêm giao thừa thật thú vị và đầy ý nghĩa với 100 món ăn được chế biến bởi đầu bếp nổi tiếng và giàu kinh nghiệm', 'b1_1.JPG', NULL, 22, '<h1 class=\"Title\">Đ&oacute;n năm mới tại nh&agrave; h&agrave;ng D&igrave;n K&yacute;</h1>\r\n<h2 class=\"Lead\">Từ 18h đến 24h ng&agrave;y 31/12, nh&agrave; h&agrave;ng hải sản - buffet D&igrave;n K&yacute; sẽ mang đến cho c&aacute;c bạn một đ&ecirc;m giao thừa thật th&uacute; vị v&agrave; đầy &yacute; nghĩa với 100 m&oacute;n ăn được chế biến bởi đầu bếp nổi tiếng v&agrave; gi&agrave;u kinh nghiệm</h2>\r\n<p class=\"Normal\">Tại đ&acirc;y sẽ diễn ra chương tr&igrave;nh ca nhạc - buffet ngo&agrave;i trời &ldquo;Đ&ecirc;m cuồng nhiệt&rdquo; với hơn 100 m&oacute;n ăn như salad c&aacute; ngừ, salad Nga, sanwich trứng c&aacute; hồi, c&agrave;ng cua b&aacute;ch hoa, soup hải sản rong biển, tứ bửu tiềm thuốc bắc, đ&agrave; điểu cuộn thịt x&ocirc;ng kh&oacute;i, b&ograve; hầm rượu vang, cocktail tr&aacute;i c&acirc;y chi&ecirc;n gi&ograve;n v&agrave; nhiều loại hải sản như h&agrave;u sống, c&aacute; hồi, t&ocirc;m ho&agrave;ng kim...</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/b1.JPG\" border=\"1\" alt=\"\" width=\"400\" height=\"267\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/b2.JPG\" border=\"1\" alt=\"\" width=\"400\" height=\"299\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&ecirc;n cạnh đ&oacute;, chương tr&igrave;nh ca nhạc c&ograve;n c&oacute; sự g&oacute;p mặt của ca sĩ Minh Hằng, nh&oacute;m For you, nh&oacute;m APO... v&agrave; s&ocirc;i động với c&aacute;c vũ điệu nhảy m&uacute;a của Hawai, Dance sport, m&uacute;a lửa&hellip; Hơn thế, chương tr&igrave;nh gameshow c&ugrave;ng nhiều giải thưởng v&agrave; qu&agrave; tặng hấp dẫn cũng l&agrave;m cho &ldquo;Đ&ecirc;m cuồng nhiệt&rdquo; trở n&ecirc;n n&oacute;ng bỏng v&agrave; l&ocirc;i cuốn hơn.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/b3.JPG\" border=\"1\" alt=\"\" width=\"300\" height=\"448\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Địa chỉ li&ecirc;n hệ: 142/18 Cộng H&ograve;a, phường 4, quận T&acirc;n B&igrave;nh, TP HCM - Hotline: (08) 38118181.</p>\r\n<p class=\"Normal\">Gi&aacute; v&eacute;: Người lớn: 400.000 đồng; Trẻ em: 200.000 đồng</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Nh&agrave; h&agrave;ng D&igrave;n K&yacute;</em>)</p>', 30, 7, 643, 0, 1);
INSERT INTO `tin` (`idTin`, `TieuDe`, `TieuDe_KhongDau`, `TomTat`, `urlHinh`, `Ngay`, `idUser`, `Content`, `idLT`, `idTL`, `SoLanXem`, `TinNoiBat`, `AnHien`) VALUES
(1378, 'Cơ hội đón năm mới tại Kichi Kichi Singapore', 'Co-Hoi-Don-Nam-Moi-Tai-Kichi-Kichi-Singapore', 'Hành trình khám phá quốc đảo Sư tử xinh đẹp này cùng Kichi Kichi mang đến rất nhiều phần quà thú vị và cơ hội đón năm mới tại Singapore - mệnh danh là “chiếc đĩa vàng của ẩm thực Sing” năm 2010.', 'a1 (2).jpg', NULL, 22, '<h1 class=\"Title\">Cơ hội đ&oacute;n năm mới tại Kichi Kichi Singapore</h1>\r\n<h2 class=\"Lead\">H&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; quốc đảo Sư tử xinh đẹp n&agrave;y c&ugrave;ng Kichi Kichi mang đến rất nhiều phần qu&agrave; th&uacute; vị v&agrave; cơ hội đ&oacute;n năm mới tại Singapore - mệnh danh l&agrave; &ldquo;chiếc đĩa v&agrave;ng của ẩm thực Sing&rdquo; năm 2010.</h2>\r\n<p class=\"Normal\">Đến chuỗi Kichi Kichi trong thời gian từ ng&agrave;y 5/11 đến 10/2/2011, thực kh&aacute;ch sẽ được tặng ngay những chiếc v&eacute; c&agrave;o hấp dẫn, trong đ&oacute; chứa những phần qu&agrave; th&uacute; vị.</p>\r\n<p class=\"Normal\">Những phần thưởng được tặng ngay tại nh&agrave; h&agrave;ng l&agrave; những: Bộ cốc xinh xắn, những chiếc &aacute;o ph&ocirc;ng thật &ldquo;Kool&rdquo; v&agrave; h&agrave;ng ngh&igrave;n ly Pepsi m&aacute;t lạnh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/a1.jpg\" border=\"1\" alt=\"\" width=\"241\" height=\"450\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">B&ecirc;n cạnh đ&oacute;, thực kh&aacute;ch sẽ kh&ocirc;ng khỏi ngỡ ng&agrave;ng khi bill thanh to&aacute;n của cả nh&oacute;m lại &iacute;t tới vậy. Những ly Pepsi đến từ chương tr&igrave;nh ưu đ&atilde;i đặc biệt đ&atilde; gi&uacute;p nh&oacute;m tiết kiệm được một khoản chi kha kh&aacute; để d&agrave;nh cho lần sau, tiếp tục kh&aacute;m ph&aacute; nhiều giải thưởng kh&aacute;c của nh&agrave; h&agrave;ng.</p>\r\n<p class=\"Normal\">&ldquo;Đo&agrave;n t&agrave;u ẩm thực Kichi Kichi&rdquo; đang tiến đến chặng thứ 2 của chương tr&igrave;nh tr&igrave;nh &ldquo;Kh&aacute;m ph&aacute; Kichi Kichi tại Singapore&rdquo;. Nh&agrave; h&agrave;ng c&ograve;n rất nhiều c&aacute;c phần qu&agrave; hấp dẫn đang chờ c&aacute;c thực kh&aacute;ch. Đặc biệt l&agrave; chương tr&igrave;nh bốc thăm tr&uacute;ng thưởng một trong 10 chuyến du lịch kh&aacute;m ph&aacute; Kichi Kichi tại quốc đảo Sư tử, trọn g&oacute;i 3 ng&agrave;y 2 đ&ecirc;m, hay iPod nghe nhạc s&agrave;nh điệu trong dịp năm mới.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/a2.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"319\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/a3.jpg\" border=\"1\" alt=\"\" width=\"400\" height=\"341\" /></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Từ ng&agrave;y 15/11 đến 22/12, chương tr&igrave;nh đ&atilde; diễn ra được 38 ng&agrave;y nhưng c&oacute; tới 78.169 thực kh&aacute;ch tham gia. Trong đ&oacute; c&oacute; 28.928 kh&aacute;ch tr&uacute;ng ngay những ly Pepsi; 189 kh&aacute;ch tr&uacute;ng &aacute;o v&agrave; 269 voucher kh&aacute;m ph&aacute; dải băng chuyền thức ăn của Kichi Kichi.</p>\r\n<p class=\"Normal\">Bạn c&oacute; thể gọi đến số hotline của nh&agrave; h&agrave;ng, hoặc truy cập v&agrave;o fanpage:www.facebook.com/kichi.hot.pot.bar&nbsp;để đặt chỗ.</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh c&ograve;n k&eacute;o d&agrave;i tới hết ng&agrave;y 10/2/2018. Th&ocirc;ng tin chi tiết truy cập:http://www.kichi-kichi.biz/.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>Chuỗi nh&agrave; h&agrave;ng lẩu băng chuyền Kichi Kichi</em>)</p>', 30, 7, 2999, 0, 1),
(1379, 'Thực đơn \'Những hương vị kỳ diệu\'', '', 'Từ ngày 26/12 đến hết ngày 6/1/2011, với thực đơn “Những hương vị kỳ diệu” tại nhà hàng Triều Nhật Asahi, bạn sẽ có một cuộc trải nghiệm thú vị về ẩm thực xứ sở Phù Tang.', 'anh_1 (3).JPG', NULL, 22, '<h1 class=\"Title\">Thực đơn \'Những hương vị kỳ diệu\'</h1>\r\n<h2 class=\"Lead\">Từ ng&agrave;y 26/12 đến hết ng&agrave;y 6/1/2011, với thực đơn &ldquo;Những hương vị kỳ diệu&rdquo; tại nh&agrave; h&agrave;ng Triều Nhật Asahi, bạn sẽ c&oacute; một cuộc trải nghiệm th&uacute; vị về ẩm thực xứ sở Ph&ugrave; Tang.</h2>\r\n<p class=\"Normal\">Tại đ&acirc;y, c&oacute; hơn 150 m&oacute;n sushi với hương vị thơm ngọt của gạo Nhật, quyện trong vị chua dịu của dấm, rượu mirin, kết hợp với c&aacute;i gi&ograve;n ngọt của hải sản. B&ecirc;n cạnh đ&oacute; c&ograve;n c&oacute; những m&oacute;n sashimi, tempura, c&aacute;c m&oacute;n nướng teppanyaki độc đ&aacute;o, m&oacute;n lẩu sukiyaki&hellip;</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/95/anh_1.JPG\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Cơm sushi cuốn rong biển, lươn tempura.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đầu ti&ecirc;n phải kể đến sushi cuốn rong biển, lươn tempura, một loại sushi rất đặc biệt. Lớp rong biển được cuốn b&ecirc;n ngo&agrave;i bao quanh một lớp nh&acirc;n lươn ngọt, mềm, kết hợp với gạo Nhật trắng ngần, dẻo thơm, mang đến một hương vị rất ri&ecirc;ng. Nếm thử một miếng sushi, bạn sẽ cảm nhận được hương vị đặc sắc, tinh tế trong c&aacute;ch chế biến m&oacute;n ăn.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/anh_22.JPG\" border=\"1\" alt=\"\" width=\"450\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Nấm b&agrave;o ngư Nhật Bản.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tempura hải sản như một m&oacute;n qu&agrave; b&iacute; mật, hấp dẫn bởi lớp bột chi&ecirc;n v&agrave;ng, gi&ograve;n tan bao ngo&agrave;i, vị thơm ngon của phần nh&acirc;n hải sản b&ecirc;n trong, h&ograve;a trộn c&ugrave;ng vị thanh m&aacute;t của nước chấm.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/anh_3.JPG\" border=\"1\" alt=\"\" width=\"450\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Gan ngỗng Ph&aacute;p nướng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Cũng mang đậm hương vị của xứ sở hoa anh đ&agrave;o l&agrave; m&oacute;n nướng tepanyaki. Đ&oacute; l&agrave; sự kết hợp của c&aacute;c loại thịt hoặc hải sản với x&igrave; dầu Nhật v&agrave; rượu sak&ecirc;. Đặc biệt, kh&ocirc;ng thể thiếu đi nước sốt teriyaki. M&oacute;n b&ograve; Kobe huyền thoại với vị thơm ngon đặc biệt kh&ocirc;ng thể chối từ. Gan ngỗng Ph&aacute;p c&oacute; lịch sử đ&atilde; 4.500 năm, được ưa chuộng kh&ocirc;ng chỉ v&igrave; độ hiếm, qu&yacute; của nguy&ecirc;n liệu, m&agrave; c&ograve;n l&agrave; một m&oacute;n ăn sạch v&agrave; bổ dưỡng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"http://vnexpress.net/Files/Subject/3B/A2/4A/95/anh_4.JPG\" border=\"1\" alt=\"\" width=\"450\" height=\"338\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">S&ograve; điệp Nhật Bản.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">&ldquo;Suki yaki&rdquo; - Thịt b&ograve; l&aacute;t mỏng &aacute;p chảo với rau tổng hợp đ&uacute;ng phong vị Nhật Bản ng&agrave;y m&ugrave;a đ&ocirc;ng. Thưởng thức lẩu sukiyaki, bạn sẽ được tận hưởng cảm gi&aacute;c chế biến mọi thứ ngay tr&ecirc;n b&agrave;n ăn với hương vị tươi nguy&ecirc;n, ngọt ng&agrave;o, ấm n&oacute;ng.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/anh_5.JPG\" border=\"1\" alt=\"\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Sukiyaki - Thịt b&ograve; l&aacute;t mỏng &aacute;p chảo với rau tổng hợp.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Qua b&agrave;n tay t&agrave;i hoa của người bếp trưởng, những m&oacute;n ăn ấy đều trở n&ecirc;n hấp dẫn, kh&ocirc;ng chỉ ngon miệng, đẹp mắt m&agrave; c&ograve;n rất tốt cho sức khỏe. Mỗi m&oacute;n l&agrave; một hương vị kỳ diệu, quyến rũ như lời mời kh&oacute; từ chối của Triều Nhật Asahi.</p>\r\n<p class=\"Normal\">Địa chỉ cho bạn:<br />- Nh&agrave; h&agrave;ng Asahi Sushi: 288 B&agrave; Triệu, H&agrave; Nội.<br />Tel: (04) 39745945; Website:&nbsp;http://www.asahisushi.vn/<br />- Nh&agrave; h&agrave;ng Asahi Hot Pot: 76 Triệu Việt Vương, H&agrave; Nội.<br />- Tel: (04) 39447966; Website:&nbsp;http://www.asahihotpot.vn&nbsp;<br />Hotline: 0902 286 286.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\">(Nguồn:&nbsp;<em>C&ocirc;ng ty TNHH Triều Nhật</em>)</p>', 30, 7, 3, 0, 1),
(1576, 'Nữ nhạc trưởng làm đại sứ Giờ Trái đất', 'Nu-Nhac-Truong-Lam-Dai-Su-Gio-Trai-Dat', 'Sáng nay chiến dịch Giờ Trái đất 2010 được khởi động ở Việt Nam, với mục tiêu có 20 tỉnh, thành đăng ký tham gia. Nữ nhạc trưởng Đặng Châu Anh, MC Thảo Vân, người mẫu Thúy Hằng... được mời làm đại sứ thiện chí.', 'EH2010.jpg', NULL, 22, '<p class=\"Title\">Nữ nhạc trưởng l&agrave;m đại sứ Giờ Tr&aacute;i đất</p>\r\n<p class=\"Lead\">S&aacute;ng nay chiến dịch Giờ Tr&aacute;i đất 2010 được khởi động ở Việt Nam, với mục ti&ecirc;u c&oacute; 20 tỉnh, th&agrave;nh đăng k&yacute; tham gia. Nữ nhạc trưởng Đặng Ch&acirc;u Anh, MC Thảo V&acirc;n, người mẫu Th&uacute;y Hằng... được mời l&agrave;m đại sứ thiện ch&iacute;.</p>\r\n<p class=\"Normal\">Theo đại diện WWF, Giờ Tr&aacute;i đất 2010 tập trung v&agrave;o k&ecirc;u gọi Ch&iacute;nh phủ, doanh nghiệp v&agrave; mỗi c&aacute; nh&acirc;n h&agrave;nh động v&agrave; g&oacute;p phần đảm bảo cho tương lai bền vững. Tại Việt Nam, chiến dịch sẽ được mở rộng hơn năm ngo&aacute;i (6 th&agrave;nh phố tham gia), với mục ti&ecirc;u sẽ c&oacute; hơn 20 địa phương đăng k&yacute; tham gia. Sự kiện ch&iacute;nh được tổ chức tại TP HCM với sự g&oacute;p mặt của những nghệ sĩ nổi tiếng v&agrave; đại sứ thiện ch&iacute;.</p>\r\n<p class=\"Normal\">Ngo&agrave;i&nbsp;nữ nhạc trưởng xinh đẹp Đặng Ch&acirc;u Anh, MC Thảo V&acirc;n, người mẫu Th&uacute;y Hằng..., chiến dịch Giờ Tr&aacute;i đất 2010 tại Việt Nam c&ograve;n c&oacute; sự tham gia của nhiều đại sứ thiện ch&iacute; kh&aacute;c. C&aacute;c đại sứ n&agrave;y sẽ đến trường học để gửi tới học sinh những th&ocirc;ng điệp về Giờ Tr&aacute;i đất, tổ chức c&aacute;c cuộc thi về chủ đề biến đổi kh&iacute; hậu...</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/EH2010.jpg\" border=\"0\" alt=\"Ảnh: Tiến Dũng.\" width=\"480\" height=\"318\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Tối 27/3, nhạc trưởng Đặng Ch&acirc;u Anh (&aacute;o v&agrave;ng) sẽ c&ugrave;ng c&aacute;c học sinh trung t&acirc;m Sol Art biểu diễn &acirc;m nhạc tại chương tr&igrave;nh Giờ Tr&aacute;i đất 2010 tại Việt Nam. Ảnh:&nbsp;<em>Tiến Dũng.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">\"Giờ Tr&aacute;i đất 2010 kh&ocirc;ng chỉ l&agrave; tắt đ&egrave;n trong v&ograve;ng một giờ (20h30-21h30 ng&agrave;y 27/3). WWF mong muốn k&ecirc;u gọi người d&acirc;n Việt Nam thực hiện một h&agrave;nh động tượng trưng để thể hiện cam kết của m&igrave;nh đối với tr&aacute;i đất. Mục ti&ecirc;u của Giờ Tr&aacute;i đất l&agrave; n&acirc;ng cao nhận thức về biến đổi kh&iacute; hậu bằng c&aacute;ch k&ecirc;u gọi mọi người tham gia tắt đ&egrave;n\", b&agrave; Trần Minh Hiền - Gi&aacute;m đốc WWF chương tr&igrave;nh Việt Nam n&oacute;i.</p>\r\n<p class=\"Normal\">Cũng theo b&agrave; Hiền, l&agrave; một trong những nước dự b&aacute;o bị ảnh hưởng nặng nề bởi biến đổi kh&iacute; hậu, Việt Nam cần phải c&oacute; c&aacute;c h&agrave;nh động v&agrave; cam kết mạnh mẽ để c&oacute; tiếng n&oacute;i mạnh hơn tr&ecirc;n trường quốc tế trong vấn đề giảm thiểu kh&iacute; nh&agrave; k&iacute;nh để đạt được mục ti&ecirc;u hạn chế nhiệt độ tăng của tr&aacute;i đất dưới 2 độ C trong thế kỷ n&agrave;y.</p>\r\n<p class=\"Normal\">Năm nay, Tập đo&agrave;n Điện lực Việt Nam (EVN) cũng tham gia chương tr&igrave;nh Giờ Tr&aacute;i đất kh&ocirc;ng chỉ với tư c&aacute;ch l&agrave; nh&agrave; t&agrave;i trợ V&agrave;ng m&agrave; c&ograve;n đ&oacute;ng vai tr&ograve; l&agrave; một trong những đại sứ của chương tr&igrave;nh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"99%\">\r\n<tbody>\r\n<tr>\r\n<td bgcolor=\"#e9e9d1\">\r\n<p class=\"Normal\">Giờ Tr&aacute;i đất l&agrave; s&aacute;ng kiến to&agrave;n cầu của WWF về biến đổi kh&iacute; hậu, bắt nguồn từ Sydney (Australia) v&agrave; trở th&agrave;nh chương tr&igrave;nh to&agrave;n cầu trong 2 năm vừa qua, với sự tham gia của hơn 1 tỷ người tại hơn 4.000 th&agrave;nh phố v&agrave; thị trấn của 88 quốc gia năm 2009.</p>\r\n<p class=\"Normal\">Năm 2009, Giờ Tr&aacute;i đất lần đầu diễn ra tại Việt Nam, với sự tham gia của H&agrave; Nội, TP HCM, Cần Thơ, Hội An, Huế v&agrave; Kh&aacute;nh Ho&agrave;. Theo Trung t&acirc;m Điều độ Hệ thống điện Quốc gia, trong \"Giờ Tr&aacute;i đất\" đ&ecirc;m 28/3/2009, sản lượng điện ti&ecirc;u thụ đ&atilde; giảm 140.000 KWh (tương đương 133 triệu đồng) so với c&ugrave;ng thời điểm tuần trước đ&oacute;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Tiến Dũng</strong></p>', 34, 8, 2195, 0, 1),
(1577, 'Hổ tại Tiểu vùng sông Mekong đối diện nguy cơ tuyệt chủng', 'Ho-Tai-Tieu-Vung-Song-Mekong-Doi-Dien-Nguy-Co-Tuyet-Chung', 'Số lượng hổ tại Tiểu vùng sông Mekong đã giảm hơn 70% trong vòng 12 năm qua, theo báo cáo mới nhất của Quỹ Bảo tồn thiên nhiên Quốc tế (WWF).', 'indochinese-tiger.jpg', NULL, 22, '<p class=\"Title\">Hổ tại Tiểu v&ugrave;ng s&ocirc;ng Mekong đối diện nguy cơ tuyệt chủng</p>\r\n<p class=\"Lead\">Số lượng hổ tại Tiểu v&ugrave;ng s&ocirc;ng Mekong đ&atilde; giảm hơn 70% trong v&ograve;ng 12 năm qua, theo b&aacute;o c&aacute;o mới nhất của Quỹ Bảo tồn thi&ecirc;n nhi&ecirc;n Quốc tế (WWF).</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/indochinese-tiger.jpg\" alt=\"Số lượng hổ tại Tiểu v&ugrave;ng s&ocirc;ng Mekong đ&atilde; giảm hơn 70% trong 12 năm qua. Ảnh:\" width=\"450\" height=\"324\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Số lượng hổ tại Tiểu v&ugrave;ng s&ocirc;ng Mekong đ&atilde; giảm hơn 70% trong 12 năm qua. Ảnh:<em>discovery.com</em>.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Theo b&aacute;o c&aacute;o \"<em>Lo&agrave;i Hổ đang tr&ecirc;n bờ vực tuyệt chủng: Đương đầu với th&aacute;ch thức tại khu vực tiểu v&ugrave;ng M&ecirc;k&ocirc;ng</em>&nbsp;(t&ecirc;n tiếng Anh&nbsp;<em>Tigers on the Brink: Facing up to the Challenge in the Greater Mekong</em>), số lượng hổ ở khu vực tiểu Mekong - bao gồm Campuchia, L&agrave;o, Myanma, Th&aacute;i Lan v&agrave; Việt Nam - đ&atilde; lao dốc từ khoảng 1.200 con trong năm Mậu Dần 1998 xuống chỉ c&ograve;n 350 con v&agrave;o năm 2010.</p>\r\n<p class=\"Normal\">B&aacute;o c&aacute;o của WWF được c&ocirc;ng bố h&ocirc;m 26/1 - một ng&agrave;y trước khi Hội nghị bộ trưởng ch&acirc;u &Aacute; lần thứ nhất về bảo tồn hổ diễn ra tại th&agrave;nh phố Hua Hin, Th&aacute;i Lan trong ba ng&agrave;y. Bộ trưởng từ 13 nước ch&acirc;u &Aacute; c&oacute; hổ sinh sống đ&atilde; tới Th&aacute;i Lan để tham dự hội nghị.</p>\r\n<p class=\"Normal\">Sự giảm s&uacute;t về số lượng hổ tại Tiểu v&ugrave;ng s&ocirc;ng Mekong phản &aacute;nh r&otilde; r&agrave;ng thực trạng hổ to&agrave;n cầu. Hiện nay, số lượng hổ to&agrave;n cầu đang ở mức thấp kỷ lục với 3.200 con. Trước đ&oacute;, thế giới vẫn c&ograve;n khoảng 5.000-7.000 con v&agrave;o năm Mậu Dần. B&aacute;o c&aacute;o cho thấy l&yacute; do ch&iacute;nh dẫn đến số lượng giảm s&uacute;t số lượng hổ Đ&ocirc;ng Dương l&agrave; nhu cầu sử dụng bộ phận của hổ trong Đ&ocirc;ng y tăng mạnh v&agrave; ảnh hưởng xấu từ sự ph&aacute;t triển thiếu bền vững những cơ sở hạ tầng đến m&ocirc;i trường sống tự nhi&ecirc;n của lo&agrave;i hổ.</p>\r\n<p class=\"Normal\">Nick Coxx, Điều Phối Vi&ecirc;n Chương tr&igrave;nh tiểu v&ugrave;ng M&ecirc;k&ocirc;ng của WWF, nhận x&eacute;t: \"Cần phải h&agrave;nh động một c&aacute;ch mạnh mẽ để đảm bảo ph&acirc;n lo&agrave;i n&agrave;y sẽ kh&ocirc;ng bị giảm đến mức kh&ocirc;ng thể phục hồi được. Nếu ch&uacute;ng ta kh&ocirc;ng tiến h&agrave;nh ngay c&aacute;c biện ph&aacute;p bảo vệ, đến năm Nh&acirc;m Dần (2022) c&oacute; rất nhiều khả năng hổ sẽ tuyệt chủng ở Việt Nam, L&agrave;o v&agrave; Campuchia\"</p>\r\n<p class=\"Normal\">Trong lịch sử, số lượng hổ rất phong ph&uacute; ở khu vực tiểu v&ugrave;ng Mekong. Đến nay, số lượng hổ t&iacute;nh ri&ecirc;ng ở mỗi quốc gia như Campuchia, L&agrave;o v&agrave; Việt Nam chưa tới 30 con. Số lượng hổ c&ograve;n lại chủ yếu ph&acirc;n bố ở khu vực n&uacute;i gi&aacute;p ranh giữa Th&aacute;i Lan v&agrave; Myanma.</p>\r\n<p class=\"Normal\"><strong><span style=\"color: #4f4f4f;\">Tuy bức tranh về số phận lo&agrave;i hổ kh&ocirc;ng được tươi s&aacute;ng, ch&uacute;ng ta vẫn c&ograve;n cơ hội để cứu lo&agrave;i hổ ở khu vực tiểu v&ugrave;ng Mekong.</span></strong>&nbsp;Tổng diện t&iacute;ch khu vực sinh sống của lo&agrave;i hổ ở v&ugrave;ng tiểu Mekong c&oacute; diện t&iacute;ch 540,000 km2, bằng khoảng diện t&iacute;ch của nước Ph&aacute;p v&agrave; l&agrave; khu vực sinh sống lớn nhất thế giới của hổ. Đ&acirc;y l&agrave; khu vực được ưu ti&ecirc;n h&agrave;ng đầu trong nỗ lực bảo tồn hổ.</p>\r\n<p class=\"Normal\">Điều phối vi&ecirc;n Cox nhận định \"Tiểu v&ugrave;ng s&ocirc;ng Mekong c&oacute; tiềm năng lớn trong việc tăng số lượng hổ\", nhưng &ocirc;ng nhấn mạnh tiềm năng n&agrave;y chỉ c&oacute; thể ph&aacute;t huy khi c&oacute; những nỗ lực r&otilde; rệt v&agrave; sự cộng t&aacute;c xuy&ecirc;n suốt giữa c&aacute;c nước trong khu vực với một qui m&ocirc; chưa từng thấy từ trước tới nay trong việc bảo vệ được những con hổ c&ograve;n s&oacute;t lại c&ugrave;ng với nguồn thức ăn v&agrave; m&ocirc;i trường sống của ch&uacute;ng.\"</p>\r\n<p class=\"Normal\">Trong hội nghị tại Hua Hin, WWF k&ecirc;u gọi bộ trưởng của 13 nước c&ugrave;ng nỗ lực để l&agrave;m tăng số lượng hổ l&ecirc;n gấp đ&ocirc;i v&agrave;o năm 2022. Những quốc gia c&ograve;n hổ sinh sống bao gồm Việt Nam, Bangladesh, Bhutan, Campuchia, Trung Quốc, Ấn Độ, Indonesia, L&agrave;o, Malaysia, Myanma, Nepal, Nga, Th&aacute;i Lan.</p>\r\n<p class=\"Normal\">Hội nghị Bộ trưởng c&aacute;c nước ch&acirc;u &Aacute; lần thứ nhất về vấn đề bảo tồn hổ diễn ra từ ng&agrave;y 27 đến 30/1 l&agrave; một phần trong lộ tr&igrave;nh ch&iacute;nh trị to&agrave;n cầu tham gia bảo vệ tương lai của lo&agrave;i hổ. Những nỗ lực n&agrave;y sẽ l&ecirc;n đến đỉnh điểm ở Hội nghị thượng đỉnh về hổ ở Vladivostok, Nga v&agrave;o th&aacute;ng 9 năm nay. Thủ tướng Nga Vladimir Putin v&agrave; gi&aacute;m đốc Robert Zoellick của Ng&acirc;n h&agrave;ng Thế giới sẽ đồng chủ tr&igrave; hội nghị.</p>\r\n<p class=\"Normal\">L&atilde;nh đạo tổ chức s&aacute;ng kiến bảo tồn hổ to&agrave;n cầu của WWF cho rằng \"c&oacute; những cơ hội chưa từng thấy c&oacute; thể kh&iacute;ch lệ tinh thần ch&iacute;nh trị v&agrave; c&ugrave;ng h&agrave;nh động để đảo ngược số lượng hổ hiện nay. Nhưng để l&agrave;m được điều n&agrave;y, ch&uacute;ng ta phải dừng mua b&aacute;n c&aacute;c sản phẩm từ hổ, ngăn chặn việc đ&aacute;nh bắt v&ocirc; tr&aacute;ch nhiệm v&agrave; bảo vệ m&ocirc;i trường sống của hổ.\"</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Minh Long</strong></p>', 34, 8, 672, 0, 1),
(1578, 'Thi thiết kế áo phông Giờ Trái đất 2010', 'Thi-Thiet-Ke-Ao-Phong-Gio-Trai-Dat-2010', 'Những tác phẩm dự thi sẽ phải truyền tải được thông điệp về tác động của biến đổi khí hậu đối với Việt Nam.', 'Thiet-ke.jpg', NULL, 22, '<p class=\"Title\">Thi thiết kế &aacute;o ph&ocirc;ng Giờ Tr&aacute;i đất 2010</p>\r\n<p class=\"Lead\">Những t&aacute;c phẩm dự thi sẽ phải truyền tải được th&ocirc;ng điệp về t&aacute;c động của biến đổi kh&iacute; hậu đối với Việt Nam.</p>\r\n<p class=\"Normal\">Nhằm hưởng ứng sự kiện Giờ Tr&aacute;i đất 2010 diễn ra v&agrave;o 20h30-21h30<strong>&nbsp;</strong>ng&agrave;y 27/3, nh&atilde;n hiệu thời trang BOO Skateshop phối hợp c&ugrave;ng WWF Việt Nam ph&aacute;t động cuộc thi thiết kế &aacute;o ph&ocirc;ng d&agrave;nh cho sinh vi&ecirc;n chuy&ecirc;n ng&agrave;nh thiết kế thời trang tại H&agrave; Nội v&agrave; c&aacute;c th&iacute; sinh tự do tr&ecirc;n to&agrave;n quốc. Cuộc thi nhằm n&acirc;ng cao sự hiểu biết v&agrave; quan t&acirc;m của x&atilde; hội đối với vấn đề m&ocirc;i trường v&agrave; biến đổi kh&iacute; hậu.</p>\r\n<p class=\"Normal\">Những t&aacute;c phẩm dự thi sẽ phải truyền tải được th&ocirc;ng điệp về t&aacute;c động của biến đổi kh&iacute; hậu đối với Việt Nam v&agrave; n&oacute;i l&ecirc;n được những h&agrave;nh động thực tiễn h&agrave;ng ng&agrave;y tuy nhỏ b&eacute; (như tiết kiệm điện...) cũng c&oacute; thể đ&oacute;ng g&oacute;p một phần kh&ocirc;ng nhỏ trong cuộc chiến chống lại biến đổi kh&iacute; hậu. 10 t&aacute;c phẩm xuất sắc nhất sẽ được lựa chọn v&agrave; c&ocirc;ng bố v&agrave;o ng&agrave;y 27/3.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Thiet-ke.jpg\" border=\"0\" alt=\"Ảnh: WWF.\" width=\"450\" height=\"300\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Thời gian nhận b&agrave;i thi đến hết ng&agrave;y 21/2. Ảnh:&nbsp;<em>WWF.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Cuộc thi đ&atilde; được ph&aacute;t động tại FPT Arena, Học viện Thời trang London (LCFS), Trung t&acirc;m đ&agrave;o tạo quốc tế Raffles, ĐH Nguyễn Tr&atilde;i, ĐH Mỹ thuật C&ocirc;ng nghiệp v&agrave; Hanoi Arena. Ban tổ chức hy vọng sẽ c&oacute; th&ecirc;m nhiều trường đ&agrave;o tạo thiết kế thời trang sẽ tham gia trong thời gian tới.</p>\r\n<p class=\"Normal\">Gi&aacute;m đốc Truyền th&ocirc;ng của WWF Việt Nam Julianne Becker tin tưởng, cuộc thi sẽ g&oacute;p phần v&agrave;o việc n&acirc;ng cao nhận thức của mọi người về biến đổi kh&iacute; hậu, v&agrave; cho thấy c&aacute;c quốc gia, như Việt Nam, c&oacute; thể l&agrave;m g&igrave; để c&ugrave;ng tham gia cũng như th&iacute;ch ứng với những t&aacute;c động của biến đổi kh&iacute; hậu\".</p>\r\n<p class=\"Normal\">Chương tr&igrave;nh Giờ Tr&aacute;i đất k&ecirc;u gọi mọi người tr&ecirc;n khắp h&agrave;nh tinh c&ugrave;ng tắt đ&egrave;n trong v&ograve;ng một giờ để thể hiện rằng chỉ bằng những h&agrave;nh động nhỏ nhất của mỗi c&aacute; nh&acirc;n cũng c&oacute; thể g&oacute;p phần l&agrave;m thay đổi hiện tượng biến đổi kh&iacute; hậu. Năm 2009, Việt Nam c&ugrave;ng với 88 quốc gia tr&ecirc;n thế giới tham gia Giờ Tr&aacute;i đất.</p>\r\n<p class=\"Normal\">Năm 2010, Giờ Tr&aacute;i đất hướng tới con số 1 tỷ người ở 6.000 th&agrave;nh phố v&agrave; thị trấn tr&ecirc;n thế giới để khẳng định hơn nữa sự tham gia của to&agrave;n nh&acirc;n loại trong cuộc chiến chống lại biến đổi kh&iacute; hậu.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Anh Khoa</strong></p>', 34, 8, 866, 0, 1),
(1579, 'Loài tắc kè mới được phát hiện ở Việt Nam', 'Loai-Tac-Ke-Moi-Duoc-Phat-Hien-O-Viet-Nam', 'Tên của loài tắc kè này được đặt theo tên của PGS Lê Xuân Cảnh, Viện Sinh thái và Tài nguyên sinh vật, để ghi nhận sự ủng hộ và những đóng góp của ông trong nghiên cứu đa dạng sinh học ở Việt Nam. ', 'Tac-ke.jpg', NULL, 22, '<p class=\"Title\">Lo&agrave;i tắc k&egrave; mới được ph&aacute;t hiện ở Việt Nam</p>\r\n<p class=\"Lead\">T&ecirc;n của lo&agrave;i tắc k&egrave; n&agrave;y được đặt theo t&ecirc;n của PGS L&ecirc; Xu&acirc;n Cảnh, Viện Sinh th&aacute;i v&agrave; T&agrave;i nguy&ecirc;n sinh vật, để ghi nhận sự ủng hộ v&agrave; những đ&oacute;ng g&oacute;p của &ocirc;ng trong nghi&ecirc;n cứu đa dạng sinh học ở Việt Nam.&nbsp;<br />Một lo&agrave;i tắc k&egrave; mới ở miền Bắc nước ta vừa được c&aacute;c nh&agrave; khoa học Đức v&agrave; Việt Nam c&ocirc;ng bố tr&ecirc;n Tạp ch&iacute; Zootaxa. Mẫu chuẩn của lo&agrave;i tắc k&egrave; n&agrave;y được thu thập tại Hữu Li&ecirc;n (Lạng Sơn) v&agrave; Sa Pa (L&agrave;o Cai). Lo&agrave;i mới được đặt t&ecirc;n khoa học l&agrave;<em>Gekko canhi</em>&nbsp;Roesler, Nguyen, Doan, Ho, Nguyen &amp; Ziegler, 2010.</p>\r\n<p class=\"Normal\">T&ecirc;n của lo&agrave;i tắc k&egrave; n&agrave;y được đặt theo t&ecirc;n của PGS L&ecirc; Xu&acirc;n Cảnh, Viện Sinh th&aacute;i v&agrave; T&agrave;i nguy&ecirc;n sinh vật, để ghi nhận sự ủng hộ v&agrave; những đ&oacute;ng g&oacute;p của &ocirc;ng trong nghi&ecirc;n cứu đa dạng sinh học ở Việt Nam.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Tac-ke.jpg\" border=\"0\" alt=\"Ảnh: Quảng Trường.\" width=\"450\" height=\"263\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Lo&agrave;i tắc k&egrave; mới&nbsp;<em>Gekko canhi.&nbsp;</em>Ảnh:&nbsp;<em>Quảng Trường.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Đ&acirc;y cũng l&agrave; lo&agrave;i tắc k&egrave; mới thứ ba được ph&aacute;t hiện trong hai năm gần đ&acirc;y ở miền Bắc nước ta, b&ecirc;n cạnh lo&agrave;i Thạch s&ugrave;ng m&iacute;&nbsp;<em>Goniurosaurus catbaensis&nbsp;</em>ph&aacute;t hiện ở đảo C&aacute;t B&agrave; (Hải Ph&ograve;ng) v&agrave; lo&agrave;i&nbsp;<em>Goniurosaurus huulienensis</em>&nbsp;ph&aacute;t hiện ở Hữu Li&ecirc;n (Lạng Sơn) năm 2008.</p>\r\n<p class=\"Normal\">Với k&iacute;ch cỡ trung b&igrave;nh 8,5-10 cm, c&oacute; vảy m&ocirc;i tr&ecirc;n v&agrave; m&ocirc;i dưới, lưng v&agrave; ph&iacute;a tr&ecirc;n ống ch&acirc;n c&oacute; h&agrave;ng u nhỏ, vảy dưới đu&ocirc;i ph&igrave;nh rộng..., tắc k&egrave; mới&nbsp;<em>Gekko canhi</em>&nbsp;c&oacute; đặc điểm h&igrave;nh th&aacute;i kh&aacute; giống với lo&agrave;i Tắc k&egrave; nhật bản&nbsp;<em>Gekko japonicus</em>, tuy nhi&ecirc;n n&oacute; kh&aacute;c với lo&agrave;i thứ hai ở chỗ: k&iacute;ch cỡ lớn hơn, c&oacute; nhiều hơn số vảy gian ổ mắt, số h&agrave;ng vảy quanh th&acirc;n, số vảy bụng...</p>\r\n<p class=\"Normal\">Đ&acirc;y cũng l&agrave; lo&agrave;i thứ 8 thuộc giống Tắc k&egrave;&nbsp;<em>Gekko</em>&nbsp;ghi nhận ở Việt Nam.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Nguyễn Quảng Trường</strong></p>', 34, 8, 1428, 0, 1),
(1580, 'Động vật \'vòi vĩnh\' du khách', '', 'Đi ôtô vào vườn thú thiên nhiên hoang dã Tamna Safari, Indonesia, khách phải chuẩn bị sẵn cà rốt, cỏ, để \"hối lộ\" các chú nai, lạc đà, bò... nếu muốn chiêm ngưỡng khu sinh sống của hơn 270 loài tại đây.', 'dan-hang-ngaRng.jpg', NULL, 22, '<p class=\"Title\">Động vật \'v&ograve;i vĩnh\' du kh&aacute;ch</p>\r\n<p class=\"Lead\">Đi &ocirc;t&ocirc; v&agrave;o vườn th&uacute; thi&ecirc;n nhi&ecirc;n hoang d&atilde; Tamna Safari, Indonesia, kh&aacute;ch phải chuẩn bị sẵn c&agrave; rốt, cỏ, để \"hối lộ\" c&aacute;c ch&uacute; nai, lạc đ&agrave;, b&ograve;... nếu muốn chi&ecirc;m ngưỡng khu sinh sống của hơn 270 lo&agrave;i tại đ&acirc;y.</p>\r\n<p class=\"Normal\">Khu vườn th&uacute; thi&ecirc;n nhi&ecirc;n hoang d&atilde; Tamna Safari nằm tr&ecirc;n đường cao tốc giữa Jakarta v&agrave; Bangdung West Java, rộng 35 hecta, c&oacute; mặt khoảng 271 lo&agrave;i từ khắp nơi tr&ecirc;n thế giới.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/dan-hang-ngaRng.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Du kh&aacute;ch phải di chuyển bằng &ocirc;t&ocirc;, song vẫn c&oacute; cảm gi&aacute;c h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n khi chứng kiến cuộc sống tự do thoải m&aacute;i của c&aacute;c lo&agrave;i th&uacute;. C&oacute; kh&aacute;ch lạ, c&aacute;c con th&uacute; kh&ocirc;ng tỏ ra sợ h&atilde;i m&agrave; nhanh ch&oacute;ng tiếp cận.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/con-lac-daR.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Thậm ch&iacute; d&ugrave;ng miệng g&otilde; v&agrave;o cửa k&iacute;nh \"v&ograve;i vĩnh\" qu&agrave; của du kh&aacute;ch.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/duoc-cho-anR.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Những củ c&agrave; rốt l&agrave; m&oacute;n qu&agrave; ưa th&iacute;ch của ch&uacute;ng.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/voiR.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Du kh&aacute;ch c&oacute; thể thoải m&aacute;i vui đ&ugrave;a, chụp ảnh c&ugrave;ng c&aacute;c ch&uacute; voi, c&oacute; thể y&ecirc;u cầu c&aacute;c ch&uacute; diễn tr&ograve; với điều kiện phải c&oacute; những củ c&agrave; rốt, c&acirc;y m&iacute;a l&agrave;m qu&agrave;.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/nui-lua.jpg\" border=\"1\" alt=\"\" width=\"450\" height=\"350\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ngo&agrave;i Tamna Safari, ở Bangdung c&ograve;n c&oacute; n&uacute;i lửa Tangkuban Perahu nằm tr&ecirc;n độ cao hơn 2.000 m, sương m&ugrave; d&agrave;y đặc. D&ugrave; đ&atilde; ngừng hoạt động từ năm 1969 ng&agrave;y nay đứng tr&ecirc;n miệng n&uacute;i vẫn c&ograve;n nồng nặc m&ugrave;i lưu huỳnh.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: left;\"><strong>Huỳnh Phan</strong></p>', 34, 8, 3, 0, 1),
(1581, 'Thiên nhiên trên đảo Kangaroo của Australia', 'Thien-Nhien-Tren-Dao-Kangaroo-Cua-Australia', 'Cùng khám phá thiên nhiên hoang sơ trên đảo Kangaroo, Australia qua khung hình của độc giả Phạm Đức Trực.', '01 (1)_1.jpg', NULL, 22, '<p class=\"Title\">Thi&ecirc;n nhi&ecirc;n tr&ecirc;n đảo Kangaroo của Australia</p>\r\n<p class=\"Lead\">C&ugrave;ng kh&aacute;m ph&aacute; thi&ecirc;n nhi&ecirc;n hoang sơ tr&ecirc;n đảo Kangaroo, Australia qua khung h&igrave;nh của độc giả Võ Tứ Hải.<br />Đảo Kangaroo nằm ở miền nam của tiểu bang Nam Australia, nổi tiếng với hệ động vật hoang d&atilde; đ&ocirc;ng đảo v&agrave; độ bao phủ của thảm thực vật tự nhi&ecirc;n cao nhất. Đảo Kangaroo được biết đến như Galapagos (quần đảo di sản thế giới thuộc Ecuador) của Australia.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/01.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"240\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Penneshaw, thị trấn lớn thứ hai của đảo.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/02.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ghềnh đ&aacute; l&ocirc; x&ocirc; tr&ecirc;n b&atilde;i biển vịnh Vivonne, một trong những b&atilde;i biển đẹp nhất Australia.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/03.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Bồ n&ocirc;ng tr&ecirc;n b&atilde;i biển vịnh Emu.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/04.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Đ&aacute; Remarkable, đ&aacute; granite h&igrave;nh th&agrave;nh do thềm lục địa n&acirc;ng l&ecirc;n ở nhiệt độ cao gặp nước biển tạo n&ecirc;n những h&igrave;nh th&ugrave; kỳ lạ.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/05.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"320\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&acirc;y đ&egrave;n biển lớn h&ugrave;ng vĩ giữa thi&ecirc;n nhi&ecirc;n ở mũi Admirals ph&iacute;a t&acirc;y nam.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/06.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">C&acirc;y cối ken d&agrave;y dọc đường bộ xuy&ecirc;n qua đảo.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/07.jpg\" border=\"1\" alt=\"\" width=\"480\" height=\"319\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Hoa lavender được trồng nhiều tr&ecirc;n đảo.</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Phạm Đức Trực</strong></p>', 34, 8, 2603, 0, 1),
(1582, 'Phát hiện loài chim chích mới ở vùng núi Việt - Lào', 'Phat-Hien-Loai-Chim-Chich-Moi-O-Vung-Nui-Viet---Lao', 'Được phát hiện và ghi nhận tại vùng núi đá vôi của Việt Nam và Lào, loài chim này khá giống với loài Chích ngực vàng Phylloscopus ricketti nhưng về hình thái thì nhỏ hơn, cánh tròn hơn.', 'Chim-moi.jpg', NULL, 22, '<p class=\"Title\">Ph&aacute;t hiện lo&agrave;i chim ch&iacute;ch mới ở v&ugrave;ng n&uacute;i Việt - L&agrave;o</p>\r\n<p class=\"Lead\">Được ph&aacute;t hiện v&agrave; ghi nhận tại v&ugrave;ng n&uacute;i đ&aacute; v&ocirc;i của Việt Nam v&agrave; L&agrave;o, lo&agrave;i chim n&agrave;y kh&aacute; giống với lo&agrave;i Ch&iacute;ch ngực v&agrave;ng&nbsp;<em>Phylloscopus ricketti</em>&nbsp;nhưng về h&igrave;nh th&aacute;i th&igrave; nhỏ hơn, c&aacute;nh tr&ograve;n hơn.<br />Lo&agrave;i chim ch&iacute;ch mới ph&aacute;t hiện n&agrave;y được đặt t&ecirc;n l&agrave; Ch&iacute;ch n&uacute;i đ&aacute; v&ocirc;i, t&ecirc;n khoa học l&agrave;<em>Phylloscopus calciatilis</em>. Lo&agrave;i n&agrave;y kh&aacute; giống với lo&agrave;i chim Ch&iacute;ch ngực v&agrave;ng<em>Phylloscopus ricketti</em>&nbsp;nhưng về h&igrave;nh th&aacute;i th&igrave; nhỏ hơn, c&aacute;nh tr&ograve;n hơn.</p>\r\n<p class=\"Normal\">Ban đầu, lo&agrave;i chim n&agrave;y được x&aacute;c định như l&agrave; lo&agrave;i Ch&iacute;ch ngực v&agrave;ng nhưng người ta lại t&igrave;m thấy lo&agrave;i n&agrave;y ở c&aacute;ch khu vực sinh sản quen thuộc của ch&uacute;ng chừng 1.000 km. Sau đấy, c&aacute;c nh&agrave; khoa học nhận thấy tiếng h&oacute;t của ch&uacute;ng cũng kh&aacute;c so với tiếng h&oacute;t của lo&agrave;i Ch&iacute;ch ngực v&agrave;ng. Đ&acirc;y l&agrave; những cơ sở ban đầu để tiến h&agrave;nh c&aacute;c nghi&ecirc;n cứu s&acirc;u hơn về lo&agrave;i n&agrave;y.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Chim-moi.jpg\" border=\"0\" alt=\"Ảnh: Ulf Johansson.\" width=\"450\" height=\"330\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Ch&iacute;ch n&uacute;i đ&aacute; v&ocirc;i - lo&agrave;i từng được x&aacute;c định như l&agrave; lo&agrave;i Ch&iacute;ch ngực v&agrave;ng. Ảnh:&nbsp;<em>Ulf Johansson.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">Tiếng h&oacute;t v&agrave; tiếng gọi của lo&agrave;i n&agrave;y đ&atilde; được ph&acirc;n t&iacute;ch. Dựa tr&ecirc;n kết quả ph&acirc;n t&iacute;ch nhiễm sắc thể v&agrave; DNA, c&aacute;c nh&agrave; khoa học nhận thấy, lo&agrave;i chim ch&iacute;ch mới n&agrave;y c&oacute; quan hệ rất gần gũi với lo&agrave;i Ch&iacute;ch ngực v&agrave;ng v&agrave; Ch&iacute;ch đ&iacute;t v&agrave;ng.</p>\r\n<p class=\"Normal\">Ch&iacute;nh những chuy&ecirc;n gia của tổ chức BirdLife v&agrave; Viện Sinh th&aacute;i v&agrave; T&agrave;i nguy&ecirc;n sinh vật đ&atilde; đưa ra những bằng chứng thuyết phục rằng lo&agrave;i chim n&agrave;y định cư v&agrave; sinh sản ở v&ugrave;ng n&uacute;i đ&aacute; v&ocirc;i miền Trung Việt Nam.</p>\r\n<p class=\"Normal\">\"V&ugrave;ng n&uacute;i đ&aacute; v&ocirc;i ở L&agrave;o v&agrave; Việt Nam rất đ&aacute;ng được lưu t&acirc;m v&igrave; đ&acirc;y l&agrave; nơi c&oacute; mức độ đa dạng sinh học cao của c&aacute;c lo&agrave;i thực vật, động vật kh&ocirc;ng xương sống v&agrave; c&aacute;c lo&agrave;i th&uacute;. Tuy nhi&ecirc;n, chỉ gần đ&acirc;y th&igrave; người ta mới quan t&acirc;m tới sự đa dạng của c&aacute;c lo&agrave;i chim ở khu vực n&agrave;y\", Jonathan Eames, trưởng đại diện tổ chức BirdLife Quốc tế tại Đ&ocirc;ng Dương n&oacute;i.</p>\r\n<p class=\"Normal\">Đ&atilde; từng c&oacute; 4 lo&agrave;i chim được t&igrave;m thấy ở khu vực n&uacute;i đ&aacute; v&ocirc;i n&agrave;y. Một trong số đ&oacute; l&agrave; lo&agrave;i Khướu mun&nbsp;<em>Stachyris herberti</em>&nbsp;được c&aacute;c chuy&ecirc;n gia của tổ chức Birdlife ph&aacute;t hiện lại v&agrave;o năm 1994 sau hơn 64 năm mất t&iacute;ch. Lo&agrave;i Khướu mun cũng được t&igrave;m thấy ở đ&uacute;ng khu vực m&agrave; lo&agrave;i Ch&iacute;ch n&uacute;i Đ&aacute; V&ocirc;i vừa được ph&aacute;t hiện.</p>\r\n<p class=\"Normal\">Đ&acirc;y l&agrave; một v&ugrave;ng rừng n&uacute;i đ&aacute; v&ocirc;i rộng lớn, nay thuộc Vườn Quốc Gia Hin Namno ở L&agrave;o v&agrave; Phong Nha - Kẻ B&agrave;ng ở Việt Nam. Mặc d&ugrave; lo&agrave;i chim n&agrave;y hiện kh&ocirc;ng bị đe dọa nhưng tổ chức BirdLife sẽ đ&aacute;nh gi&aacute; t&igrave;nh trạng bảo tồn lo&agrave;i của ch&uacute;ng, từ đ&oacute; th&ecirc;m dữ liệu cho S&aacute;ch Đỏ của IUCN về lo&agrave;i n&agrave;y.</p>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Anh Khoa</strong></p>', 34, 8, 1456, 0, 1),
(1583, 'Giờ Trái đất năm 2018 diễn ra ngày 27/3', 'Gio-Trai-Dat-Nam-2010-Dien-Ra-Ngay-27/3', '20h30-21h30 ngày 27/3/2018, Việt Nam sẽ cùng với người dân trên toàn thế giới tắt những thiết bị chiếu sáng không cần thiết để góp phần ngăn chặn và thích ứng với biến đổi khí hậu.', 'Ho-guom.jpg', NULL, 22, '<p class=\"Title\">Giờ Tr&aacute;i đất năm 2010 diễn ra ng&agrave;y 27/3</p>\r\n<p class=\"Lead\">20h30-21h30 ng&agrave;y 27/3/2018, Việt Nam sẽ c&ugrave;ng với người d&acirc;n tr&ecirc;n to&agrave;n thế giới tắt những thiết bị chiếu s&aacute;ng kh&ocirc;ng cần thiết để g&oacute;p phần ngăn chặn v&agrave; th&iacute;ch ứng với biến đổi kh&iacute; hậu.<br />19h tối 17/12, chương tr&igrave;nh Giờ Tr&aacute;i đất đặc biệt diễn ra tại Copenhagen (Đan Mạch) do WWF Quốc tế v&agrave; WWF Đan Mạch phối hợp tổ chức. Một triệu người d&acirc;n th&agrave;nh phố Copenhagen đại diện cho 6,7 tỷ c&ocirc;ng d&acirc;n to&agrave;n cầu gửi th&ocirc;ng điệp: Thế giới cần phải c&oacute; những h&agrave;nh động ki&ecirc;n quyết về biến đổi kh&iacute; hậu; đ&atilde; đến l&uacute;c c&aacute;c nh&agrave; l&atilde;nh đạo thế giới lắng nghe nguyện vọng của c&aacute;c c&ocirc;ng d&acirc;n to&agrave;n cầu.</p>\r\n<p class=\"Normal\">Th&ocirc;ng điệp n&agrave;y nhằm gửi tới c&aacute;c nh&agrave; l&atilde;nh đạo, đại diện của 192 quốc gia tr&ecirc;n thế giới đang c&oacute; mặt trong th&agrave;nh phố, những người sẽ đưa ra quyết định cuối c&ugrave;ng cho c&aacute;c vấn đề về biến đổi kh&iacute; hậu được thảo luận tại Hội nghị Thượng đỉnh của Li&ecirc;n Hợp quốc về Biến đổi Kh&iacute; hậu trong 10 ng&agrave;y qua.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"1\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td><img src=\"upload/tintuc/Ho-guom.jpg\" border=\"0\" alt=\"Hồ Gươm trong thời khắc Giờ Tr&aacute;i đất năm 2018. Ảnh: Vo Tu Hai\" width=\"470\" height=\"289\" /></td>\r\n</tr>\r\n<tr>\r\n<td class=\"Image\">Th&aacute;p R&ugrave;a trong thời khắc Giờ Tr&aacute;i đất năm 2018. Ảnh:Vo Tu Hai.</em></td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\">C&ugrave;ng ng&agrave;y, WWF Quốc tế tuy&ecirc;n bố thời gian sự kiện Giờ Tr&aacute;i đất năm 2018 sẽ diễn ra l&uacute;c 20h30-21h30 (giờ địa phương) ng&agrave;y 27/3.</p>\r\n<p class=\"Normal\">Tiếp nối th&agrave;nh c&ocirc;ng của Giờ Tr&aacute;i đất 2018, với sự tham gia của 6 tỉnh th&agrave;nh tr&ecirc;n cả nước, Giờ Tr&aacute;i đất 2018 tại Việt Nam hy vọng sẽ được ủng hộ nhiều hơn nữa nhằm tập trung v&agrave;o gi&aacute;o dục v&agrave; n&acirc;ng cao nhận thức cho người d&acirc;n về biến đổi kh&iacute; hậu, tại sao xuất hiện biến đổi kh&iacute; hậu v&agrave; những g&igrave; ch&uacute;ng ta c&oacute; thể l&agrave;m để ngăn chặn v&agrave; th&iacute;ch ứng với biến đổi kh&iacute; hậu.</p>\r\n<p class=\"Normal\">\"Việc tắt đ&egrave;n v&agrave; những thiết bị điện kh&ocirc;ng cần thiết kh&ocirc;ng phải l&agrave; c&ocirc;ng việc kh&oacute; khăn, nhưng n&oacute; thực sự cần sự cố gắng, t&igrave;nh y&ecirc;u v&agrave; tr&aacute;ch nhiệm đối với h&agrave;nh tinh n&agrave;y, để h&agrave;nh động đ&oacute; trở th&agrave;nh th&oacute;i quen trong cuộc sống hằng ng&agrave;y của mỗi c&aacute; nh&acirc;n\", Phương Ng&acirc;n, c&aacute;n bộ truyền th&ocirc;ng v&agrave; th&agrave;nh vi&ecirc;n nh&oacute;m thực hiện chương tr&igrave;nh Giờ Tr&aacute;i đất của WWF Việt Nam ph&aacute;t biểu.</p>\r\n<p class=\"Normal\">Vừa trở về từ chuyến th&aacute;m hiểm Ch&acirc;u Nam Cực c&ugrave;ng 5 th&agrave;nh vi&ecirc;n kh&aacute;c của Việt Nam, Ng&acirc;n cho biết, đ&atilde; được chứng kiến những t&aacute;c động r&otilde; r&agrave;ng của biến đổi kh&iacute; hậu l&ecirc;n ch&acirc;u lục n&agrave;y. Trong những năm gần đ&acirc;y, một số khối băng lớn với trọng lượng h&agrave;ng trăm tỷ tấn (như khối băng Larsen B) đ&atilde; vỡ th&agrave;nh những mảnh nhỏ do hiện tượng n&oacute;ng l&ecirc;n to&agrave;n cầu.</p>\r\n<p class=\"Normal\">\"Băng tan c&oacute; nghĩa l&agrave; mực nước biển sẽ d&acirc;ng cao, ảnh hưởng trực tiếp v&agrave; nặng nề l&ecirc;n những nước c&oacute; đường bờ biển như Việt Nam. Đ&atilde; đến l&uacute;c người d&acirc;n Việt Nam cần nhận thức được vấn đề biến đổi kh&iacute; hậu v&agrave; những t&aacute;c động của n&oacute; đối với ch&iacute;nh cuộc sống của họ. Đ&oacute; cũng ch&iacute;nh l&agrave; mục ti&ecirc;u ch&iacute;nh của chương tr&igrave;nh Giờ Tr&aacute;i Đất 2018\", Phương Ng&acirc;n nhấn mạnh.</p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"3\" width=\"99%\" align=\"center\">\r\n<tbody>\r\n<tr>\r\n<td bgcolor=\"#eeeedd\">\r\n<p class=\"Normal\">Giờ Tr&aacute;i đất l&agrave; s&aacute;ng kiến to&agrave;n cầu của WWF về Biến đổi Kh&iacute; hậu, bắt nguồn từ Sydney (Australia) v&agrave; trở th&agrave;nh chương tr&igrave;nh to&agrave;n cầu trong 2 năm vừa qua, với sự tham gia của hơn 1 tỷ người tại hơn 4.000 th&agrave;nh phố v&agrave; thị trấn của 88 quốc gia năm 2009. Chương tr&igrave;nh k&ecirc;u gọi c&aacute;c c&aacute; nh&acirc;n, doanh nghiệp, tổ chức c&ugrave;ng tắt c&aacute;c thiết bị chiếu s&aacute;ng kh&ocirc;ng cần thiết v&agrave;o l&uacute;c 20h30-21h30 tối thứ Bảy cuối c&ugrave;ng của th&aacute;ng Ba hằng năm.</p>\r\n<p class=\"Normal\">Việt Nam tham gia Giờ Tr&aacute;i đất lần đầu ti&ecirc;n năm 2009, với sự tham gia của H&agrave; Nội, TP HCM, Cần Thơ, Hội An, Huế v&agrave; Kh&aacute;nh Ho&agrave;.</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p class=\"Normal\" style=\"text-align: right;\"><strong>Anh Khoa</strong></p>', 34, 8, 2172, 0, 1);

